INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Lei Municipal nº 1.640/2025', 'lei-municipal-no-1-640-2025', 'projeto_lei', '1640/2025', 2025, 'DISPÕE SOBRE O REAJUSTE SALARIAL DO PISO DO MAGISTÉRIO PÚBLICO DO MUNICÍPIO DE SUMÉ – PARAÍBA, ALTERA O ANEXO ÚNICO DA LEI COMPLEMENTAR Nº 13, DE 08 DE JANEIRO DE 2010, E DÁ OUTRAS PROVIDÊNCIAS.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO MUNICIPAL DE SUMÉ, ESTADO DA PARAÍBA, no uso das </span><span class="ng-star-inserted">atribuições que lhe são conferidas pela Constituição Federal e Lei Orgânica do Município, </span><span class="ng-star-inserted">faz saber que a Câmara Municipal aprovou e Eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. Fica alterado o Anexo único, da Lei Complementar nº 13, de 08 de janeiro </span><span class="ng-star-inserted">de 2010, para reajustar o Piso Salarial do Magistério Público do Município de Sumé – </span><span class="ng-star-inserted">Paraíba, observando-se o artigo 5º, da Lei Federal nº 11.738/2008, passando a vigorar a </span><span class="ng-star-inserted">TABELA ÚNICA do ANEXO ÚNICO, com as alterações da presente Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º. Os salários base das classes funcionais dos servidores a que se refere o </span><span class="ng-star-inserted">artigo anterior ficam reajustados conforme Tabela de remunerações constante do Anexo </span><span class="ng-star-inserted">Único.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único. Aos servidores inativos e pensionistas, cujo ato de concessão é </span><span class="ng-star-inserted">embasado pelo princípio constitucional da paridade salarial, fica assegurado o reajuste </span><span class="ng-star-inserted">previsto no art. 1ª, de acordo com os valores contidos na planilha unificada, conforme</span><br class="ng-star-inserted" /><span class="ng-star-inserted">anexo único da presente Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º. Fica mantida a alíquota de 5% (cinco por cento) para as progressões </span><span class="ng-star-inserted">verticais e progressões horizontais, previstas no § 3º do art. 8º, da LC nº. 13/2010, devendo- </span><span class="ng-star-inserted">se tomar por base de cálculo inicial o valor previsto para os níveis MAG 401.1.1 e MAG </span><span class="ng-star-inserted">402.1.1.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º. As despesas decorrentes da presente Lei serão as constantes das verbas </span><span class="ng-star-inserted">orçamentárias próprias ou suplementares da Prefeitura Municipal de Sumé – Paraíba.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º. Esta Lei entrará em vigor na data de sua publicação, retroagindo seus </span><span class="ng-star-inserted">efeitos a 1º de janeiro de 2025, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted" style="text-align: center"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé – PB, em 24 de janeiro de 2025.</span></p><p><strong>Manoel Lourenço Queiroz Duarte</strong><br class="ng-star-inserted" /><strong>-- Prefeito do Município --</strong></p><p>&nbsp;</p><table><tbody><tr><th>R$ 4.867,77</th><th colspan="7">Professore(a)s do Ensino Fundamental I e II<br />piso nacional 40 horas</th><th colspan="7">Sumé 30h</th></tr><tr><th>40 horas</th><th>MAG 401.1.1</th><th>MAG 401.1.2</th><th>MAG 401.1.3</th><th>MAG 401.1.4</th><th>MAG 401.1.5</th><th>MAG 401.1.6</th><th>MAG 401.1.7</th></tr><tr><th></th><th>MAG 402.1.1</th><th>MAG 402.1.2</th><th>MAG 402.1.3</th><th>MAG 402.1.4</th><th>MAG 402.1.5</th><th>MAG 402.1.6</th><th>MAG 402.1.7</th></tr><tr><th></th><th>R$ 3.652,39</th><th>R$ 3.835,01</th><th>R$ 4.026,76</th><th>R$ 4.228,10</th><th>R$ 4.439,51</th><th>R$ 4.661,48</th><th>R$ 4.894,56</th></tr><tr><th></th><th>MAG 401.2.1</th><th>MAG 401.2.2</th><th>MAG 401.2.3</th><th>MAG 401.2.4</th><th>MAG 401.2.5</th><th>MAG 401.2.6</th><th>MAG 401.2.7</th></tr><tr><th></th><th>MAG 402.2.1</th><th>MAG 402.2.2</th><th>MAG 402.2.3</th><th>MAG 402.2.4</th><th>MAG 402.2.5</th><th>MAG 402.2.6</th><th>MAG 402.2.7</th></tr><tr><th></th><th>R$ 3.835,01</th><th>R$ 4.026,76</th><th>R$ 4.228,10</th><th>R$ 4.439,51</th><th>R$ 4.661,48</th><th>R$ 4.894,56</th><th>R$ 5.139,28</th></tr><tr><th></th><th>MAG 401.3.1</th><th>MAG 401.3.2</th><th>MAG 401.3.3</th><th>MAG 401.3.4</th><th>MAG 401.3.5</th><th>MAG 401.3.6</th><th>MAG 401.3.7</th></tr><tr><th></th><th>MAG 402.3.1</th><th>MAG 402.3.2</th><th>MAG 402.3.3</th><th>MAG 402.3.4</th><th>MAG 402.3.5</th><th>MAG 402.3.6</th><th>MAG 402.3.7</th></tr><tr><th></th><th>R$ 4.026,76</th><th>R$ 4.228,10</th><th>R$ 4.439,51</th><th>R$ 4.661,48</th><th>R$ 4.894,56</th><th>R$ 5.139,28</th><th>R$ 5.396,25</th></tr><tr><th></th><th>MAG 401.4.1</th><th>MAG 401.4.2</th><th>MAG 401.4.3</th><th>MAG 401.4.4</th><th>MAG 401.4.5</th><th>MAG 401.4.6</th><th>MAG 401.4.7</th></tr><tr><th></th><th>MAG 402.4.1</th><th>MAG 402.4.2</th><th>MAG 402.4.3</th><th>MAG 402.4.4</th><th>MAG 402.4.5</th><th>MAG 402.4.6</th><th>MAG 402.4.7</th></tr><tr><th></th><th>R$ 4.228,10</th><th>R$ 4.439,51</th><th>R$ 4.661,48</th><th>R$ 4.894,56</th><th>R$ 5.139,28</th><th>R$ 5.396,25</th><th>R$ 5.666,06</th></tr></tbody></table><p class="ng-star-inserted"><span class="ng-star-inserted"> </span></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-Municipal-no-1.640-2025.pdf', '2025-01-24', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Lei Municipal nº 1.639/2025', 'lei-municipal-no-1-639-2025', 'projeto_lei', '1639/2025', 2025, 'DISPÕE SOBRE O REAJUSTE SALARIAL DOS AGENTES COMUNITÁRIOS DE SAÚDE E DOS AGENTES DE COMBATE ÀS ENDEMIAS DO QUADRO DE PESSOAL DO MUNICÍPIO DE SUMÉ - PARAÍBA E DÁ OUTRAS PROVIDÊNCIAS.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO MUNICIPAL DE SUMÉ, ESTADO DA PARAÍBA, no uso das </span><span class="ng-star-inserted">suas atribuições que lhe são conferidas pela Constituição Federal e Lei Orgânica do </span><span class="ng-star-inserted">Município, faz saber que a Câmara Municipal aprovou e Eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. Nos termos do art. 198, § 9º, da Constituição Federal de 1988 e da Emenda </span><span class="ng-star-inserted">Constitucional nº 120/2022, fica estabelecido que o vencimento dos Agentes Comunitários </span><span class="ng-star-inserted">de Saúde e dos Agentes de Combate às Endemias passa a ser de R$ 3.036,00 (três mil e </span><span class="ng-star-inserted">trinta e seis reais), equivalente à 2 (dois) salários-mínimos, utilizando-se como indicador </span><span class="ng-star-inserted">o valor do salário-mínimo em vigor a partir de 1º de janeiro de 2025.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º O cumprimento do disposto nesta Lei, fica condicionado ao repasse por </span><span class="ng-star-inserted">parte da União, nos termos do Art. 198, § 9º da Constituição Federal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º As despesas decorrentes desta Lei serão atendidas pelas dotações próprias </span><span class="ng-star-inserted">do orçamento para o ano de 2025.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Esta Lei entra em vigor na data de sua publicação, com efeitos retroativos  </span><span class="ng-star-inserted">01 de janeiro de 2025.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º. Revogam-se as disposições em contrário.</span></p><p class="ng-star-inserted" style="text-align: center"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé-PB, 24 de janeiro de 2025.</p><p><strong>Manoel Lourenço Queiroz Duarte</strong><br class="ng-star-inserted" /><strong>-- Prefeito do Município --</strong><br /></span></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-Municipal-no-1.639-2025.pdf', '2025-01-24', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Lei Municipal nº 1.638/2024', 'lei-municipal-no-1-638-2024', 'projeto_lei', '1638/2024', 2025, 'DISPÕE SOBRE A CONCESSÃO DE REAJUSTE SALARIAL, A TÍTULO DE REVISÃO GERAL ANUAL, NOS VENCIMENTOS DO FUNCIONALISMO PÚBLICO DO MUNICÍPIO DE SUMÉ - PARAÍBA E DÁ OUTRAS PROVIDÊNCIAS.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO MUNICIPAL DE SUMÉ, ESTADO DA PARAÍBA, no uso das suas atribuições que lhe são conferidas pela Constituição Federal e Lei Orgânica do Município, faz saber que a Câmara Municipal aprovou e Eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica concedido reajuste, a título de revisão geral anual nos vencimentos dos servidores e funcionários Municipais de Sumé – Paraíba, que recebam na paridade do salário mínimo, na forma desta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º O reajuste de que trata esta Lei, obedecerá aos mesmos parâmetros adotados pelo Governo Federal na sua política de reajuste anual do salário mínimo, de modo que para os servidores municipais que percebam contraprestação salarial na base de um salário mínimo, perceberão salário no importe de R$1.518,00 (um mil quinhentos e dezoito reais).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º As despesas decorrentes da presente Lei serão as constantes das verbas orçamentárias próprias ou suplementares da Prefeitura Municipal de Sumé- Paraíba.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4°. Essa Lei entrará em vigor na data de sua publicação, retroagindo seus efeitos a 1º de janeiro de 2025, revogadas as disposições em contrário. Gabinete do Prefeito de Sumé – PB, em 24 de janeiro de 2025.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted"><br />Manoel Lourenço Queiroz Duarte<br class="ng-star-inserted" />-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2024/01/Lei-Municipal-no-1.638-2025.pdf', '2025-01-24', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Lei Municipal nº 1.637/2024', 'lei-municipal-no-1-637-2024', 'projeto_lei', '1637/2024', 2024, 'ESTIMA A RECEITA E FIXA A DESPESA DO MUNICÍPIO DE SUMÉ, PARA O EXERCÍCIO DE 2025 E DÁ OUTRAS PROVIDÊNCIAS.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO DO MUNICÍPIO DE SUMÉ DO ESTADO DA PARAÍBA, faço saber que o Poder Legislativo aprovou e eu sanciono a seguinte lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Artigo 1.° - Fica aprovado o Orçamento Programa do Município de SUMÉ, para exercício Econômico-Financeiro de 2025, discriminado pelos anexos integrantes desta Lei, que estima a Receita em R$ 105.832.416,00 (Cento e Cinco Milhões, Oitocentos e Trinta e Dois Mil e Quatrocentos e Dezesseis Reais), e fixa a Despesa em igual valor.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Artigo 2.° - A Receita será realizada mediante a arrecadação de Tributos, Contribuições, Transferências e outras Receitas Correntes e de Capital, na forma da Legislação em vigor e das especificações do Anexo I, de acordo com a seguinte discriminação:</span></p><h2>I - RECEITAS DA ADMINISTRAÇÃO DIRETA</h2><table><tbody><tr><th></th><th></th><th>%</th></tr><tr><td>RECEITAS CORRENTES</td><td>96.594.946,00</td><td>91,27</td></tr><tr><td>IMPOSTOS, TAXAS E CONTRIBUIÇÕES DE MELHORIA</td><td>4.253.271,00</td><td>4,02</td></tr><tr><td>CONTRIBUIÇÕES</td><td>1.303.556,00</td><td>1,23</td></tr><tr><td>RECEITA PATRIMONIAL</td><td>510.116,00</td><td>0,48</td></tr><tr><td>RECEITA DE SERVIÇOS</td><td>5.300,00</td><td>0,00</td></tr><tr><td>TRANSFERÊNCIAS CORRENTES</td><td>90.461.414,00</td><td>85,48</td></tr><tr><td>OUTRAS RECEITAS CORRENTES</td><td>61.289,00</td><td>0,06</td></tr><tr><td>RECEITAS DE CAPITAL</td><td>9.537.971,00</td><td>9,01</td></tr><tr><td>OPERAÇÕES DE C