INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI MUNICIPAL Nº 1.632/2024', 'lei-municipal-no-1-632-2024', 'projeto_lei', '1632/2024', 2024, 'Dispõe sobre a denominação de Espaço Público', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. Fica denominada de Antônio Vicente da Mota “Buga” a passagem molhada construída no Sítio Oiti, localizada na Zona Rural do Município de Sumé – PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º. Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 12 de dezembro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.632-Denomina-Espaco-Publico-Passagem-molhada-Antonio-Vicente-da-Mota.pdf', '2024-12-12', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
