INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI MUNICIPAL Nº 1.630/2024', 'lei-municipal-no-1-630-2024', 'projeto_lei', '1630/2024', 2024, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito adicional especial no valor de R$ 353.000,00 (Trezentos e cinquenta e três mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de SUMÉ.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.05 – SECRETARIA DE EDUCAÇÃO</span><br class="ng-star-inserted" /><span class="ng-star-inserted">12. Educação</span><br class="ng-star-inserted" /><span class="ng-star-inserted">361. Ensino Fundamental</span><br class="ng-star-inserted" /><span class="ng-star-inserted">2001. Modernização da Infra Estrutura da Rede Escolar</span><br class="ng-star-inserted" /><span class="ng-star-inserted">2.020 – Melhoria das Instalações, Equipamentos e Mobiliário da Rede Municipal</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 1.755 – Recursos de Alienação de Bens/Ativos - Administração Direta</span><br class="ng-star-inserted" /><span class="ng-star-inserted">4.4.90.52.00 – Equipamentos e Material Permanente............................................... 86.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.10 - Fundo Municipal de Saúde</span><br class="ng-star-inserted" /><span class="ng-star-inserted">10. Saúde</span><br class="ng-star-inserted" /><span class="ng-star-inserted">301. Atenção Básica</span><br class="ng-star-inserted" /><span class="ng-star-inserted">2014. Expansão e Melhoria na Infra Estrutura de Atendimento</span><br class="ng-star-inserted" /><span class="ng-star-inserted">1.036 - Melhoria das Instalações, Equipamentos e Mobiliário da Rede Municipal</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 1.755 – Recursos de Alienação de Bens/Ativos - Administração Direta</span><br class="ng-star-inserted" /><span class="ng-star-inserted">4.4.90.52.00 – Equipamentos e Material Permanente............................................... 267.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2° Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 2º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, serão utilizados recursos de Alienação de Bens/Ativos - Administração Direta no montante de R$ 353.000,00 (Trezentos e cinquenta e três mil reais).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 12 de dezembro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.630-Abertura-de-Creditos-Adicinais-Especiais.pdf', '2024-12-12', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.629/2024', 'lei-no-1-629-2024', 'projeto_lei', '1629/2024', 2024, 'AUTORIZA A TRANSPOSIÇÃO, O REMANEJAMENTO E A TRANSFERÊNCIA TOTAL OU PARCIAL DE DOTAÇÕES DO ORÇAMENTO VIGENTE, ENTRE PODERES, DE UM ÓRGÃO OU CATEGORIA DE PROGRAMAÇÃO PARA OUTRO E ADOTA OUTRAS PROVIDÊNCIAS.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito Constitucional do Município de SUMÉ - PB, no uso das suas atribuições legais e em conformidade com a Lei Orgânica Municipal, com fundamento no § 3º do Art° 167 da Constituição Federal, no inciso III do art. 41 e art. 44 e 45 da lei Federal de nº 4.320 de 14 de março de 1964.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal Decreta e eu sanciono a seguinte lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1.º - Para atendimento do que dispõe o inciso VI do art. 167, da Constituição Federal de 1988, fica o Poder Executivo Municipal autorizado a realizar a transposição, o remanejamento e a transferência, total ou parcial, de dotações orçamentárias, destinadas ao IPAMS, até o limite de R$ 1.000.000,00 (Hum milhão de reais), utilizando como fonte de recurso, as disponibilidades caracterizadas no Parágrafo Primeiro, do artigo 43, da Lei Federal nº 4.320/1964.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2.º - A autorização desta lei se aplica exclusivamente às operações do Orçamento Fiscal e da Seguridade Social, alocadas nos seguintes grupos de natureza de despesa:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) 31 - Pessoal e Encargos Sociais;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) 32 - Juros e Encargos da Dívida;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) 33 - Outras Despesas Correntes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">d) 44 - Investimentos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">e) 45 - Inversões Financeiras;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">f) 46 - Amortização da Dívida.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3.º - As operações autorizadas far-se-ão até o limite dos saldos das respectivas dotações vinculadas</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) no Órgão, a programas diferentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) no Programa, a Órgãos diferentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) a Órgãos e Programas diferentes.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único - O Decreto que autorizar a transposição, o remanejamento ou a transferência de recursos nos termos desta Lei, deverá discriminar as operações realizadas, agregadas segundo as categorias definidas no artigo 3º desta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4.º Esta Lei entra em vigor na data de sua publicação, e revoga as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de SUMÉ - PB, em 20 de novembro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">PREFEITO</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.629-Autoriza-a-transposicao.-o-remanejamento-e-a-transferencia-toral-ou-parcial-de-dotacoes-do-orcamento.pdf', '2024-11-20', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.628/2024', 'lei-no-1-628-2024', 'projeto_lei', '1628/2024', 2024, 'ALTERA QUANTITATIVO DO CARGO DE AGENTE FISCAL DE TRIBUTOS, DE PROVIMENTO EFETIVO NO PLANO DE CARGOS E SISTEMA DE CARREIRAS DOS SERVIDORES PÚBLICOS DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ/PB, INSTITUIDO PELA LEI Nº. 1.109/2013 E DÀ OUTRAS PROVIDÊNCIAS.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica criada 01 (uma) vaga para o cargo de Agente Fiscal de Tributos, integrante do Quadro Permanente de Pessoal do Poder Executivo do Município de Sumé PB, instituído pela Lei Municipal nº1.109/2013.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º As atribuições do cargo de que trata o Art. 1º desta Lei são as constantes no Decreto nº 930, de 13 de novembro de 2011, e no Decreto nº 1.172, de 26 de julho de 2017.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º As despesas decorrentes da aplicação desta Lei correrão por conta das dotações orçamentárias próprias, constantes do Orçamento Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Esta lei entra em vigor na data de sua publicação, revogando-se as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Sumé/PB, em 16 de outubro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.628-Cria-Cargo-de-Agente-Fiscal-de-Tributos.pdf', '2024-10-16', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.627/2024', 'lei-no-1-627-2024', 'projeto_lei', '1627/2024', 2024, 'ALTERA QUANTITATIVO DO CARGO DE CUIDADOR EDUCACIONAL, DE PROVIMENTO EFETIVO NO PLANO DE CARGOS E SISTEMA DE CARREIRAS DOS SERVIDORES PÚBLICOS DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ/PB, INSTITUIDO PELA LEI Nº. 1.109/2013 E DÁ OUTRAS PROVIDÊNCIAS.', '<p>O<span class="ng-star-inserted"> Prefeito do Município de Sumé</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Ficam criadas 02 (duas) vagas para o cargo de Cuidador Educacional, integrante do Quadro Permanente de Pessoal do Poder Executivo do Município de Sumé PB, instituído pela Lei Municipal nº 1.220/2017.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º As atribuições do cargo de que trata o Art. 1º desta Lei são as constantes na Lei nº 1.220, anexo II, e no Decreto nº 1.172, de 26 de julho de 2017.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º As despesas decorrentes da aplicação desta Lei correrão por conta das dotações orçamentárias próprias, constantes do Orçamento Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Esta lei entra em vigor na data de sua publicação, revogando-se as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 26 de setembro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.627-Cria-vagas-no-cargo-de-Cuidador-Educacional.pdf', '2024-09-26', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.626/2024 - Vereador Damião Rildo da Silva', 'lei-no-1-626-2024-vereador-damiao-rildo-da-silva', 'projeto_lei', '1626/2024', 2024, 'Denomina Passagem Molhada no Distrito de Pio-X de Paulo Soares da Silva', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO DO MUNICÍPIO DE SUMÉ /PB</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que A Câmara Municipal de Sumé aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominado a Passagem Molhada no Distrito de Pio-X de Paulo Soares da Silva, localizado na saída para a cidade de Amparo-PB, na zona rural do município de Sumé-PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 13 de setembro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.626-Denomina-Passagem-Molhada-Paulo-Soares-da-Silva-Distrito-de-Pio-X.pdf', '2024-09-13', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
