INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.623/2024', 'lei-no-1-623-2024', 'projeto_lei', '1623/2024', 2024, 'Dispõe sobre a denominação de Espaço Público', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO DO MUNICÍPIO DE SUMÉ /PB</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que a Câmara Municipal de Sumé aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. Fica denominada de EUNICE DE MACÊDO DUARTE a praça a ser construída no terreno situado a Rua João Albino Pedrosa Neto, que liga Sumé a Sucuru e a Rua Vilarina Aleixo, Quadra 083 – Lote 0070, Bairro Renascer – Município de Sumé/PB</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º. Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 13 de setembro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.623-Denomina-Logradouro-Publico-Praca-Eunice-de-Macedo-Duarte.pdf', '2024-09-13', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.622/2024', 'lei-no-1-622-2024', 'projeto_lei', '1622/2024', 2024, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito adicional especial no valor de R$ 200.000,00 (Duzentos mil reais), destinados a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de SUMÉ.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">00.212 - FUNDO MUNICIPAL DE SAÚDE</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">10.122.1003.2010 – Manutenção das atividades do Fundo Municipal de Saúde</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 1710000000 – Transferência Especial dos Estados</span><br class="ng-star-inserted" /><span class="ng-star-inserted">3.3.90.30.00 - Material de Consumo ................................................................ 125.000,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">33.90.36.00 – Outros Serviços de Terceiros Pessoa Fisica ................................................................ 75.000,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">TOTAL............................................................... 200.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, o excesso de arrecadação dos recursos de 200.000,00 (Duzentos mil reais), do convênio firmado entre o Governo do Estado e este Munícipio</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrár.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 30 de agosto de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">EDÉN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.622-Abertura-de-creditos-adicionais-especiais.pdf', '2024-08-30', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.621/2024', 'lei-no-1-621-2024', 'requerimento', '1621/2024', 2024, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito adicional especial no valor de R$ 350.000,00 (Trezentos e cinquenta mil reais), destinados a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de SUMÉ.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">00.212 - FUNDO MUNICIPAL DE SAÚDE</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">10.301.2014.1034 – Aquisição de veículo e equipamentos para a saúde</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 1710000000 – Transferência Especial dos Estados</span><br class="ng-star-inserted" /><span class="ng-star-inserted">44.90.52.00 – Equipamentos e Material Permanente ................................................................ 350.000,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">TOTAL ................................................................ 350.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, o excesso de arrecadação dos recursos de 350.000,00 (Trezentos e cinquenta mil reais), do convênio firmado entre o Governo do Estado e este Munícipio</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 30 de agosto de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.621-Abertura-de-Creditos-especiais.pdf', '2024-08-30', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.620/2024', 'lei-no-1-620-2024', 'projeto_lei', '1620/2024', 2024, 'Dispõe sobre a abertura de crédito adicional especial ao orçamento vigente', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito Constitucional do Município de SUMÉ - PB, no uso das suas atribuições legais e em conformidade com a Lei Orgânica Municipal, com fundamento no § 3º do Art° 167 da Constituição Federal, no inciso III do art. 41 e art. 44 e 45 da lei Federal de n° 4.320 de 14 de março de 1964 e na Lei Federal de n° 14.399 de 08 de julho de 2022, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica aberto de credito adicional especial, no montante de R$ 152.434,70 (cento e cinquenta e dois mil, quatrocentos e trinta e quatro reais e setenta centavos), destinados ao esforço de dotação do orçamento público do município de SUMÉ – PB, vigente como segue, visando fomentar as ações que serão desenvolvidas no âmbito da Política Pública ligada ao segmento artístico cultural com dotações orçamentárias ligadas as ações contempladas pela Lei Federal de n° 14.399 para instruir e dar celeridade e efetividade as ações.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.06-13.392.2011.2057 - Incentivo e Promoção de Eventos e Atividades Artísticas e Culturais</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 719 - Transferências da Política Nacional Aldir Blanc de Fomento à Cultura - Lei nº 14.399/2022</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">33.50.41.