INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.616/2024', 'lei-no-1-616-2024', 'projeto_lei', '1616/2024', 2024, 'CRIA O PROJETO RESTAURA E CURA E A CENTRAL DE CURATIVOS ESPECIAIS DO MUNICÍPIO DE SUMÉ.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica criado por esta Lei o O PROJETO RESTAURA E CURA E A CENTRAL DE CURATIVOS ESPECIAIS DO MUNICÍPIO DE SUMÉ, cuja sede receberá a denominação de VITÓRIA REGINA BARBOSA, com o objetivo de auxiliar no desafogamento da demanda excedente das Unidades de Saúde da Família, bem como atuar na avaliação de feridas e aplicação dos mais diversos tipos de curativos e coberturas, em formas e propriedades diferentes, a fim de facilitar a cura.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Esta Lei entra em vigor na data de sua publicação, revogando-se as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 10 de julho de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.616-Projeto-Restaura-e-Cura-e-a-Central-de-Curativos-Especiais.pdf', '2024-07-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
