INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.606/2024', 'lei-no-1-606-2024', 'projeto_lei', '1606/2024', 2024, 'Dispõe sobre autorização do Poder Executivo Municipal de Sumé (PB) para contratar operação de crédito com o BANCO DO BRASIL S.A.', '<p class="ng-star-inserted"><span class="ng-star-inserted">A Câmara Municipal de Sumé (PB) aprova e eu, Prefeito Municipal, sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica o Poder Executivo autorizado a contratar operação de crédito junto ao BANCO DO BRASIL S.A., até o valor de R$ 6.300.000,00 (seis milhões e trezentos mil reais), nos termos da Resolução CMN n° 4.995, de 24.03.2022, e suas alterações, destinados a financiar Projetos de Investimento na Área de Energia Renovável, observada a legislação vigente, em especial as disposições da Lei Complementar n° 101, de 04 de maio de 2000.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único. Os recursos provenientes da operação de crédito autorizada serão obrigatoriamente aplicados na execução dos empreendimentos previstos no caput deste artigo, sendo vedada a aplicação de tais recursos em despesas correntes, em consonância com o § 1º do art. 35 da Lei Complementar Federal nº 101, de 04 de maio de 2000.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Os recursos provenientes da operação de crédito a que se refere esta Lei deverão ser consignados como receita no Orçamento ou em créditos adicionais, nos termos do inc. II, § 1º, art. 32, da Lei Complementar 101/2000 e arts. 42 e 43, inc. IV, da Lei nº 4.320/1964.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Os orçamentos ou os créditos adicionais deverão consignar, anualmente, as dotações necessárias às amortizações e aos pagamentos dos encargos, relativos aos contratos de financiamento a que se refere o artigo primeiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Fica o Chefe do Poder Executivo autorizado a abrir créditos adicionais destinados a fazer face aos pagamentos de obrigações decorrentes da operação de crédito ora autorizada.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º Para pagamento do principal, juros, tarifas bancárias e demais encargos financeiros e despesas da operação de crédito, fica o Banco do Brasil autorizado a debitar a conta corrente de titularidade do município, a ser indicada no contrato, em que são efetuados os créditos dos recursos do município, ou qualquer(isquer) outra(s) conta(s), salvo a(s) de destinação específica, mantida em sua agência, os montantes necessários às amortizações e pagamento final da dívida, nos prazos contratualmente estipulados.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único. Fica dispensada a emissão da nota de empenho para a realização das despesas a que se refere este artigo, nos termos do §1º, do art. 60, da Lei 4.320, de 17 de março de 1964.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º Esta Lei entra em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito Constitucional</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.606-Operacao-de-credito-BANCO-DO-BRASIL.pdf', '2024-04-24', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.604/2024', 'lei-no-1-604-2024', 'projeto_lei', '1604/2024', 2024, 'REVISÃO SALARIAL AOS SERVIDORES EFETIVOS DA CÂMARA MUNICIPAL DE SUMÉ.PB, DE ACORDO COM O PREVISTO NO ART. 37, INCISO X, DA CONSTITUIÇÃO FEDERAL. SERÁ CONCEDIDA REVISÃO SALARIAL DE 4,51% COM BASE NA INFLAÇÃO ACUMULADA, MAIS AUMENTO REAL DE 2%, TOTALIZANDO O PERCENTUAL DE 6,51%, ΑΤΙNGINDO A TODOS OS SERVIDORES EFETIVOS DA CASA LEGISLATIVA.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICIPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso das suas atribuições que lhe são conferidas pelas Constituições Federal, do Estado da Paraíba e pela Lei Orgânica do Município, faz saber que a Câmara Municipal decreta a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica concedido, a partir de 1º de abril de 2.024, reajuste de 6,51% (seis inteiros e cinquenta e um centésimos por cento) no salário base dos servidores efetivos da Câmara Municipal de Sumé-PB, sendo 4,51% com base na inflação acumulada mais aumento real de 2%.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - A tabela constante do Anexo I, passa a vigorar com o reajuste instituído pela presente Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Esta Lei entrará em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 27 de março de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município de Sumé-PB</span></strong></p><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SÍMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Agente Administrativo</td><td>PL- SAP-1</td><td>1</td><td>1.922,57</td></tr><tr><td>Agente de Apoio Parlamentar</td><td>PL- SAP-4</td><td>1</td><td>1.922,57</td></tr></tbody></table><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SÍMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Auxiliar Administrativo</td><td>PL- SAP-2</td><td>1</td><td>1.838,29</td></tr></tbody></table><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SÍMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Auxiliar de Limpeza e Serviços Gerais</td><td>PL- SAP-2</td><td>1</td><td>1.794,10</td></tr></tbody></table><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SÍMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Digitador</td><td>PL- SAP-2</td><td>1</td><td>1.838,29</td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.604-Dispoe-sobre-o-reajuste-salarial-dos-servidores-efetivos-da-Camara-Municipal-e-da-outras-providencias.pdf', '2024-03-27', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
