INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.602/2024', 'lei-no-1-602-2024', 'projeto_lei', '1602/2024', 2024, 'DISPÕE SOBRE O ATENDIMENTO PREFERENCIAL AOS PORTADORES DE CANCER E PACIENTES DE HEMODIALISE EM TRATAMENTO NOS ESTABELECIMENTOS COMERCIAIS, BANCARIOS E SERVIÇOS SIMILARES DO MUNICÍPIO DE SUMÉ', '<p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. - Os pacientes em tratamento de cancer e hemodialise terão atendimento preferencial e prioritário em todos os estabelecimentos comerciais, bancários e serviços similares do município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único - A preferência e a prioridade de que trata o "caput" deste artigo implica em que os beneficiários não se sujeitem as filas comuns, além da adoção de medidas que promovam agilidade ao atendimento e a prestação de serviços, incluindo-se os serviços bancários mesmo que o doador não seja cliente da agência bancaria.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2°. Todos os estabelecimentos discriminados no Artigo 1º deverão obrigatoriamente afixar em local visível a informação sobre o benefício concedido pela presente lei, incluindo o número e a data de publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3°. Para receber o atendimento preferencial de que trata a presente lei, o paciente apresentara laudo médico comprobatório de seu estado clínico, que contenha o CID correspondente, com data não superior a 90 dias.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único – Fica a cargo da Secretaria de Saúde do Município, a confecção da carterinha de identificação dos pacientes em tratamento de cancer e hemodialise.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4°. 0 Poder Executivo regulamentara a presente lei, no que couber e for necessario para seu cumprimento efetivo no prazo de 60 (sessenta) dias, a contar da sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º. - Está Lei entrará em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 28 fevereiro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.602-Atendimento-Preferencial-paa-pacientes-com-cancer-e-hemodialise.pdf', '2024-02-28', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.601/2024', 'lei-no-1-601-2024', 'projeto_lei', '1601/2024', 2024, 'DISPÕE SOBRE A NOMEAÇÃO DE PRÉDIO PÚBLICO', '<p class="ng-star-inserted"><span class="ng-star-inserted">A Câmara Municipal de Sumé aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. Fica denominado de GENIVAL PAULINO DE SOUSA “ O VAVAZÃO”, o Ginásio Poliesportivo a ser construído na Rua Maestro Antônio Josué de Lima, localizada no Conjunto Habitacional Sebastião Vitorino – Município de Sumé/PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º. Esta Lei entra em vigor na data de sua publicação, ficando revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé/PB, em 21 fevereiro de 2024.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.601-Denominacao-de-Esoaco-Publico-Ginasio-Poliesportivo-Genival-Paulino-de-Sousa-Vavazao.pdf', '2024-02-21', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.600/2023', 'lei-no-1-600-2023', 'projeto_lei', '1600/2023', 2023, 'CRIA NA CÂMARA MUNICIPAL DE SUMÉ – PARAÍBA, OS CARGOS DE PROVIMENTO EM COMISSÃO DE ASSESSOR PARLAMENTAR', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PRESIDENTE DA CÂMARA MUNICIPAL DE SUMÉ, ESTADO DA PARAÍBA, no uso das suas atribuições que lhe são conferidas pelas Constituições Federal, do Estado da Paraíba e pela Lei Orgânica do Município e, em atendimento ao solicitado pelos vereadores </span><strong class="ng-star-inserted"><span class="ng-star-inserted">Antônio Carlos Sousa Sarmento, José Antônio Fernandes de Oliveira, Leônidas Albino Pedrosa, Damião Rildo da Silva, Francisco Fontinele Feitosa Santa Cruz, Rosildo Alves Monteiro, Aloízio Salvador de Lima e Mário Delandy Diniz Holanda</span></strong><span class="ng-star-inserted">, faz saber que a Câmara Municipal decreta a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º. Fica criado, no âmbito da Câmara Municipal de Sumé.PB, 11 (onze) cargos de Provimento em Comissão de Assessor Parlamentar da Casa Legislativa, com designação, vinculação, vencimentos, quantidade de vagas, símbolo e atribuições, de conformidade com o disposto no Anexos I desta Lei, sendo:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I- 01 (um) cargo de Assessor Parlamentar da Mesa Diretora da Câmara Municipal de Sumé-PB, de livre nomeação do Vereador Presidente;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">II- 10 (dez) cargos de Assessor Parlamentar de Vereador da Casa Legislativa, sendo (01) um para cada Parlamentar, de livre nomeação do Vereador Presidente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º. O cargo de que trata os incisos: I e II do artigo 1º desta Lei, é de confiança, de livre nomeação e exoneração, a qualquer tempo, pelo Presidente da Câmara Municipal de Sumé, desde que solicitado pelo vereador da indicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º - O Vereador, quando solicitada nomeação para o cargo de Assessor Parlamentar, deve apresentar à Presidência da Câmara Municipal, a indicação do nome do ocupante, o assessor indicado pelo vereador que mantiver processo transitado e julgado, não poderá assumir a assessoria parlamentar.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - No caso de o Parlamentar achar necessário o desligamento de ocupante do cargo comissionado de Assessor Parlamentar deve solicitar o dito desligamento, à Presidência da Câmara;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º - A remuneração pelo exercício dos cargos, a que se refere a presente Lei será de 01 (um) salário mínimo mensal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º. As despesas oriundas da execução desta Lei correrão à conta de dotações orçamentárias próprias, consignadas no orçamento da Câmara Municipal de Sumé - .PB, observando-se, sempre, os limites de gastos com pessoal estabelecidos no parágrafo 1º do art. 29 e da emenda Constitucional nº 25 da Constituição Federal, respectivamente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4°. Atribuições do Assessor Parlamentar:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I. Reunir legislação, projetos e propostas de interesse do Vereador, assessorando-o na formulação de questionamentos e nas matérias em que se mostrarem necessárias;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II. Realizar estudos de viabilidade e pesquisas, a pedido dos Vereadores, para a elaboração de Projetos de Lei ou outras espécies de proposições parlamentares;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III. Representar o Vereador no atendimento à comunidade, tanto da zona urbana quanto da zona rural, quando lhe for solicitado;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV. Preparar e/ou revisar material relativo a pronunciamentos, exposições e proposições do Vereador;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V. Recepcionar munícipes, às autoridades e à população em geral que visita as dependências dos gabinetes parlamentares desta Casa de Leis, realizando a triagem de suas demandas e coletando dados a respeito de suas reivindicações para as devidas providências, dando o desfecho adequado para o atendimento, seja diretamente, de acordo com as orientações dadas pelo Vereador assessorado, ou direcionando o popular para atendimento pessoal a ser realizado pelo próprio Parlamentar, prestando orientações e realizando os encaminhamentos necessários aos órgãos e setores competentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VI. Prestar assessoramento imediato ao Vereador, quando lhe for solicitado, durante a participação deste nas comissões permanentes ou temporárias da Câmara Municipal;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VII. Manter o Vereador informado sobre prazos a cumprir, bem como acompanhar as providências obtidas das proposições em trâmite na Câmara Municipal de Sumé - .PB;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VIII. Agendar e organizar as reuniões externas de interesse do Vereador, compromissos, horários de reuniões, entrevistas, visitas e solenidades, especificando todos os dados e informações pertinentes, bem como realizando as necessárias anotações em agendas para lembrar os Parlamentares assessorados a respeito de seus afazeres e facilitar-lhes o cumprimento de todas as obrigações assumidas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IX. Encaminhar ao gabinete do Vereador os assuntos de interesse público, para análise posterior e a elaboração de proposta legislativa correspondente;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">X. Auxiliar o Vereador na fiscalização da Administração Pública, observando o cumprimento da legislação, das normas e instruções pertinentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XI. Desempenhar outras atividades de assessoramento interno e externo ao gabinete do Vereador, desde que compatíveis com o cargo ocupado;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XII. Zelar e guardar sigilo das informações recebidas no exercício de suas funções;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XIII. Manter contatos verbais, telefônicos ou por escrito com quem quer que seja para a obtenção de informações úteis ao bom funcionamento dos gabinetes dos Vereadores e demais expedientes políticos ou legislativos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XIV. Supervisionar e coordenar as atividades realizadas no âmbito dos gabinetes parlamentares, tais como, receber, classificar, distribuir, arquivar documentos oficiais ou de caráter confidencial do vereador para selecionar assuntos de afetos ao respectivo gabinete;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XV. Redigir, Projetos de Lei, outras proposições parlamentares, ofícios de Vereadores e demais documentos realizar operações básicas de microcomputador e atividades correlatas e alimentar o sistema de informática do departamento de suporte legislativo operar aparelhos de reprografia, tais como scanner outros similares;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XVI. Conferir textos relativos à atividade legislativa e fiscalizadora realizada pelos Vereadores, especialmente quanto à ortografia, estética, formatação e clareza do texto;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XVII. Exercer atividades de representação social e de apoio político e administrativo aos Vereadores, tanto internamente quanto externamente junto à comunidade;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XVIII- Construir, promover e preservar uma boa imagem pública para o Vereador assessorado, inclusive a realização de publicações ou administração de websites, páginas em redes sociais dos Parlamentares ou por outros meios semelhantes, zelando sempre pela imagem institucional da Câmara de Vereadores perante a comunidade;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">XIX. Desempenhar outras atividades correlatas, conforme determinações dadas pelo Parlamentar assessorado.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º. Esta Lei entrará em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Sumé -.PB, em 27 de dezembro de 2023.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito Constitucional</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.600-CRIA-NA-CAMARA-MUNICIPAL-CARGOS-DE-PROVIMENTO-EM-COMISSAO-DE-ASSESSOR-PARLAMENTAR.pdf', '2023-12-27', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.599/2023', 'lei-no-1-599-2023', 'projeto_lei', '1599/2023', 2023, 'ESTIMA RECEITA E FIXA A DESPESA DO MUNICIPIO DE SUMÉ, PARA O EXERCICIO DE 2024', '<p>&nbsp;</p><p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO DO MUNICÍPIO DE SUMÉ DO ESTADO DA PARAÍBA, faço saber que o Poder Legislativo aprovou e eu sanciono a seguinte lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Artigo 1.° - Fica aprovado o Orçamento Programa do Município de SUMÉ, para exercício Econômico-Financeiro de 2024, discriminado pelos anexos integrantes desta Lei, que estima a Receita em R$ 99.500.000,00 (Noventa e Nove Milhões e Quinhentos Mil Reais), e fixa a Despesa em igual valor.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Artigo 2.° - A Receita será realizada mediante a arrecadação de Tributos, Contribuições, Transferências e outras Receitas Correntes e de Capital, na forma da Legislação em vigor e das especificações do Anexo I, de acordo com a seguinte discriminação:</span></p><table style="width: 100.626%;height: 508px"><thead><tr style="height: 24px"><th style="width: 68.0585%;height: 24px">I – RECEITAS DA ADMINISTRAÇÃO DIRETA</th><th style="width: 21.0856%;height: 24px"></th><th style="width: 9.39457%;height: 24px">%</th></tr></thead><tbody><tr style="height: 24px"><td style="width: 68.0585%;height: 24px">RECEITAS CORRENTES</td><td style="width: 21.0856%;height: 24px">90.114.801,00</td><td style="width: 9.39457%;height: 24px">90,57</td></tr><tr style="height: 48px"><td style="width: 68.0585%;height: 48px">IMPOSTOS, TAXAS E CONTRIBUIÇÕES DE MELJORIAS</td><td style="width: 21.0856%;height: 48px">3.856.640,00</td><td style="width: 9.39457%;height: 48px">3,88</td></tr><tr style="height: 24px"><td style="width: 68.0585%;height: 24px">CONTRIBUIÇÕES</t