INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.550/2023', 'lei-no-1-550-2023', 'projeto_lei', '1550/2023', 2023, 'Dispõe sobre a abertura de credito adicional especial ao orçamento vigente', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito Constitucional do município de Sumé-PB, no uso de suas atribuições e considerando o disposto nos Arts. 165, §5°; 167, inciso V da Constituição Federal; e na Lei Complementar nº 195, de 8 de julho de 2022, faço saber que a Câmara Municipal de Sumé-PB Decreta e eu sanciono a seguinte lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica o Poder Executivo autorizado a abrir no orçamento vigente do MUNICÍPIO DE SUMÉ crédito especial, no valor de R$ 181.546,52 (Cento e oitenta e um mil, quinhentos e quarenta e seis reais e cinquenta e dois centavos), conforme dotação abaixo identificada:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.06.13.392.2011.2.057– Incentivo e Promoção de Eventos e Atividades Artísticas e Culturais</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 715 - Transferências Destinadas ao Setor Cultural - LC n° 195/2022 – Art. 6º - Audiovisual</span><br class="ng-star-inserted" /><span class="ng-star-inserted">33.90.31.00- Premiações Culturais, Artísticas, Científicas e Desportivas..................................R$<code class="rendered"><span class="katex"><span class="katex-mathml">120.706,66</span></span></code></span><br class="ng-star-inserted" /><span class="ng-star-inserted">33.90.39.00 – Outros Serviços de Terceiros – Pessoa Jurídica...........................................R$ </span><span class="ng-star-inserted">8.500,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Total da Fonte de Recurso............................................R$ 129.206,66</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 716 Transferências Destinadas ao Setor cultural - LC nº 195/2022 – Art. 8° - Demais Setores da Cultura</span><br class="ng-star-inserted" /><span class="ng-star-inserted">33.90.31.00 - Premiações Culturais, Artísticas, Científicas e Desportivas...................................R$</span><code class="rendered"><span class="katex"><span class="katex-mathml">52.339,86<br />TotaldaFontedeRecurso...............................................R</span><span class="katex-html" aria-hidden="true"><span class="base"><span class="mord">52.339</span><span class="mpunct">,</span><span class="mord">86</span></span></span></span></code><br class="ng-star-inserted" /><span class="ng-star-inserted">TOTAL GERAL........................................................R$ 181.546,52</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Fica autorizado o remanejamento de valores dentro da mesma fonte de recurso, entre os elementos de despesa já definidos, caso algum valor se torne insuficiente para a realização da ação;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4.320/64, o excesso de arrecadação dos recursos da Lei Paulo Gustavo, totalizando R$ 181.546,52</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Revogam-se as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 16 de agosto de 2023.</span></p><p style="text-align: center"><strong>ÉDEN DUARTE PINTO DE SOUSA </strong><br /><strong>Prefeito do Município de Sumé-PB</strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.550-Dispoe-sobre-a-abertura-de-credito-adicional-especial-ao-orcamento-vigente-Lei-Paulo-Gustavo.pdf', '2023-08-16', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
