INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.547/2023', 'lei-no-1-547-2023', 'projeto_lei', '1547/2023', 2023, 'Denomina estrada que interligam o Plano Rodoviário Municipal', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso de suas atribuições legais, faz saber que a Câmara de Vereadores aprova e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominado de Estrada João Caetano de Araújo a Estrada do Pio X, Pau d''Arco, Sigla – SM-25, com início na estrada SM-39 e final com a estrada SM-60, de acordo com a Lei Municipal nº 1.531/2023.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 10 de agosto de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.547-Denominado-de-Estrada-Joao-Caetano-de-Araujo.pdf', '2023-08-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.546/2023', 'lei-no-1-546-2023', 'projeto_lei', '1546/2023', 2023, 'INSTITUI A POLÍTICA MUNICIPAL DE PROGRAMA DE BUSCA ATIVA ESCOLAR E O PROGRAMA DE RECUPERAÇÃO DAS APRENDIZAGENS PARA ESTUDANTES DA EDUCAÇÃO BÁSICA DO MUNICÍPIO DE SUMÉ', '<p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso de suas atribuições legais, com amparo no art. 84, IV e VI, da Constituição Federal de 1988, e na Lei Orgânica do Município de Sumé;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO I</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Do Objeto e Princípios Gerais</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1°. Fica instituída no Município de Sumé a Política municipal de Busca Ativa das crianças e jovens em idade própria para a educação básica obrigatória, com os seguintes objetivos:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I assegurar o acesso universal das crianças e jovens de 6 (seis) a 17 (dezessete anos) à educação básica obrigatória, compreendendo a educação pré-escolar, o ensino fundamental e o ensino médio;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II promover a cooperação entre os entes federados para garantir a frequência à escola das crianças e jovens que a ela ainda não têm acesso ou que dela se evadiram;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III promover a cooperação intersetorial das áreas do Poder Público relacionadas com a busca ativa das crianças e jovens para a frequência à educação básica obrigatória, especialmente em razão do estado de pandemia;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV elevar a frequência escolar e reduzir os índices de evasão e de abandono escolar;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">V diminuir a distorção idade-série.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2°. Fica criado e instituído o Programa de Recuperação das Aprendizagens, destinado a atender educandos da educação básica, objetivando:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I recuperar as perdas de aprendizagem ocasionadas pelo fechamento das escolas devido a pandemia de covid-19;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">II oferecer oportunidades de aprendizagem para alavancar os estudos e fortalecer a aprendizagem para o sucesso na continuidade dos estudos e permanência na escola;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">III sanar dificuldades e lacunas de aprendizagem;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">IV alicerçar o processo de alfabetização;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">V promover a alfabetização e letramento na idade certa;</span><br class="ng-star-inserted" /><span class="ng-star-inserted">VI melhorar o letramento, principalmente nas séries mais avançadas.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3°. Fica autorizada a realização de convênios, parcerias, acordos de cooperação técnica e contratação de serviços especializados para a execucão dos Programas.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO II</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Programa de busca ativa</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4°. A política de busca ativa utilizará as seguintes estratégias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I recenseamento anual das crianças e jovens na idade própria para a educação básica obrigatória e a respectiva chamada pública;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II formação de comitês intersetoriais para a busca ativa, integrados por representantes das áreas da Educação, Assistência Social e Saúde e de garantias dos direitos da criança e do adolescente;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III elaboração de diretrizes e metodologias para a busca ativa:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV formação e qualificação de equipes, integradas por profissionais das áreas referidas no inciso I, tendo como base de atuação a escola ou conjunto próximo de escolas do município;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V criação de base de dados e mapas de geoprocessamento que orientem a busca ativa nas diversas localidades do município;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VI identificação, registro, controle e acompanhamento de crianças e adolescentes que estão fora da escola ou em risco de evasão;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VII utilização de instrumentos de tecnologia digital para acesso contínuo e atualizado das equipes aos dados necessários;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VIII sensibilização, mobilização e comunicação que envolvam a sociedade local, especialmente as comunidades mais vulneráveis em que a infrequência ou a evasão escolar mais se manifestam;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5°. Devem ser priorizados, preferencialmente, dois componentes curriculares: Matemática e Língua Portuguesa, por serem considerados de maior deficiência entre os estudantes brasileiros e por serem básicos para outras áreas do conhecimento.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6°. A duração do Programa poderá abarcar vários períodos letivos, até o alcance de médias satisfatórias nas avaliações nacionais de proficiência.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7°. O tempo determinado ao Programa poderá ser computado como carga horária letiva desde que as aulas sejam oferecidas a todos os alunos, dentro do mesmo semestre letivo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 8°. Todos os alunos participarão das classes de recuperação, partindo do pressuposto da necessidade de reparar perdas de aprendizagem, em razão das escolas públicas na Paraíba terem fechado, sem oferta do ensino presencial, durante quatro semestres letivos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 9°. O Programa poderá atender outros componentes do currículo básico além da Língua Portuguesa e Matemática, dependendo das necessidades de aprendizagens de cada etapa, especialmente dos alunos do ensino médio, sem prejuízo para a carga horária dos dois componentes básicos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 10. O Poder Executivo Municipal, regulamentará esta Lei, no que couber.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 11. Esta Lei entra em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.546-INSTITUI-A-POLITICA-MUNICIPAL-DE-PROGRAMA-DE-BUSCA-ATIVA-ESCOLAR-E-O-PROGRAMA-DE-RECUPERACAO-DAS-APRENDIZAGENS-PARA-ESTUDANTES-DA.pdf', '2023-08-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.545/2023', 'lei-no-1-545-2023', 'projeto_lei', '1545/2023', 2023, 'Dispõe sobre alteração do quadro de cargos de provimento efetivo do Poder Executivo Municipal de Sumé-PB', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art.1º Esta lei dispõe sobre a ampliação de vagas de cargo efetivo no Quadro de Pessoal da Prefeitura Municipal de Sumé-PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art.2° Para os fins previstos nesta Lei, fica ampliada o número de vagas dos cargos de provimento efetivo da Administração Municipal, conforme dispõe a Lei nº1.314/2019 – Professor do Ensino Fundamental II – Licenciatura em Educação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Em virtude da ampliação das vagas dos cargos efetivos a que se refere esta lei, fica autorizado o Poder Executivo Municipal a atualizar a Tabela do Quadro Permanente de Pessoal da Prefeitura Municipal de Sumé-PB, consoante às Leis que regem os cargos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Esta lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 10 de agosto de 2023.</span></p><table><thead><tr><th>DENOMINAÇÃO DO CARGO</th><th>HABILITAÇÃO</th><th>CÓDIGO/SÍMBOLO/CLASSE</th><th>VENCIMENTO</th><th>N° DE VAGAS AMPLIADAS</th></tr></thead><tbody><tr><td>Professor de Ensino Fundamental II</td><td>Ciências Humanas e Sociais</td><td>402.15</td><td>R$ 3.316,83</td><td>02</td></tr><tr><td>Professor de Ensino fundamental II</td><td>Ciências Exatas e da Natureza</td><td>402.14</td><td>R$ 3.316,83</td><td>02</td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.545-Dispoe-sobre-alteracao-do-quadro-de-cargos-de-provimento-efetivo-do-Poder-Executivo-Municipal-de-Sume-PB.pdf', '2023-08-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.544/2023', 'lei-no-1-544-2023', 'projeto_lei', '1544/2023', 2023, 'Altera a Lei Municipal nº 1.200, de 21 de setembro de 2016 que trata sobre o Plano de Amortização Atuarial', '<p class="ng-star-inserted"><span class="ng-star-inserted">Altera a Lei Municipal nº 1.200, de 21 de setembro de 2016 que trata sobre o Plano de Amortização Atuarial e dá outras providências.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso de suas atribuições legais e de conformidade com a Lei Orgânica Municipal, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º O caput do art. 2º da Lei Municipal nº 1.200, de 21 de setembro de 2016, passa a vigorar com a seguinte redação:</span></p><p class="ng-star-inserted" style="text-align: left"><strong><span class="ng-star-inserted">“Art. 2º A alíquota normal da obrigação financeira de responsabilidade dos órgãos e entidades da Administração Direta e da Indireta do Poder Executivo incidente sobre a remuneração de natureza contributiva mensal dos servidores ativos, a que se refere o art. 18, redação original da Lei Municipal nº 961, de 18 de maio de 2009, passa a ser acrescida mensalmente de uma alíquota suplementar temporária nos percentuais e períodos constantes do ANEXO ÚNICO a esta Lei.<br /></span><span class="ng-star-inserted">(...)"</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º O Anexo Único da Lei nº 1.200, de 21 de setembro de 2016, que trata do Plano de Amortização Atuarial, em conformidade com os resultados da Reavaliação Atuarial realizada em março de 2023, passa a vigorar com as seguintes alíquotas:</span></p><table><thead><tr><th>Ano