INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.537/2023', 'lei-no-1-537-2023', 'projeto_lei', '1537/2023', 2023, 'Dispõe sobre a denominação de Espaço Municipal', '<p class="ng-star-inserted"><span class="ng-star-inserted">A Câmara Municipal de Sumé aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica denominada de Pista de Motocross e Manobras Radicais VANDEILDO JOSÉ DA SILVA “VANDO” o espaço municipal localizado no Assentamento Nossa Senhora da Conceição, às margens da BR 412.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º O deslocamento do espaço para outra área não acarretará a mudança do nome da Pista, que permanecerá doravante com a denominação ora atribuída.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 07 de junho de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.537-Denominada-de-Pista-de-Motocross-e-Manobras-Radicais-VANDEILDO-JOSE-DA-SILVA-VANDO.pdf', '2023-06-07', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.536/2023', 'lei-no-1-536-2023', 'projeto_lei', '1536/2023', 2023, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, propõe o seguinte Projeto de Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito adicional especial no valor de R$ 277.784.00 (Duzentos e setenta e sete mil, setecentos e oitenta e quatro reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de SUMÉ.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.05 – SECRETARIA DE EDUCAÇÃO</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">12.361.2003.2.2023 - Manutenção do ensino fundamental</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 543 – Transferências do FUNDEB - Complementação da União – VAAR</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.30.00 - Material de Consumo ...........................................................................117.784,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">3.3.90.36.00 – Outros Serviços de terceiros Pessoa Fisica................................................................20.000,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">3.3.90.39.00 Outros Serviços de terceiros Pessoa Jurídica...........................................................20.000,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">4.4.90.52.00 - Equipamento e material Permanente ..........................................................................120.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3° Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 2º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, o excesso de arrecadação dos recursos de Transferência do FUNDEB Complementação da União - VAAR no montante de 277.784.00 (Duzentos e setenta e sete mil, setecentos e oitenta e quatro reais)</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 07 de junho de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito Constitucional do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.536-Abertura-de-creditos-adicionais-especiais.pdf', '2023-06-07', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.535/2023', 'lei-no-1-535-2023', 'projeto_lei', '1535/2023', 2023, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 138.010,00 (Cento e trinta e oito mil reais e dez centavos), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.09 - SECRETARIA DE OBRAS E SERVIÇOS URBANOS</span><br class="ng-star-inserted" /><span class="ng-star-inserted">15.451.2005.1006 – Pavimentação de Vias Públicas</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 755 – Recursos de Alienação de Bens/Ativos – Administração Direta</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.90.51.00.00.00.00 - Obras e Instalações .................................................................47.970,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.12 - FUNDO MUNICIPAL DE SAÚDE</span><br class="ng-star-inserted" /><span class="ng-star-inserted">10.302.2014.1035 – Construção, Reforma e/ou Ampliação do Hospital e Unidades Básicas de Saúde</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 755 - Recursos de Alienação de Bens/Ativos – Administração Direta</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.90.51.00.00.00.00 – Obras e Instalações .................................................................59.580,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">10.301.2014.1036 – Melhoria das instalações, Equipamentos e Mobiliário da Rede Municipal de Saúde</span><br class="ng-star-inserted" /><span class="ng-star-inserted">Recurso: 755 -– Recursos de Alienação de Bens/Ativos – Administração Direta</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.90.51.00.00.00.00 – Obras e Instalações ..................................................................30.460,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 2º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, o excesso de arrecadação dos recursos de alienação de bens/ativos - Administração Direta.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3° Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 07 de junho de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito Constitucional do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.535-Abertura-de-creditos-adicionais-especiais.pdf', '2023-06-07', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
