INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.531/2023', 'lei-no-1-531-2023', 'projeto_lei', '1531/2023', 2023, 'Dispõe sobre a Plano Rodoviário do Município de Sumé-PB, a partir da alteração da Lei Municipal nº 300 de 11, de março de 1973.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso de suas atribuições legais e de conformidade com a Lei Orgânica Municipal, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica aprovado o novo Plano Rodoviário do Município de Sumé, alterando-se a Lei Municipal nº. 300, de 11 de março de 1973, passando a ser representado pelos seguintes instrumentos referenciais:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - Mapa indicativo, com as dimensões 549 x 841 mm, retirando o contorno das publicações do IBGE, incluindo as estradas do Sistema Federal, Estadual e Municipal, a rede hidrográfica e aglomerados urbanos, bem como, a situação do município no contexto do Estado da Paraíba, conforme ANEXO I desta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - Listagem das estradas que interligam o Plano Rodoviário Municipal, conformar ANEXO II desta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Esta lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Esta Lei entrará em vigor a partir da data da sua publicação, ficando revogadas todas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 03 de maio de 2023.</span></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.531-Dispoe-sobre-a-Plano-Rodoviario-do-Municipio-de-Sume-PB-a-partir-da-alteracao-da-Lei-Municipal-n-300-de-11-de-marco-de-1973.pdf', '2023-05-03', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.530/2023', 'lei-no-1-530-2023', 'projeto_lei', '1530/2023', 2023, 'Denomina Passagem Molhada Prícilio Gonçalves Ribeiro no Sítio Riacho da Roça', '<p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso de suas atribuições legais e de conformidade com a Lei Orgânica Municipal, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominado a Passagem Molhada Prícilio Gonçalves Ribeiro no Sítio Riacho da Roça, localizada na zona rural do município de Sumé-PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 28 de abril de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.530-Denomina-Passagem-Molhada-Pricilio-Goncalves-Ribeiro-no-Sitio-Riacho-da-Roca.pdf', '2023-04-28', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.529/2023', 'lei-no-1-529-2023', 'projeto_lei', '1529/2023', 2023, 'Dispõe sobre o reajuste salarial dos servidores efetivos da Câmara Municipal', '<p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso de suas atribuições legais e de conformidade com a Lei Orgânica Municipal, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - O salário dos cargos de provimento efetivo do Poder Legislativo Municipal passará a ser, a partir do dia lo de março de 2023, as constantes dos ANEXOS I, II, III e IV a esta lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Fica Reajustado, no percentual de 7% (sete por cento) os vencimentos dos cargos abaixo relacionados, constantes no anexo I.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Fica Reajustado, no percentual de 7% (sete por cento) os vencimentos do cargo abaixo relacionado, constante no anexo II.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Fica Reajustado, no percentual de 7% (sete por cento) os vencimentos do cargo abaixo relacionado, constante no anexo III.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - Fica Reajustado, no percentual de 7% (sete por cento) os vencimentos do cargo abaixo relacionado, constante no anexo IV.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6° As despesas com a execução desta lei correrão à contar das dotações orçamentárias próprias consignadas no Orçamento do Poder Legislativo para o corrente exercício financeiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7º - Esta Lei entra em vigor na data de sua publicação, retroagindo seus efeitos a 1º de março de 2023.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 05 de abril de 2023.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito do Município de Sumé-PB</span></strong></p><table><thead><tr><th>CARGOS</th><th>SIMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Agente Administrativo</td><td>PL-SAP-1</td><td>1</td><td>R$ 1.805,06</td></tr><tr><td>Agente de Apoio Parlamentar</td><td>PL-SAP-4</td><td>1</td><td>R$ 1.805,06</td></tr></tbody></table><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SIMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Auxiliar Administrativo</td><td>PL-SAP-2</td><td>1</td><td>R$ 1.725,94</td></tr></tbody></table><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SIMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Auxiliar de limpeza e Serviços Gerais</td><td>PL-SAP-3</td><td>1</td><td>R$ 1.684,45</td></tr></tbody></table><p>&nbsp;</p><table><thead><tr><th>CARGOS</th><th>SIMBOLOS</th><th>NÚMERO</th><th>VENCIMENTO</th></tr></thead><tbody><tr><td>Digitador</td><td>PL-SAP-5</td><td>1</td><td>R$ 1.725,94</td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.529-Dispoe-sobre-o-reajuste-salarial-dos-servidores-efetivos-da-Camara-Municipal-e-da-outras-providencias.pdf', '2023-04-05', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
