INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.525/2023', 'lei-no-1-525-2023', 'projeto_lei', '1525/2023', 2023, 'Dispõe sobre alteração do quadro de cargos de provimento efetivo do Poder Executivo Municipal de Sumé-PB', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art.1º Esta lei dispõe sobre a ampliação de vagas de cargo efetivo no Quadro de Pessoal da Prefeitura Municipal de Sumé-PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art.2° Para os fins previstos nesta Lei ficam ampliadas o número de vagas dos seguintes cargos de provimento efetivo da Administração Municipal, conforme dispõe as Leis n° 1.109/2013, 1.214/2017, 1.302/2019, 1.326/2019, 1.460/2022 e 1.478/2022 e a Lei Complementar nº 44/2022:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Em virtude da ampliação das vagas dos cargos efetivos a que se refere esta lei, fica autorizado o Poder Executivo Municipal a atualizar a Tabela do Quadro Permanente de Pessoal da Prefeitura Municipal de Sumé-PB, consoante às Leis que regem os cargos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Esta lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 17 de março de 2023.</span></p><table><thead><tr><th>CARGOS</th><th>HABILITAÇÃO</th><th>CÓDIGO/SÍMBOLO/CLASSE</th><th>VENCIMENTO</th><th>N° DE VAGAS AMPLIADAS</th></tr></thead><tbody><tr><td>Professor de Fundamental II</td><td>Professor de Música</td><td>MAG-402.13.1</td><td>R$ 2.884,20</td><td>1</td></tr><tr><td>Professor de Fundamental II</td><td>Professor de Linguagens e Códigos</td><td>MAG-402.16</td><td>R$ 2.884,20</td><td>2</td></tr><tr><td>Assistente Social</td><td></td><td>ANS-501.1</td><td>2.751,61</td><td>1</td></tr><tr><td>Orientador Social</td><td></td><td>SAD-206.1</td><td>1.356,73</td><td>1</td></tr><tr><td>Nutricionista</td><td></td><td>SSA-ANS-601.14.1</td><td>2.751,61</td><td>1</td></tr><tr><td>Motorista Classe "D"</td><td></td><td>ANE-107.6</td><td>1.381,41</td><td>1</td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.525-Altera-o-quadro-de-cargos-de-provimento-efetivo-do-Poder-Executivo-Municipal-de-Sume-PB.pdf', '2023-03-17', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.524/2023', 'lei-no-1-524-2023', 'projeto_lei', '1524/2023', 2023, 'Dispõe sobre a denominação de Logradouro Público do Município de Sumé', '<p class="ng-star-inserted"><span class="ng-star-inserted">O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO DA PARAÍBA, no uso de suas atribuições legais e de conformidade com a Lei Orgânica Municipal, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica denominada de Karina Alves de Barros, a Rua Projetada VL 08 localizada no Loteamento Morada Nobre III, o Bairro do Mandacaru. Localiza-se paralelamente as Ruas Projetadas VL 10 e VL 12, e perpendicularmente as Ruas Projetadas VL 02 e VL 04.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 17 de março de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.524-Denomina-Logradouro-Pulico-Karina-Alves-de-Barros.pdf', '2023-03-17', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.523/2023', 'lei-no-1-523-2023', 'projeto_lei', '1523/2023', 2023, 'DISPÕE SOBRE A IMPLANTAÇÃO DO PISO SALARIAL NACIONAL AOS PROFISSIONAIS DO MAGISTÉRIO DA EDUCAÇÃO BÁSICA PÚBLICA DO MUNICÍPIO DE SUMÉ. ALTERA A LEI MUNICIPAL N° 1.460, DE 16 DE FEVEREIRO 2022 E O ANEXO ÚNICO DA LEI COMPLEMENTAR Nº. 13, DE 08 DE JANEIRO DE 2010 DÁ OUTRAS PROVIDÊNCIAS.', '<p>O CHEFE DO PODER EXECUTIVO DO MUNICÍPIO DE SUMÉ, ESTADO<br />DA PARAÍBA, no uso de suas atribuições legais e de conformidade com a Lei Orgânica Municipal, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</p><p>Art. 1º. Fica alterado o ANEXO ÚNICO, da Lei Complementar Municipal n° 13, de 08 de janeiro de 2010, para implantar o piso salarial dos Profissionais do Magistério da Educação Básica Pública do Município de Sumé, nos limites estabelecidos pela Portaria Interministerial 07/2023 de 29 de dezembro de 2022, para conceder um reajuste de 15% (quinze por cento) sobre o vencimento base de 2022, passando a vigorar nos termos da Tabela I, do Anexo I, da Lei Complementar Municipal n° 13, de 08 de janeiro de 2010, com as alterações da presente Lei.</p><p>Art. 2º. Em caso de aprovação tardia pelo Poder Legislativo, que enseje perda remuneratória referente a algum mês de 2023, deverá ser ressarcida a partir do mês seguinte à vigência da presente norma, somada à nova remuneração prevista na Tabela I, do Anexo Único.</p><p>Art. 3º. Ficam equiparados os vencimentos dos professores dos Ensinos Fundamentais I e II, da Educação Básica Pública do Município de Sumé, passando a vigorar nos termos de planilha unificada, conforme anexo único da presente Lei.</p><p>Parágrafo Único. Aos servidores inativos e pensionistas, cujo ato de concessão é embasado pelo princípio constitucional da paridade salarial fica assegurada o reajuste previsto no art. 1ª, de acordo com os valores contidos na planilha unificada, conforme anexo único da presente Lei.</p><p>Art. 4º. Fica mantida a alíquota de 5% (cinco por cento) para as progressões verticais e progressões horizontais, previstas no § 3º do art. 8º, da LC nº. 13/2010, devendo-se tomar por base de cálculo inicial o valor previsto para os níveis MAG 401.1.1 e MAG 402.1.1.</p><p>Art.5º. Esta Lei entra em vigor na data de sua publicação, ficando revogadas as disposições em contrário.</p><p>Gabinete do Prefeito do Município de Sumé-PB, 09 de março de 2023.</p><p style="text-align: center"><strong>ÉDEN DUARTE PINTO DE SOUSA </strong><br /><strong>Prefeito do Município de Sumé-PB</strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.523-Dispoe-sobre-a-implantacao-do-piso-salarial-nacional-do-magisterio-da-educacao-basica-do-municipio-de-Sume.pdf', '2023-03-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.522/2023', 'lei-no-1-522-2023', 'projeto_lei', '1522/2023', 2023, 'Dá nova redação a dispositivos da Lei Municipal n.° 754, de 25 de março de 1999 que dispõe sobre a Política Municipal dos Direitos das Crianças e do Adolescente.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica alterado o art. 14º da Lei Municipal n.° 754, de 25 de março de 1999, em consonância com Lei Federal n.º 8.069, de 13 de julho de 1990 e Lei Federal n.º 13.824, de 9 de maio de 2019, que passa a ter a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">“Art. 14. O Conselho Tutelar dos Direitos da Criança e do Adolescente do município de Sumé, é um órgão autônomo e de funcionamento permanente, integrante da administração pública local, composto por 5 (cinco) membros, escolhido pela população local por processo de escolha mediante sufrágio universal e direto, pelo voto uninominal facultativo e secreto, para mandato de 4 (quatro) anos, permitido recondução por novo processo de escolha. (NR)”</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 09 de março de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.522-Da-nova-redacao-a-dispositivos-da-Lei-Municipal-n-754.-de-25-de-marco-de-1999-que-dispoe-sobre-a-Politica-Municipal-dos-Direitos-da-Crianca-e-do-Adolescente.pdf', '2023-03-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.521/2023', 'lei-no-1-521-2023', 'projeto_lei', '1521/2023', 2023, 'Denomina Passagem Molhada no Sítio Ingá de Abílio Alves da Silva (Abílio Curtidor)', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso de suas atribuições legais, faz saber que a Câmara Municipal aprovou e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominado a Passagem Molhada do Sítio Ingá de Abília Alves da Silva (Abílio Curtidor), na zona rural do município de Sumé-PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 09 de março de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.521-Denomina-Passagem-Molhada-Abilio-Alves-da-Silva-Abilio-Curtidor-Sitio-Inga.pdf', '2023-03-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.520/2023', 'lei-no-1-520-2023', 'projeto_lei', '1520/2023', 2023, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 150.000,00 (Cento e cinquenta mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.09 – SECRETARIA OBRAS E SERVIÇOS URBANOS</span><br class="ng-star-inserted" /><span class="ng-star-inserted">17.512.2019.1047 – Implantação do Sistema de Tratamento de Resíduos Sólidos - Aterro Sanitário</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 500 – Recursos não vinculados de Impostos</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.50.36.00.00.00.00 – Outros Serviços de Terceiros - Pessoa Física ..............................................122.015,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.50.36.00.00.00.00 – Outros Serviços de Terceiros - Pessoa Jurídica...............................27.985,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, as anulações das seguintes dotações:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.09 – SECRETARIA OBRAS E SERVIÇOS URBANOS</span><br class="ng-star-inserted" /><span class="ng-star-inserted">17.512.2019.1047 – Implantação do Sistema de Tratamento de Resíduos Sólidos - Aterro Sanitário</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 700 – Outras Transferências de Convênios ou Repasses da União</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.90.51.00.00.00.00 – Obras e Instalações...........................................................150.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé-PB, 16 de fevereiro de 2023.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito Constitucional do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.520-Abertura-de-creditos-adicionais-especiais.pdf', '2023-02-16', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
