INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.508/2022', 'lei-no-1-508-2022', 'projeto_lei', '1508/2022', 2022, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 1.116.745,22 (Um milhão e cento e dezesseis mil e setecentos e quarenta e cinco reais e vinte e dois centavos), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.12 - FUNDO MUNICIPAL DE SAÚDE</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">12.365.2001.1004 – Construção, Reforma e/ou Ampliação de Unidades de Educação Infantil</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">(387) F.R. 571 4.4.90.51.00.00.00.00. - Obras e Instalações....................................................... 1.116.745,22</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Para cobertura do crédito adicional especial definido no Artigo 1º a fim de se respeitar às disposições legais previstas na Lei 4320/64, será utilizado o excesso de arrecadação dos recursos de Transferência do ESTADO referente a CONVÊNIOS.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé/PB, 27 de outubro de 2022.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito Constitucional --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/02/Lei-n-1.508-Abertura-de-creditos-adicionais-especiais.pdf', '2022-10-27', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
