INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.451/2021', 'lei-no-1-451-2021', 'projeto_lei', '1451/2021', 2021, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 167.000,00 (Centos e sessenta e sete mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.12 – FUNDO MUNICIPAL DE SAÚDE</span><br class="ng-star-inserted" /><span class="ng-star-inserted">12.301.2014.1034 – Aquisição de Veículo e Equipamentos para Saúde</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">(900)10.301.2014.1034 F.R. 1001-4.4.90.52.00.00.00.00 - Equipamentos e Material Permanente 167.000,00</span><br class="ng-star-inserted" /><span class="ng-star-inserted">TOTAL. 167.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, as anulações das seguintes dotações:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.12 – FUNDO MUNICIPAL DE SAÚDE</span><br class="ng-star-inserted" /><span class="ng-star-inserted">12.301.2014.1034 – Aquisição de Veículo e Equipamentos para Saúde</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">(591)10.301.2014.1034 F.R. 1215-4.4.90.52.00.00.00.00 - Equipamentos e Material Permanente 67.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">(592)10.301.2014.1034 - F.R. 1220 -4.4.90.52.00.00.00.00 - Equipamentos e Material Permanente 100.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">TOTAL 167.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé (PB), em 29 de novembro de 2021.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">Prefeito Constitucional do Município de Sumé</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.451-Abertura-de-creditos-adicionais-especiais-para-o-fim-que-especifica-e-adota-outras-providencias.pdf', '2021-11-29', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.452/2021', 'lei-no-1-452-2021', 'projeto_lei', '1452/2021', 2021, 'Denomina logradouro Público do Município de Sumé', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominada de RUA JOSÉ ANTONIO DE SOUSA, a Rua Projetada 11 localizada no Loteamento Paixão, Rodovia BR 412. Paralelamente a Rua Antonio Pereira da Silva Filho (Bacurau) e a Rua Projetada 12, e perpendicularmente a Rua Severino Leite Filho (Birino).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), 02 de dezembro de 2021.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito Constitucional do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.452-Denomina-logradouro-Publico-Jose-Antonio-de-Sousa_a-Rua-Projetada-11-localizada-no-Loteamento-Paixao.pdf', '2021-12-02', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
