INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.440/2021', 'lei-no-1-440-2021', 'projeto_lei', '1440/2021', 2021, 'Denomina Açude Público do Sítio Riacho das Porteiras de Inácio Alves da Silva (Inácio Chucalheiro)', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominado Açude Público do Sítio Riacho das Porteiras de Inácio Alves da Silva (Inácio Chucalheiro), localizado no Sítio Riacho das Porteiras, zona rural do município de Sumé-Pb.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé (PB), em 22 de outubro de 2021.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito Constitucional do Município de Sumé-PB --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.440-Denomina-Acude-Publico-do-Sitio-Riacho-das-Porteiras-de-Inacio-Alves-da-Silva_Inacio-Chucalheiro.pdf', '2021-10-22', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.439/2021', 'lei-no-1-439-2021', 'projeto_lei', '1439/2021', 2021, 'Autoriza a redução da reserva não edificável, ao longo da BR 412 que compreende entre no Município de Sumé PB.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica assegurado o direito, de edificações na faixa não edificável contígua às faixas de domínio público ao longo da BR 412 possibilitando a redução da extensão dessa faixa não edificável no Município de Sumé/PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único - O uso da faixa de domínio poderá ser utilizado para via marginal ou rua lateral: via paralela à pista principal BR412, de um ou ambos os lados, com o objetivo de atender ao tráfego local, longitudinal à rodovia e pertinente à área urbanizada adjacente, e permitir o disciplinamento dos locais de entrada e saída da rodovia.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Ao longo das faixas de domínio público da BR 412, a reserva de faixa não edificável será de no mínimo 5(cinco) metros de cada lado.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§1° - A diminuição deve estar embasada por estudos técnicos e estar alinhada com o plano de desenvolvimento do município.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - As edificações localizadas nas áreas contíguas às faixas de domínio público dos trechos de rodovia que atravessem perímetros urbanos ou áreas urbanizadas passíveis de serem incluídas em perímetro urbano, desde que construídas até a data de promulgação deste parágrafo, ficam dispensadas da observância da exigência prevista no §1º deste artigo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé (PB), em 29 de setembro de 2021.</span></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.439-Autoriza-a-reducao-da-reserva-nao-edificavel-ao-longo-da-BR-412-_Municipio-de-Sume.pdf', '2021-09-29', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
