INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.392/2020', 'lei-no-1-392-2020', 'projeto_lei', '1392/2020', 2020, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">O PREFEITO CONSTITUCIONAL DO MUNICÍPIO DE SUMÉ, no uso das atribuições legais, faz saber que a Câmara Municipal aprovou e é sancionada a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 688.000,00 (seiscentos e oitenta e oito mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.09 – SECRETARIA DE OBRAS E SERVIÇOS URBANOS</span><br class="ng-star-inserted" /><span class="ng-star-inserted">15.451.2005.1010 – Ampliação da Rede de Iluminação Pública</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1920 – Recursos de Operações de Crédito</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.90.51.00 – Obras e Instalações............................................... 688.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Para cobertura do crédito adicional especial definido no Artigo 1º a fim de se respeitar às disposições legais previstas no artigo 43 da Lei 4320/64, será utilizado o excesso de arrecadação oriundo da Operação de Crédito autorizada pela Lei Municipal 1.318/2019 e/ou a anulação parcial ou total de dotações do orçamento vigente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito Sumé (PB), em de 28 de setembro de 2020</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.392-Abertura-de-creditos-adicionais.pdf', '2020-09-28', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
