INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.381/2020', 'lei-no-1-381-2020', 'projeto_lei', '1381/2020', 2020, 'INSTITUI O PROGRAMA DE ACOLHIMENTO FAMILIAR PROVISÓRIO DE CRIANÇAS E ADOLESCENTES, DENOMINADO "PROGRAMA FAMÍLIA ACOLHEDORA".', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica instituído o Programa de Acolhimento Familiar Provisório de Crianças e Adolescentes, denominado "Programa Família Acolhedora", como parte inerente da política de atendimento à criança e ao adolescente do Município de Sumé, sob a coordenação da Secretaria da Assistência Social.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único. O programa criado de acordo com a cabeça deste artigo, como medida protetora, destinar-se-á a toda criança ou adolescente, residentes no Município de Sumé, com idade entre 0 (zero) e 18 (dezoito) anos, em situação de risco e vulnerabilidade social, e/ou que tiveram seus direitos ameaçados ou violados, afastados da família de origem.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - São objetivos do “Programa Família Acolhedora":</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - oferecer alternativa de espaço protegido à criança e ao adolescente em situação de risco e vulnerabilidade social e/ou que tiveram seus direitos ameaçados ou violados, em caráter provisório e excepcional, por intermédio de encaminhamento às famílias acolhedoras, para garantir a convivência familiar e comunitária;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - fortalecer a família de origem, com o reconhecimento de suas possibilidades e dificuldades, para possibilitar a reintegração da criança e/ou adolescente, afastados provisoriamente de seu convívio;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - incluir a família de origem na rede de proteção social e pessoal, visando à manutenção do convívio familiar e comunitário das crianças e/ou adolescentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - selecionar e capacitar as famílias candidatas ao acolhimento da criança e/ou adolescente, como medida de proteção;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - contribuir na superação da situação vivida pela criança e pelo adolescente com menor grau de sofrimento e perda, preparando-os para a reintegração familiar;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VI - preparar a criança ou adolescente, incluída(o) no Programa, para colocação em família substituta, no caso de destituição do poder familiar.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - A criança ou adolescente cadastrada(o) no Programa receberá:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - com absoluta prioridade, atendimento nas áreas de saúde, educação e assistência social, por meio das políticas públicas existentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - acompanhamento psicossocial e pedagógico, preferencialmente, pelo “Programa Família Acolhedora”;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - estímulo à manutenção e/ou reformulação de vínculos afetivos com sua família de origem;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - permanência com seus irmãos na mesma família acolhedora, sempre que possível.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Para os efeitos desta Lei, considera-se família acolhedora, a família, sem discriminação de gênero, etnia, estado civil e religião, e que preencham os seguintes requisitos:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - ter idade acima de 25 (vinte e cinco) anos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - ser residente no Município de Sumé;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - não possuir antecedentes criminais;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - não apresentar problemas psiquiátricos e/ou dependência de substâncias psicoativas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - concordância de todos os membros da família;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VI - disponibilidade real em oferecer proteção e amor à criança e ao adolescente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - A inscrição das famílias interessadas em participar do "Programa Família Acolhedora” será gratuita, feita por meio do preenchimento de Ficha de Cadastro do Programa, apresentando os documentos a seguir indicados:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - Carteira de Identidade;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - Certidão de Nascimento ou de Casamento;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - Comprovante de Residência;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - Certidão Negativa de Antecedentes Criminais;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - Laudo de Avaliação Psiquiátrico.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único. Não se incluirá no Programa a pessoa com vínculo de parentesco com a criança ou o adolescente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º - A família acolhedora prestará serviço de caráter voluntário não gerando vínculo empregatício ou profissional com o órgão executor do Programa.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7º - As famílias cadastradas receberão acompanhamento e preparação contínua, sendo orientadas sobre os objetivos do Programa e sobre a diferenciação entre a medida de adoção e a medida de proteção de acolhimento familiar.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único. A preparação das famílias cadastradas será feita por intermédio de uma metodologia participativa, considerando os seguintes aspectos:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - orientação direta às famílias nas visitas domiciliares e entrevistas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - participação nos encontros de formação e troca de experiência com todas as famílias, com abordagem do Estatuto da Criança e do Adolescente, das questões sociais relativas à família de origem, das relações intrafamiliares, da guarda como medida de colocação em família substituta, do papel da família acolhedora e outras questões pertinentes;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - participação em cursos e eventos de formação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 8° - A família acolhedora, incluída no Programa, receberá um Auxílio Pecuniário de MEIO SALÁRIO MÍNIMO por criança ou adolescente acolhida(o).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º - A família acolhedora selecionada poderá acolher, ao mesmo tempo, mais de uma criança/adolescente, se forem irmãos/irmãs. Fazendo jus ao auxílio correspondente a cada uma. Em se tratando de grupo de mais 2 (dois) irmãos(as) deverá se realizar uma avaliação, preferencialmente, pela Equipe Técnica do Programa para verificar se o acolhimento em família acolhedora é a melhor alternativa para o caso ou seria mais adequado o acolhimento em outra modalidade de serviço.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - O auxílio de que trata este artigo será pago proporcionalmente aos dias de acolhimento, quando estes forem menores do que o mês corrido.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º - O Serviço de Acolhimento Familiar contará com Recursos Orçamentários e Financeiros alocados no orçamento da Secretaria Municipal de Assistência Social, podendo contar de forma complementar com recursos dos Fundos para a Infância e a Adolescência – FIA e de parcerias com o Estado e a União.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 9° - A duração do acolhimento varia de acordo com a situação apresentada. A duração máxima de referência será de 2 (dois) anos, podendo haver acolhimento mais prolongado, se determinado judicialmente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 10. O encaminhamento da criança ou adolescente ocorrerá mediante "Termo de Guarda e Responsabilidade" concedido à Família Acolhedora, determinado em processo judicial.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 11. O término do acolhimento familiar da criança ou do adolescente se dará por determinação judicial, considerando o parecer técnico, atendendo aos encaminhamentos pertinentes ao retorno à família de origem ou colocação em família substituta, por meio das seguintes medidas:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - acompanhamento após a reintegração familiar, visando a não reincidência do fato que provocou o afastamento da criança;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - acompanhamento psicossocial à família acolhedora após o desligamento da criança, atendendo às suas necessidades;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - orientação e supervisão do processo de visitas entre a família acolhedora e a família substituta.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 12. A família acolhedora tem a responsabilidade familiar pelas crianças e adolescentes acolhidos, responsabilizando-se pelo que segue:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - todos os direitos e responsabilidades legais reservados ao guardião, obrigando-se à prestação de assistência material, moral e educacional à criança e ao adolescente, conferindo ao seu detentor o direito de opor-se a terceiros, inclusive aos pais, nos termos do artigo 33 do Estatuto da Criança e do Adolescente;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - participação do processo de preparação, formação e acompanhamento;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - prestação de informações aos profissionais do “Programa Família Acolhedora” sobre a situação da criança e do adolescente acolhida(o);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - contribuição na preparação da criança ou adolescente para o retorno à família de origem, sempre sob orientação técnica dos profissionais do Programa Família Acolhedora;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - nos casos de inadaptação, a família procederá à desistência formal da guarda, responsabilizando-se pelos cuidados da criança acolhida até novo encaminhamento, o qual será determinado pela autoridade judiciária competente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único. A transferência para outra família acolhedora deverá ser feita de maneira gradativa e com o devido acompanhamento, realizado pelo Programa de Família Acolhedora.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 13. A equipe técnica que for constituída prestará acompanhamento sistemático à família acolhedora, à criança acolhida e à família de origem.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 14. Após a emissão de parecer psicossocial favorável à inclusão no Programa e decisão judicial, quando for o caso, as famílias assinarão um Termo de Adesão ao “Programa Família Acolhedora”.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 15. Em caso de desligamento do Programa, as famílias acolhedoras deverão fazer solicitação por escrito, justificando a saída.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 16. O acompanhamento à família de origem e o processo de reintegração familiar da criança será realizado, preferencialmente, pelos profissionais do “Programa Família Acolhedora”.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º Os profissionais acompanharão as visitas entre criança/família de origem/família acolhedora, a serem realizadas em espaço discernido por equipe técnica.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º Sempre que solicitado pela autoridade judiciária competente, a equipe técnica prestará informações sobre a situação da criança acolhida e informará quanto à possibilidade ou não de reintegração familiar, bem como, poderá ser solicitado a realização de laudo psicossocial com apontamento das vantagens e desvantagens da medida, com vistas a subsidiar as decisões judiciais.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º Quando entender necessário, visando à agilidade do processo e a proteção da criança, a equipe técnica prestará informações ao Juizado sobre a situação da criança acolhida e as possibilidades ou não de reintegração familiar.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 17. Ο Poder Executivo regulamentará a presente Lei, em especial quanto a:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - obrigações e competências da Secretaria da Assistência Social e demais órgãos públicos, eventualmente envolvidos com o Programa "Família Acolhedora";</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - normas e procedimentos para implantação, execução, acompanhamento e controle do “Programa Família Acolhedora", e</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - criação de Equipes Interdisciplinares.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 18. Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé, em 29 de julho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.381-Institui-o-programa-de-acolhimento-familiar-provisorio-de-criancas-e-adolescentes.-denominado-Programa-Familia-Acolhedora.pdf', '2020-07-29', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.380/2020', 'lei-no-1-380-2020', 'projeto_lei', '1380/2020', 2020, 'Dispõe sobre as Diretrizes para elaboração da Lei Orçamentária de 2021', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO I</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DISPOSIÇÕES PRELIMINARES</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - Ficam estabelecidas, em cumprimento à Constituição Federal, à Constituição do Estado da Paraíba, à Lei Orgânica do Município de Sumé e ao Plano Plurianual em vigor, sem prejuízo da incidência de demais normas pertinentes, as diretrizes orçamentárias para o exercício financeiro de 2021, compreendendo:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) as Metas Fiscais;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) as prioridades e metas da administração pública municipal direta, indireta e fundacional, bem como as do Poder Legislativo Municipal;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) a estrutura e a organização do orçamento do município;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">d) as diretrizes para a elaboração da Lei Orçamentária Anual;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">e) as diretrizes gerais para execução e alterações do orçamento do município;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">f) as disposições sobre a Dívida Pública Municipal;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">g) as disposições relativas às despesas com pessoal e encargos sociais;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">h) as disposições sobre receita e alterações na legislação tributária;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">i) as disposições finais.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO II</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS METAS FISCAIS</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Em cumprimento ao estabelecido no artigo 4º da Lei Complementar nº. 101, de 04 de maio de 2000, as metas fiscais de receitas, despesas, resultado primário, nominal e montante da dívida pública para o exercício financeiro de 2021, estão identificados nos Demonstrativos I a VIII desta Lei, em conformidade com a Portaria nº. 286, de 07 de maio de 2019, da Secretaria do Tesouro Nacional.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único – São anexos de Metas Fiscais referidos no caput:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo I – Metas Anuais</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo II – Avaliação do Cumprimento das Metas Fiscais do Exercício Anterior</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo III – Metas Fiscais Atuais Comparadas Com as Fixadas nos Três Exercícios Anteriores</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo IV – Evolução do Patrimônio Líquido</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo V – Origem e Aplicação dos Recursos Obtidos Com a Alienação de Ativos</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo VI – Avaliação da Situação Financeira e Atuarial do Regime Próprio de Previdência dos Servidores Públicos</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo VII – Estimativa e Compensação da Renúncia de Receita</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Demonstrativo VIII – Margem de Expansão das Despesas Obrigatórias de Caráter Continuado</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - A Lei Orçamentária Anual abrangerá as Entidades da Administração Direta, Indireta e da Seguridade Social.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO III</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS PRIORIDADES E METAS DA ADMINISTRAÇÃO MUNICIPAL</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4° - As prioridades e metas da Administração Pública Municipal para o exercício de 2021, estabelecidas no Anexo I desta Lei, incluem os investimentos, as atividades de natureza continuada, o RPPS – Regime Próprio de Previdência, a conservação e manutenção do patrimônio, administrativas e as obrigações constitucionais e legais, e, deverão estar desdobradas em ações, observando os seguintes eixos estratégicos para o desenvolvimento sustentável do município:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I. Poder Legislativo</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) Modernização dos serviços do Poder Legislativo, mediante a racionalização das atividades administrativas, e melhoria das rotinas de trabalho.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II. Poder Executivo</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) A reorganização e modernização da estrutura administrativa do Poder Executivo Municipal, visando à otimização da prestação dos serviços públicos à comunidade;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) Conservação, preservação e recuperação do meio ambiente natural: ampliar áreas verdes e espaços livres públicos, universalizar os sistemas de água e esgoto do município e realizar gestão integrada e sustentável de resíduos sólidos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) Educação: ampliação da oferta de ensino, do conhecimento e da inclusão social, qualificar a rede de educação infantil, por meio da ampliação e melhoria das unidades destinadas às crianças de zero a cinco anos, qualificar o ensino fundamental, qualificar a proposta pedagógica, por meio do Plano Municipal de Educação, acelerar o desempenho dos estudantes da rede municipal, promover a excelência e a universalização do ensino público;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">d) Saúde: melhorar a qualidade do atendimento e ampliar a rede de saúde, fortalecer a rede de saúde existente, por meio de melhorias na infraestrutura das unidades de atendimento e da capacitação dos profissionais com destaque para os níveis de atendimento que proporcione a melhoria da qualidade de vida da população;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">e) Assistência Social: promoção social à família, à criança e ao adolescente e à população idosa com ênfase no cumprimento das políticas estabelecidas no Estatuto do Idoso, Estatuto da Criança e do Adolescente, fortalecer a rede de assistência, com a manutenção e ampliação do serviço de atendimento e acolhida das pessoas em situação de vulnerabilidade social, e intensificar a política sobre drogas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">f) Esporte e lazer: estimular o uso e manutenção dos equipamentos esportivos municipais, incentivar as atividades esportivas nas escolas da rede municipal de ensino, garantir a qualidade dos equipamentos de lazer e esportes nos espaços públicos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">g) Direitos humanos: fortalecer as políticas para as mulheres, fortalecer políticas públicas e programas direcionados à igualdade racial, ao idoso, pessoas com deficiência, crianças, adolescentes e jovens, por meio da expansão dos serviços oferecidos por diferentes órgãos da prefeitura;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">h) Gestão pública: aprimorar o processo através de diversos canais de comunicação, garantir transparência na divulgação e acesso às informações, otimizar os mecanismos de gestão, dando continuidade ao programa de captação de receitas e controle e redução de despesas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">i) Desenvolvimento econômico: estimular e desenvolver o empreendedorismo, a inovação tecnológica e social, a economia solidária, compartilhada e colaborativa;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">j) Valorização da cultura: implementar os mecanismos de incentivo à cultura municipal; promover a identidade e o pertencimento dos cidadãos pela Cidade; incentivar a ocupação dos espaços públicos por diferentes linguagens artísticas e culturais; viabilizar atividades de formação em arte, cultura, gestão e produção cultural; e</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">k) Melhoria da eficiência e aumento do processo de transparência governamental: fomentar os canais de diálogo com a população.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. As prioridades e metas especificadas no Demonstrativo Programas terão procedência na alocação de recursos no Orçamento de 2021, não se constituindo, todavia, em limite à programação das despesas.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. As metas físicas serão indicadas em nível de projetos e atividades.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º. Na elaboração da proposta orçamentária, o Poder Executivo poderá aumentar ou diminuir as metas físicas estabelecidas no Plano Plurianual, a fim de compatibilizar a despesa orçada à receita estimada, de forma a preservar o equilíbrio das contas públicas.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º. No orçamento para o exercício de 2021, os recursos destinados aos programas sociais deverão ser alocados, prioritariamente para atendimento da população localizada nas áreas de menor índice de desenvolvimento humano, sendo assim priorizada a população carente e de baixa renda do município.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 5º. Considera-se programas sociais aqueles destinados à melhoria qualitativa e quantitativa nas áreas de educação, saúde, saneamento básico, assistência social, habitação, geração de emprego e renda e suplementação alimentar.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO IV</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DA ESTRUTURA E ORGANIZAÇÃO DO ORÇAMENTO</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - O Orçamento Fiscal e da Seguridade Social discriminarão a despesa por Unidade Orçamentária, segundo a classificação funcional e a programática, explicitando para cada projeto, atividade ou operação especial, respectivas metas e valores da despesa por grupo e modalidade de aplicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. A classificação funcional-programática seguirá o disposto na Portaria n.º 42, do Ministério de Orçamento e Gestão, de 14/04/99.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. Os programas, classificadores da ação governamental, pelos quais os objetivos da administração se exprimem, serão aqueles constantes do Plano Plurianual 2019-2021.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º. Na indicação do grupo de despesa, a que se refere o caput deste artigo, será obedecida a seguinte classificação, de acordo com a Portaria Interministerial n.º 163/01, da Secretaria do Tesouro Nacional e da Secretaria de Orçamento Federal, e suas alterações:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) Pessoal e encargos sociais (1);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) juros e encargos da dívida (2);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) outras despesas correntes (3);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">d) investimentos (4);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">e) inversões financeiras (5);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">f) amortização da dívida (6).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º. A Reserva do Regime Próprio de Previdência do Servidor - RPPS, prevista no art. 8°, da Portaria Interministerial nº 163, de 04 de maio de 2001, e a Reserva de Contingência, prevista no art. 5º, inciso III da Lei Complementar Federal nº 101, de 04 de maio de 2000, serão identificadas, quanto ao grupo de natureza de despesa, pelo código 9.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º - Para efeito desta Lei, entende-se por:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - Programa, o instrumento de organização da ação governamental visando à concretização dos objetivos pretendidos, sendo mensurado por indicadores estabelecidos no plano plurianual;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - Atividade, um instrumento de programação para alcançar o objetivo de um programa, envolvendo um conjunto de operações que se realizam de modo contínuo e permanente, das quais resulta um produto necessário à manutenção da ação de governo;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - Projeto, um instrumento de programação para alcançar o objetivo de um programa, envolvendo um conjunto de operações, limitadas no tempo, das quais resulta um produto que concorre para a expansão ou aperfeiçoamento da ação de governo;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - Operação especial, as despesas que não contribuem para a manutenção das ações de governo, das quais não resulta um produto, e não geram contraprestação direta sob a forma de bens ou serviços.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - Unidade orçamentária, o menor nível da classificação institucional, agrupada em órgãos orçamentários, entendidos estes como os de maior nível da classificação institucional.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7° - Cada programa identificará as ações necessárias para atingir os seus objetivos, sob a forma de atividades, projetos e operações especiais, especificando os respectivos valores e metas, bem como as unidades orçamentárias responsáveis pela realização da ação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 8° - Cada ação orçamentária deve identificar a função e a subfunção às quais se vinculam e apresentará as dotações orçamentárias, por fontes de recursos, modalidades de aplicação e por grupos de natureza da despesa, conforme classificações da portaria interministerial nº 163, de 04 de maio de 2001.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 9° - As categorias de programação, de que trata esta Lei, serão identificados no projeto de lei orçamentária por programas, atividades, projetos ou operações especiais.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 10° - O projeto de Lei Orçamentária anual para o exercício financeiro de 2021 será encaminhado ao Poder Legislativo municipal, até o dia 30 de setembro do corrente ano, de acordo com a Lei Orgânica do Município, e será composto de:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - texto da lei;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - consolidação dos quadros orçamentários, incluindo os complementos referenciados no art. 22, inciso III, IV e § único da Lei n.º 4.320/64;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - anexo dos orçamentos fiscal e da seguridade social, detalhando a receita e a despesa na forma definida nesta Lei;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - discriminação da receita e da despesa, referente aos orçamentos fiscal e da seguridade social.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO V</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DIRETRIZES PARA ELABORAÇÃO DA LEI ORÇAMENTÁRIA ANUAL</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 11° - O Orçamento do Município para o exercício de 2021 será elaborado visando assegurar o controle social e a transparência na execução do orçamento.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. Os processos de elaboração e definição do Projeto de Lei Orçamentária para 2021 e sua respectiva execução deverão ser realizados de modo a evidenciar a transparência da gestão fiscal, observando-se o princípio da publicidade, permitindo-se dessa forma, o acesso da sociedade às informações relativas a essas etapas.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. Será assegurada aos cidadãos a participação no processo de elaboração e fiscalização do orçamento, através da definição das prioridades de investimento de interesse do município, mediante regular processo de consulta.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 12° - No projeto de lei orçamentária anual, as receitas e as despesas serão orçadas a preços correntes, estimados para o exercício a que se refere.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 13° - Na programação, nenhuma despesa poderá ser fixada sem que estejam definidas as respectivas fontes de recursos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único - Além de observar as demais diretrizes estabelecidas nesta Lei, a alocação de recursos na Lei Orçamentária e em seus créditos adicionais, bem como a respectiva execução, serão feitas de forma a propiciar o controle dos custos das ações e a avaliação dos resultados dos programas de governo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 14° - A estimativa de receita de operações de crédito, para o exercício de 2021, terá como limite máximo, as Resoluções 40/01 e 43/01, do Senado Federal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 15° - A Lei Orçamentária poderá autorizar a realização de operações de crédito por antecipação de receita, desde que observado o disposto no art. 38 da LRF.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 16° - O Projeto de Lei Orçamentária Anual na programação da despesa não poderá:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - Fixar despesas sem que existam fontes de recursos compatíveis e sem que as unidades executoras sejam instituídas legalmente;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - Incluir projetos com a mesma finalidade em mais de um órgão, ressalvadas aqueles que complementem ações específicas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III – Incluir recursos em favor de Clubes e Associações de servidores ou quaisquer outras entidades congêneres, excetuadas Creches e Escolas para o atendimento pré-escolar;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - Consignar dotações para investimentos com duração superior a um exercício financeiro que não esteja previsto no Plano plurianual;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - Consignar dotações para pagamento, a qualquer título, a servidores da administração pública, por Consultoria ou Assistência Técnica, exclusive aqueles custeados com recursos provenientes de convênios, acordos, ajustes ou instrumentos congêneres, firmados com órgãos de direito público ou privados, nacionais ou internacionais.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 17° - A Reserva de Contingência será constituída exclusivamente com recursos do Tesouro Recursos Ordinários, em montante equivalente a, no mínimo, 0,5% (cinco décimos por cento), e no máximo, 3% (três por cento) da receita corrente líquida estimada nos termos do inciso IV do art. 2º da Lei Complementar Federal nº 101, de 2000.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. Os recursos da Reserva de Contingência serão destinados ao atendimento de passivos contingentes e outros riscos e eventos fiscais imprevistos, obtenção de resultado primário positivo se for o caso, e para a abertura de créditos adicionais suplementares conforme disposto na Portaria MPO n°. 42/1999, art. 5º. e Portaria STN n°. 163/2001, art. 8°. (art. 5º. III, “b” da LRF).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. Na hipótese de não utilização da Reserva de Contingência nos fins previstos no caput até 30 de novembro do exercício vigente desta lei, os recursos correspondentes poderão ser destinados à cobertura de créditos suplementares e especiais que necessitem ser abertos para reforço ou inclusão de dotações orçamentárias.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 18° - O Poder Executivo Municipal está autorizado a incluir na Lei Orçamentária anual e em seus créditos adicionais, dotações a título de auxílio financeiro a pessoas físicas e, de quaisquer recursos do município, para clubes, associações e entidades privadas, sem fins lucrativos, de atividades de natureza continuada de atendimento direto ao público nas áreas de assistência social, saúde e educação, ou que esteja registrada no Conselho Nacional de Assistência Social - CNAS, a dotações a título de subvenções sociais.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. - A concessão de benefício de que se trata o caput deste artigo, deverá ser definida em lei específica.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. - Os recursos destinados à pessoa física, tanto em moeda em corrente como bens materiais, para atender necessidades proeminentes nas áreas de saúde, educação, transporte e de moradia, quando não possível o atendimento por Programas de Governo, estará condicionado à comprovação do reconhecimento de estado de pobreza, na forma da Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º. - A concessão de benefícios é classificada como:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) Contribuições – dotações destinadas a atender despesas as quais não corresponda contraprestação direta de bens e serviços e não sejam reembolsáveis pelo recebedor, bem como as destinadas a atender outras entidades de direito privado, desde que, sejam entidades sem fins lucrativos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) Subvenções sociais – dotações destinadas a atender despesas de instituições privadas sem fins lucrativos de caráter educacional, cultural, esportiva ou assistencial, inclusive as de assistência à saúde;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) Auxílios – dotação destinada a atender despesas de investimentos e inversões financeiras de entidades privadas sem fins lucrativos de interesse público voltado para área de abrangência social.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º. A pessoa jurídica para habilitar-se ao recebimento dos recursos referidos neste artigo, além do cumprimento das exigências legais, deve apresentar declaração de funcionamento regular nos últimos três anos, tanto por entidades fiscalizadoras quanto reguladoras.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 5º. As entidades beneficiadas com recursos do Tesouro Municipal deverão prestar contas no prazo de 30 dias, contados do recebimento do recurso, na forma estabelecida pela Secretaria das Finanças Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 6º. – É vedada a destinação de recursos públicos a entidades privadas que estejam com prestações de contas irregulares ou inadimplentes.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 19°. O Orçamento da Seguridade Social compreenderá as dotações destinadas a atender as ações nas áreas de Saúde, Previdência e Assistência Social, abrangendo, entre outros, os recursos provenientes de receitas próprias dos órgãos, entidades e fundos que, por sua natureza, devam integrar o orçamento.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único Deverão ser discriminados os recursos do município e as transferências de recursos do estado e da União para a execução descentralizada das Ações de Saúde, como já vem sendo executado no âmbito da Administração Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 20° - As emendas apresentadas ao Projeto da Lei Orçamentária anual deverão obedecer ao disposto no artigo 169 e seus parágrafos da Constituição Estadual.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. Fica vedada a apresentação de emendas que impliquem o aumento de despesas sem a estimativa de seu valor e sem a indicação de fontes de recursos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. – A anulação da Reserva de Contingência para atender a emendas não poderá ser superior a 5% (cinco por cento) do valor consignado no Projeto da Lei Orçamentária para esse fim.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO VI</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DIRETRIZES GERAIS PARA EXECUÇÃO E ALTERAÇÃO DO ORÇAMENTO</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 21° - No caso de necessidade de limitação de empenho das dotações orçamentárias e da movimentação financeira, a ser efetivado nas hipóteses previstas no art. 9º e no inciso II, § 1º, do art. 31, da Lei Complementar n° 101 de 04/05/2000, essa limitação será aplicada aos Poderes Executivo e Legislativo de forma proporcional à participação de seus orçamentos, excluídas as duplicidades, na Lei Orçamentária Anual.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º. Ocorrendo a hipótese definida no caput deste artigo, o Poder Executivo comunicará ao Poder Legislativo, o montante que lhe caberá tornar indisponível, para empenho e movimentação financeira.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º. O repasse financeiro a que se refere o art. 168, da Constituição Federal/88 fica incluído na limitação prevista no caput deste artigo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 22° - A execução orçamentária, direcionada para a efetivação das metas fiscais estabelecidas em anexo, deverá ainda, manter a receita corrente superavitária frente às despesas correntes, com a finalidade de comportar a capacidade própria de investimento.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 23° - A abertura de créditos suplementares e especiais dependerá da existência de recursos disponíveis para a despesa.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único - A Lei Orçamentária poderá autorizar a abertura de créditos Adicionais Suplementares até o limite de 50% da despesa fixada, em consonância com as disposições contidas no artigo 43 da Lei 4.320/64.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 24° - As alterações do Quadro de Detalhamento de Despesa – QDD nos níveis de modalidade de aplicação, elemento de despesa e fonte de recurso, observado os mesmos grupos de despesa, categoria econômica, projeto/atividade/operação especial e unidade orçamentária, poderão ser realizadas para atender às necessidades de execução, mediante publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As alterações decorrentes da abertura e reabertura de créditos adicionais integrarão os quadros de detalhamento de despesa, os quais serão modificados independentemente de nova publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 25° - A execução orçamentária e financeira da despesa poderá se dar de forma descentralizada atendendo a previsão legal e precedida da designação, por ato do Poder Executivo, do respectivo gestor responsável.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 26° - São vedados quaisquer procedimentos pelos ordenadores de despesas, que impliquem na execução de despesas sem comprovada e suficiente disponibilidade de dotação orçamentária e sem adequação com as cotas financeiras de desembolso.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 27° - Entende-se, para efeito do § 3º, do art. 16 da Lei Complementar nº 101, de 2000, como despesa irrelevante, aquelas cujo valor não ultrapasse, para bens e serviços, os limites dos incisos I e II do art. 24 da Lei 8.666, de 1993.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 28° - A Contadoria Geral do Município, vinculada à Secretaria das Finanças Municipal consolidará, através de sistema integrado, a execução orçamentária, financeira e o controle dos registros patrimoniais de todos os órgãos e entidades vinculadas ao Orçamento do município de Sumé, unificando os procedimentos e normas de Contabilidade e Tesouraria.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único O Poder Legislativo encaminhará a Secretaria das Finanças Municipal, até o dia 20 após o encerramento de cada bimestre, os demonstrativos contábeis para consolidação do Relatório de Execução Orçamentária, nos termos da Lei Complementar n° 101, de 2000.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 29° - Na execução orçamentária para 2021, a apuração dos custos dar-se-á por meio do Sistema de Mensuração de Custos Públicos - SMCP, conforme determina a alínea "e", do inciso I, art. 4º e o §3º do art. 50, ambos da Lei Complementar Federal nº 101, de 04 de maio de 2000.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO VII</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DISPOSIÇÕES SOBRE A DÍVIDA PÚBLICA MUNICIPAL</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 30° - A Lei Orçamentária de 2021 poderá conter autorização para contratação de Operação de Crédito para atendimento à Despesas de Capital, observado o limite de endividamento, de até 10% das Receitas Correntes Líquidas apuradas até o final do semestre anterior a assinatura do contrato, ou até o limite dos créditos destinados a despesas de capital, na forma estabelecida nos artigos 30, 31 e 32 da LRF e Lei nº. 4.320/64.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 31° A contratação de operações de crédito dependerá de autorização legislativa.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 32° - Ultrapassado o limite de endividamento definido na legislação pertinente e enquanto perdurar o excesso, o Poder Executivo obterá resultado primário necessário através da limitação de empenho e movimentação financeira. (art. 31, § 1º., II da LRF).</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO VIII</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DISPOSIÇÕES RELATIVAS ÀS DESPESAS COM PESSOAL E ENCARGOS SOCIAIS</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 33° - Os Poderes Executivo e Legislativo terão os limites na elaboração de suas propostas orçamentárias para pessoal e encargos sociais, observados os arts. 19, 20 e 71, da Lei Complementar n.º 101, de 2000.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 34° - Fica excluído da proibição prevista no inciso V, parágrafo único, do art. 22, da Lei Complementar 101, a contratação de hora extra para pessoal, quando se tratar de relevante interesse público.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 35° - Se a despesa total com pessoal ultrapassar os limites estabelecidos no art. 19º da LC n.° 101/00, a adoção das medidas de que tratam os parágrafos 3º e 4º do Art. 169 da CF, preservará os servidores das áreas de Saúde, Educação e Assistência Social.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 36° - Para fins de atendimento no disposto no artigo 169, § 1º, inciso II, da Constituição Federal, ficam autorizadas a concessão de quaisquer vantagens, aumentos e reajustes de remuneração, inclusive revisão geral dos vencimentos e proventos dos servidores, criação de cargos, empregos e funções, nomeação de servidor aprovados em concurso público, alterações na estrutura de carreiras do quadro de pessoal dos Poderes Executivo e Legislativo e de suas entidades descentralizadas instituídas e mantidas pelo poder Público Municipal, desde que observadas e obedecidos o disposto nos artigos 19 a 23 da Lei de Responsabilidade Fiscal.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO IX</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DISPOSIÇÕES SOBRE RECEITA E ALTERAÇÕES NA LEGISLAÇÃO TRIBUTÁRIA</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 37° - A estimativa da receita que constará do Projeto de Lei Orçamentária para o exercício de 2021, contemplará as medidas de aperfeiçoamento da administração dos tributos municipal, com vistas à expansão de base de tributação e consequente aumento das receitas próprias.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 38° - A estimativa da receita mencionada no artigo anterior será levada em consideração o impacto de alteração na legislação tributária, observada a capacidade econômica do contribuinte e a justa distribuição de renda, com destaque para a:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - Atualização da planta genérica de valores do Município;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - Revisão, atualização ou adequação da legislação sobre o Imposto Predial e Territorial Urbano;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - Revisão da legislação referente ao Imposto sobre Serviços de Qualquer Natureza;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - Revisão da Legislação aplicável ao Imposto sobre Transmissão Inter Vivos e de Bens Imóveis e de Direito Reais sobre Imóveis;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - Instituição de taxas pela utilização efetiva ou potencial de serviços públicos específicos e divisíveis, prestados ao contribuinte ou posto à sua disposição;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VI - Revisão da legislação sobre taxas pelo exercício de polícia;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VII – Revisão das isenções dos tributos municipais, para manter o interesse público e a justiça fiscal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º - Com o objetivo de estimular o desenvolvimento econômico e cultural do Município, o Poder Executivo encaminhará projetos de lei de incentivos ou benefícios de natureza tributária, cuja renúncia poderá alcançar os montantes dimensionados no anexo de metas fiscais, já considerados no cálculo do resultado primário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - A concessão ou a ampliação de benefícios fiscais somente poderá ocorrer se atendidas as determinações contidas no artigo 14 da Lei de Responsabilidade Fiscal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º - A parcela da receita orçamentária prevista no caput deste artigo, que decorrer de propostas de alterações na legislação tributária, ainda em tramitação, quando do envio do projeto de lei orçamentária anual à Câmara, poderá ser identificada, discriminando-se as despesas cuja execução ficará condicionada à aprovação das respectivas alterações legislativas.</span></p><p class="ng-star-inserted"><strong class="ng-star-inserted"><span class="ng-star-inserted">CAPÍTULO X</span><br class="ng-star-inserted" /><span class="ng-star-inserted">DAS DISPOSIÇÕES FINAIS</span></strong></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 39° - A mesa da Câmara Municipal deverá encaminhar ao Poder Executivo Municipal até 31 de julho de 2020, a proposta orçamentária relativa às dotações do Poder Legislativo para o exercício de 2021, observado as disposições do Art. 29-A da Constituição Federal e a previsão da Receita do exercício corrente prevista pelo Poder Executivo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 40° - A Procuradoria Geral do Município encaminhará a Secretaria da Finança Municipal, até o dia 01 de julho de 2020, os processos de precatórios judiciais a serem incluídos no projeto de lei orçamentária para o exercício de 2021.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 41° - A Câmara Municipal deverá devolver para a sanção do Prefeito, a Lei Orçamentária Anual com os respectivos autógrafos, até 01 de dezembro do corrente ano, que deverá ser sancionada e publicada até o dia 31 de dezembro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único A Câmara Municipal não entrará em recesso enquanto não cumprir o disposto no "caput" deste artigo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 42° - Caso o projeto de lei orçamentária de 2021 não seja sancionado até 31 de dezembro de 2020, a programação dele constante poderá ser executada em cada mês, até o limite de 1/12 (um, doze avos) do total de cada dotação, na forma da proposta remetida a Câmara Municipal, enquanto a respectiva lei não for sancionada.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 43° - O Poder Executivo estabelecerá a programação financeira e o cronograma anual de desembolso mensal, nos termos do art. 8º da Lei Complementar n° 101/00, por grupo de despesa, bem como as metas bimestrais de arrecadação, até trinta dias após a publicação da lei orçamentária anual.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 44° - Serão consideradas legais as despesas com juros e multas pelo eventual atraso no pagamento de compromissos assumidos, motivados por insuficiência de tesouraria.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 45° - Os créditos especiais e extraordinários, abertos nos últimos quatro meses do exercício, poderão ser reabertos no exercício subsequente, por ato do Chefe do Poder Executivo.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 46° - O Poder Executivo está autorizado a transferir recursos financeiros aos órgãos da administração indireta que participam do orçamento do município.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 47° - Ο Executivo Municipal está autorizado a assinar convênios com o Governo Federal e Estadual através de seus órgãos da administração direta ou indireta, para a realização de obras ou serviços de competência ou não do município.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 48° - O Poder executivo enviará à Câmara Municipal uma via impressa e por meio eletrônico o projeto da Lei Orçamentária Anual.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 49° - O Poder executivo divulgará em seu sítio oficial na internet os projetos da Lei de Diretrizes Orçamentárias (LDO), do Plano Plurianual (PPA) e da Lei do Orçamento Anual (LOA), além da divulgação de sua execução orçamentária e financeira, através do Portal da Transparência.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 50° - Esta Lei entra em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 06 de julho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.380-LDO-Exercicio-2021.pdf', '2020-07-06', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.387/2020', 'lei-no-1-387-2020', 'projeto_lei', '1387/2020', 2020, 'Altera a Lei 1.375, de 22 de junho de 2020, para autorizar o Poder Executivo a prorrogar o incentivo temporário e transitório até o mês de dezembro do corrente ano aos servidores que exercem atividades presenciais de enfrentamento, prevenção e combate ao Coronavírus (COVID-19)', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - O art. 1º da Lei 1.375, de 22 de junho de 2020 passa a vigorar com a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Em virtude da declarada situação de emergência e calamidade em saúde pública do município de Sumé, fica autorizado o Poder Executivo a prorrogar o incentivo temporário e transitório até o mês de dezembro do corrente ano aos servidores que exercem atividades presenciais de enfrentamento, prevenção e combate ao coronavírus (COVID 19), nos valores de R$</span><code class="rendered"><span class="katex"><span class="katex-mathml">210,00 (duzentos e dez reais) para profissionais de nível médio R$</span></span></code><span class="ng-star-inserted">390,00 (trezentos e noventa reais) para profissionais de nível superior.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé, em 06 de agosto de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.-387_-Prorroga-Incentivo-dos-profissionais-de-saude-na-linha-de-frente-da-COVID-19.pdf', '2020-08-06', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.379/2020', 'lei-no-1-379-2020', 'projeto_lei', '1379/2020', 2020, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 245.727,52 (Duzentos e quarenta e cinco mil e setecentos e vinte e sete reais e cinquenta e dois reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">2.12- FUNDO MUNICIPAL DA SAÚDE</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">10.122.1003.2010 – Manutenção das Atividades do Fundo Municipal de Saúde</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1992 Aux. Fin. da União aos Municípios (Saúde e Assist. Social) inc. I Art. 5º da LC 173/2020</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.30.00.00.00.00 - Material de Consumo 5.727,52</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.11 FUNDO MUNICIPAL DA ASSISTÊNCIA SOCIAL</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">08.244.2009.2050 – Manutenção de Outros Programas do FNAS</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1992 Aux. Fin. da União aos Municípios (Saúde e Assist. Social) inc. I Art. 5º da LC 173/2020</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.1.90.11.00.00.00.00 – Vencimentos e Vantagens Fixas - Pessoal Civil 160.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">08.244.2009.2052 - Concessão de Benefícios Eventuais de Assistência Social</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1992 Aux. Fin. da União aos Municípios (Saúde e Assist. Social) inc. I Art. 5º da LC 173/2020</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.32.00.00.00.00 – Material, Bens ou Serviço p/Distribuição Gratuita 80.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Para cobertura do crédito adicional especial definido no Artigo 1º a fim de se respeitar às disposições legais previstas no artigo 43 da Lei 4320/64, será utilizado o excesso de arrecadação oriundo da transferência da União referente ao recurso da LC 173/2020 e/ou a anulação parcial ou total de dotações do orçamento vigente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito Sumé/PB, em 26 de junho de 2020</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.379-Abertura-de-creditos-adicionais-especiais-para-o-fim-que-especifica-e-adota-outras-providencias.pdf', '2020-06-26', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.378/2020', 'lei-no-1-378-2020', 'projeto_lei', '1378/2020', 2020, 'Fixa os subsídios dos Vereadores para o período da Legislatura de 2021 a 2024', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - O subsídio mensal do Presidente da Câmara Municipal, para o mandato correspondente ao período de 2021 à 2024, fica fixado em parcela única no valor de R$</span><code class="rendered"><span class="katex"><span class="katex-mathml">9.750,00 (nove mil setecentos e cinquenta reais) mensais e do vereador, em parcela</span></span></code><code class="rendered"><span class="katex"><span class="katex-mathml">única, no valor de R$</span></span></code><span class="ng-star-inserted"> 6.500,00 (seis mil e quinhentos reais) mensais; cujos valores dos subsídios só terão seus efeitos financeiros implementados a partir de 1º de janeiro de 2022 nos termos do art. 8º da lei Complementar 173, de 27 de maio de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Será observado para o pagamento dos subsídios dos vereadores não apenas o limite previsto no art. 29, Inciso VI, da Constituição Federal, como ainda, o limite total com os gastos com pessoal previsto pelo art. 18, § 2º, da Lei Complementar 101, de 04 maio de 2000;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Dos valores referentes aos gastos com pessoal, será deduzida a parcela referente aos dispêndios como pagamento dos servidores públicos e então será apurada a parcela destinada aos gastos com subsídios dos vereadores, sendo estes divididos de forma isonômica, observando-se os limites de remuneração de que trata a magna carta;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Esta Lei entra em vigor na data de sua publicação, produzindo efeitos financeiros a 1º de janeiro de 2022.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 22 de junho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.378-Fixa-os-subsidios-dos-Vereadores-para-o-periodo-da-Legislatura-de-2021-a-2024.pdf', '2020-06-22', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.377/2020', 'lei-no-1-377-2020', 'projeto_lei', '1377/2020', 2020, 'Fixa os subsídios do Prefeito, do Vice Prefeito e dos Secretários Municipais para o período do mandato de 2021 à 2024.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - O subsídio mensal do Prefeito Municipal de Sumé, para o mandato correspondente ao período de 2021 à 2024, fica fixado, em parcela única, no valor de R$</span><code class="rendered"><span class="katex"><span class="katex-mathml">15.000,00(Quinze mil reais) e do Vice−Prefeito, em parcela única, no valor de R$ </span></span></code><span class="ng-star-inserted">7.500,00 (Sete mil e quinhentos reais) mensais, cujos valores dos subsídios só terão seus efeitos financeiros implementados a partir de 1º de janeiro de 2022 nos termos do art. 8º da lei Complementar 173, de 27 de maio de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2° - Os subsídios mensais dos Secretários Municipais ficam fixados, em parcela única de R$ 5.000,00 (Cinco mil reais) mensais, cujos valores dos subsídios só terão seus efeitos financeiros implementados a partir de 1º de janeiro de 2022 nos termos do art. 8° da lei Complementar 173, de 27 de maio de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Os subsídios fixados por esta Lei serão atualizados com base no mesmo índice de reajuste concedido ao funcionalismo público municipal, respeitando como limite a correção inflacionária dos meses anteriores à concessão da respectiva reposição, apurada segundo o indicador oficial adotado para efeito da proteção assegurada no art. 37, X, da Constituição Federal;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Esta Lei entra em vigor na data de sua publicação, produzindo efeitos financeiros a 1º de janeiro de 2022.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 22 de junho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.377-Fixa-os-subsidios-do-Prefeito.-do-Vice-Prefeito-e-dos-Secretarios-Municipais-para-o-periodo-do-mandato-de-2021-a-2024-e-da-outras-providencias.pdf', '2020-06-22', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.376/2020', 'lei-no-1-376-2020', 'projeto_lei', '1376/2020', 2020, 'Denomina logradouro Público', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica denominada de Rua JUAREZ GOUVEIA DA CRUZ, a Rua VL 04 localizada no Loteamento Milênio, nesta cidade, perpendicular as Ruas VL 01 e VL 08, possuindo as seguintes dimensões: 103,00 m ao Norte, 106,00 ao Sul e 7,00 de largura, totalizando uma área total de 733,60 m².</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito do Município de Sumé (PB), em 22 de junho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.376-Fica-denominada-de-Rua-JUAREZ-GOUVEIA-DA-CRUZ.-a-Rua-VL-04.-localizada-no-Loteamento-Milenio.pdf', '2020-06-22', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.374/2020', 'lei-no-1-374-2020', 'projeto_lei', '1374/2020', 2020, 'Suspende, durante o período de 180 (cento e oitenta) dias, a cobrança dos empréstimos consignados contraídos pelos servidores públicos, ativos e inativos, do Município de Sumé.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - Fica suspensa, pelo período de 180 (cento e oitenta) dias, a cobrança dos empréstimos consignados contraídos pelos servidores públicos, ativos e inativos, do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º - As parcelas que ficarem em aberto durante este período, deverão ser acrescidas ao final do contrato, sem a incidência de juros ou multas.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - É vedado a instituição financeira que concedeu o empréstimo incluir o nome do servidor público em qualquer órgão de restrição ao credito em decorrência da suspensão prevista nesta lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - A Suspensão dos empréstimos consignados se dará de forma automática pelo ente ao qual o servidor está vinculado.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo Único – O servidor que não desejar que haja suspensão do seu empréstimo consignado deverá fazer requerimento direcionado a Administração Municipal manifestando sua intenção.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 09 de junho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.374-Suspende-por-180-cento-e-oitenta-dias.-a-cobranca-dos-emprestimos-consignados-contraidos-pelos-servidores-publicos.-ativos-e-inativos.-do-Municipio-de-Sume.pdf', '2020-06-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.373/2020', 'lei-no-1-373-2020', 'projeto_lei', '1373/2020', 2020, 'DESAFETA BEM PÚBLICO IMÓVEL ONDE FUNCIONOU A ESCOLA UMEIEF ESMERINO BARBOSA, SITUADA NO SÍTIO TERRA VERMELHA, NO MUNICÍPIO DE SUMÉ – PB.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica desafetado para fins de utilidade pública bem dominical o imóvel rural onde funcionou a escola UMEIEF Esmerino Barbosa, localizada no Sítio Terra Vermelha, no Município de Sumé – PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - O referido imóvel será utilizado para a implantação de uma Unidade Básica de Saúde na localidade.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - A Futura Unidade Básica de Saúde permanecerá com a mesma denominação, qual seja, Esmerino Barbosa.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 09 de junho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.373-Desafeta-bem-publico-imovel-onde-funcionou-a-UMEIEF-Esmerino-Barbosa-no-sitio-Terra-Vermelha.pdf', '2020-06-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.372/2020', 'lei-no-1-372-2020-2', 'projeto_lei', '1372/2020', 2020, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 50.000,00 (cinquenta mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único - As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">2.11 - FUNDO MUNICIPAL DA ASSISTÊNCIA SOCIAL</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">08.244.2009.2044 – Desenvolvimento das Ações de Proteção Social Básica – PAIF/CRAS</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1312 - Transferência de Convênios - Assistência Social</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.32.00.00.00.00 - Material, Bens ou Serviço p/Distribuição Gratuita........ 40.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">08.244.2009.2052 - Concessão de Benefícios Eventuais de Assistência Social</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1312 - Transferência de Convênios - Assistência Social</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.32.00.00.00.00 - Material, Bens ou Serviço p/Distribuição Gratuita... 10.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2° - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, as anulações das seguintes dotações:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.11 - FUNDO MUNICIPAL DA ASSISTÊNCIA SOCIAL</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">08.244.2009.2050 – Manutenção de Outros Programas do FNAS</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1312 - Transferência de Convênios - Assistência Social</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.36.00.00.00.00 - Outros Serviços de Terceiros - Pessoa Física..............................15.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.39.00.00.00.00 - Outros Serviços de Terceiros - Pessoa Jurídica......................... 35.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito Sumé/PB, em de 09 de junho de 2020</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO DO MUNICÍPIO --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.372-Abertura-de-creditos-adicionais-especiais-para-o-fim-que-especifica.pdf', '2020-06-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.371/2020', 'lei-no-1-371-2020', 'projeto_lei', '1371/2020', 2020, 'Dispõe sobre a denominação de Logradouro Público do Município de Sumé', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica denominada de Raimunda Ferreira de Oliveira “Dona Dorinha” a praça a ser construída no Bairro de Várzea Redonda, no terreno que faz esquina com a Rua Francisco Paulino Gomes e a Rua Santa Mendonça, possuindo as dimensões de 11,00m de frente e fundos por 15,20m de lateral esquerda e direita, totalizando uma área de 167,20 m².</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ, em 09 de junho de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.371-Denomina-Raimunda-Ferreira-de-Oliveira-Dona-Dorinha.-a-praca-a-ser-construida-no-bairro-de-Varzea-Redonda.pdf', '2020-06-09', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.370/2020', 'lei-no-1-370-2020', 'projeto_lei', '1370/2020', 2020, 'Altera o texto da Lei nº 1.344, de 27 de dezembro de 2019 – Lançamento do Imposto sobre a Propriedade Predial e Territorial Urbana – IPTU para o exercício de 2020', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">“Art. 1º O Imposto sobre a Propriedade Predial e Territorial Urbana – IPTU relativo ao exercício de 2020 será lançado, em caráter especial, em 3 (parcelas), da seguinte forma:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - 1ª parcela: em 30 (trinta) de setembro de 2020 – (quarta-feira);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - 2ª parcela: em 30 (trinta) de outubro de 2020 – (sexta-feira);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - 3ª parcela: em 30 (trinta) de novembro de 2020 – (segunda-feira).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Mediante opção do contribuinte, o IPTU poderá ser lançado dentro do seguinte esquema:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - cota antecipada e a data do vencimento para pagamento integral: até o dia 30 (trinta) de setembro de 2020 (quarta-feira), com um desconto de 10% (dez) por cento;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - cota única para pagamento integral até o dia 30 (trinta) de novembro de 2020 (segunda-feira).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé, em 06 de maio de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.370-Altera-o-texto-da-Lei-n-1.344.-de-27-de-dezembro-de-2019-Lancamento-do-IPTU-para-o-exercicio-de-2020.pdf', '2020-05-06', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.369/2020', 'lei-no-1-369-2020', 'projeto_lei', '1369/2020', 2020, 'Acrescenta o Parágrafo 1º ao Art. 1º da Lei nº 1.284, de 06 de dezembro de 2018, que trata dos Valores de Custeio do Serviço de Iluminação Pública – CIP.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica acrescentado o Parágrafo 1º ao Art. 1º da Lei nº 1.284/2018, que terá a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">"... §1° - Ficam também isentos da Contribuição de Iluminação Pública – CIP, os usuários que possuem mais de uma UC, desde que comprovem, através de requerimento direcionado a Prefeitura, que uma dessas UC''s se destinam exclusivamente a ligação de equipamentos voltados a agricultura e pecuária, como eletrobombas (submersas ou injetoras, monofásicas ou trifásicas, independentes de sua potencia - CV) e moendas, recaindo a isenção apenas sobre esta."</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Ficam revogadas as disposições em contrário;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé, em 06 de maio de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.369-Acrescenta-o-Paragrafo-1-ao-Art.-1-da-Lei-n-1.284.-de-06-de-dezembro-de-2018.-que-trata-dos-Valores-de-Custeio-do-Servico-de-Iluminacao-Publica-–-CIP.pdf', '2020-05-06', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.368/2020', 'lei-no-1-368-2020', 'projeto_lei', '1368/2020', 2020, 'Denomina logradouro Público', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica denominada de Rua MARIA DO SOCORRO DE SOUSA “SOCORRO DO DNOCS”, a Rua Projetada localizada no Bairro Novo Sumé – Quadra 11 - situada entre as Ruas Antônio Bezerra de Sousa e João Rodrigues Sobrinho, nesta cidade.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 06 de maio de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.368-Denomina-Rua-Maria-do-Socorro-de-Sousa-Socorro-do-Dnocs.pdf', '2020-05-06', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.367/2020', 'lei-no-1-367-2020', 'projeto_lei', '1367/2020', 2020, 'Autoriza o Poder Executivo Municipal a fazer uma Cessão de Uso não onerosa de imóvel do patrimônio do Município de Sumé à FUNDAÇÃO INSTITUTO BRASILEIRO DE GEOGRAFIA E ESTATÍSTICA - IBGE.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo do Município de Sumé autorizado a fazer uma Cessão de Uso – não onerosa – à FUNDAÇÃO INSTITUTO BRASILEIRO DE GEOGRAFIA E ESTATÍSTICA – IBGE, com sede de atividades na Rua Irineu Pinto, 94, na cidade de João Pessoa – PB, Estado da Paraíba, CEP 58.010-100, inscrita no CNPJ (MF) sob o n° 33.787.094/0018-98, de 2 (duas) salas do patrimônio do Município de Sumé integradas às dependências do Centro de Comercialização e Artesanato Elias Pereira de Araújo, nesta cidade. O imóvel é localizado na Rua Alice Japiassu de Queiroz, n° 130, Centro - e inscrito no Cadastro de Patrimônio Imobiliário da Prefeitura do Município de Sumé sob o nº 01010060120001.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º - O imóvel de que trata a cabeça deste artigo destinar-se-á exclusivamente ao desenvolvimento das atividades públicas conferidas legalmente à Cessionária.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - O imóvel não poderá ter destinação diversa da que está descrita no § 1º deste artigo e em cláusulas resolutórias do contrato de cessão de uso respectivo, resolvendo-se a cessão inclusive pela extinção ou desativação da Cessionária, perdendo, esta, e neste caso, todas as benfeitorias de qualquer natureza feitas no imóvel cedido para uso, independentemente de indenização por parte do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - O Contrato de Cessão de Uso – não remunerada - referido ao art. 1º, desta Lei, observará, dentre outras, as seguintes condições especiais:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - o prazo de duração da cessão de uso é de 5 (cinco) anos, contados, inclusive, a partir da data da assinatura do Contrato de Cessão de Uso;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - a cessão de uso não será remunerada;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - a cessão de uso não poderá ser transferida por ato </span><span class="ng-star-inserted">inter vivos</span><span class="ng-star-inserted">, nem será objeto de hipoteca ou de qualquer outro gravame real;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - a Cessionária responderá por todos os encargos civis, administrativos, previdenciários, securitários e tributários que venham a incidir sobre o imóvel, e</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - as benfeitorias, de qualquer natureza, e as acessões feitas no imóvel serão incorporadas incondicionalmente ao patrimônio imobiliário do Município de Sumé por expiração do prazo da Cessão de Uso e a conseqüente devolução do imóvel ao patrimônio do Município.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º A Cessionária é responsável:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - pelo encaminhamento ao Município de Sumé de todas as notificações, citações, avisos ou intimações dos poderes públicos que forem entregues no imóvel;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - pela manutenção, conservação e reparos que entender conveniente à instalação dos seus equipamentos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - pelas reparações que o imóvel necessitar, no transcorrer do contrato de cessão de uso.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º A Cessionária não poderá sublocar, ceder, emprestar ou transferir – total ou parcialmente – sob qualquer fundamento ou pretexto, a cessão de uso.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º A Cessionária deve providenciar, às suas expensas, quaisquer obras de manutenção que se tornarem necessárias, dando conhecimento ao Município de Sumé de eventuais danos sofridos ou reparos providos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º A Cessionária arcará com a responsabilidade civil por todos e quaisquer danos materiais e morais causados, dolosa ou culposamente, ao Município de Sumé ou a terceiros, por ação ou omissão de seus empregados, trabalhadores, prepostos ou representantes.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 5º A cessão que trata esta Lei será objeto de assinatura de Contrato de Cessão de Uso entre as partes.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 6º O foro competente para dirimir qualquer questão oriunda da cessão de uso será o da Comarca de Sumé, Estado da Paraíba, excluído qualquer outro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 27 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.367-Autoriza-o-Poder-Executivo-Municipal-a-fazer-uma-Cessao-de-Uso-–-nao-onerosa-de-imovel-do-patrimonio-do-Municipio-de-Sume-ao-IBGE.pdf', '2020-03-27', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.366/2020', 'lei-no-1-366-2020', 'projeto_lei', '1366/2020', 2020, 'Dispõe sobre as medidas temporárias para enfrentamento da emergência de saúde pública, decorrente da pandemia do COVID-19, conforme disposto na Lei Federal nº. 13.979, de 6 de fevereiro de 2020', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Esta Lei dispõe sobre medidas temporárias a serem adotadas no âmbito do Município de Sumé, Estado da Paraíba, para enfrentamento da emergência de saúde pública, decorrente da pandemia do COVID-19.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Para o enfrentamento da emergência de saúde a que se refere o art. 1º, desta Lei, poderão ser adotadas as seguintes medidas:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I – isolamento social;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II – quarentena dos suspeitos de infecção;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III – determinação de realização compulsória de:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) Exames médicos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) testes laboratoriais;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) coleta de amostras clínicas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">d) vacinação e outras medidas profiláticas;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">e) tratamentos médicos específicos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - estudo ou investigação epidemiológica;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V- requisição de bens, serviços e produtos de pessoas naturais e jurídicas, hipóteses em que será garantido o pagamento posterior de justa indenização.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º Para os fins desta Lei, considera-se:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - isolamento social: separação de pessoas e bens contaminados, transportes e bagagens no âmbito intermunicipal, mercadorias e outros, com o objetivo de evitar a contaminação ou a propagação do COVID-19, e sua possível contaminação ou propagação;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - quarentena: restrição de atividade ou separação de pessoas suspeitas de contaminação das demais que não estejam doente, ou ainda, bagagens, contêineres, animais ou meios de transporte, no âmbito de sua competência, com objetivo de evitar a possível contaminação ou propagação do COVID-19.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º A requisição administrativa, a que se refere o inciso V, da cabeça deste artigo, deverá garantir ao particular o pagamento de justa indenização e observará o seguinte:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - terá suas condições e requisitos definidos, em portaria do Secretário da Saúde, e envolverá, se for o caso:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) hospitais, clínicas e laboratórios privados, independentemente da celebração de contratos administrativos, e</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) profissionais da saúde, hipótese que não acarretará a formação de vínculo estatutário ou empregatício com a administraçãopública.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - a vigência não poderá exceder duração da emergência de saúde pública decorrente do COVID-19.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º A adoção das medidas para viabilizar o tratamento e/ou obstar a contaminação ou a propagação do COVID -19 deverá guardar proporcionalidade com a extensão da situação de emergência.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º A Secretaria da Saúde recomendará o auto-isolamento, pelo período de 15 (quinze) dias, de qualquer pessoa que tenha sido remanescente das áreas consideradas de transmissão local/comunitária, consideradas pelos Boletins Epidemiológicos emitidos e atualizados pelo Ministério da Saúde.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Ficam suspensos, pelo período de vigência desta Lei, no âmbito do Município de Sumé:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - eventos de qualquer natureza com público superior a 10 (dez) pessoas, pelo período inicial de 30 (trinta) dias, passível de prorrogação, em caso de agravamento da pandemia;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - viagens de servidores municipais a serviço do Município de Sumé para deslocamento no território nacional ou no exterior;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - prova de vida dos servidores municipais inativos;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV - férias de servidores de áreas essenciais ao enfrentamento dapandemia pelo período de 60 (sessenta dias);</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V- o Serviço de Convivência e Fortalecimento de Vínculos (SCFV) e o Projeto Mônica a partir de 25 de março de 2020;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">VI - cirurgias eletivas no Hospital e Maternidade “Alice de Almeida”.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§1° Os deslocamentos mencionados no inciso II, a cabeça deste artigo, poderão ser, excepcionalmente, autorizados pela Secretaria da Administração, após justificativa formal da necessidade da viagem a ser elaborada pelo Secretário da pasta interessada, com antecedência mínima de 10 (dez) dias.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º Todo servidor municipal que retornar do exterior ou das áreas consideradas de transmissão local deverá efetuar comunicação imediata à Secretaria da Saúde e permanecer em isolamento domiciliar por 15 (quinze) dias, mesmo que não apresente qualquer sintoma relacionado ao COVID-19, devendo aguardar orientações da Pasta.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º Locais de grande circulação de pessoas, como bancos, casas lotéricas, cooperativas de crédito, shopping Centers e comércio em geral deverão adotar medidas de prevenção para conter a disseminação do COVID-19.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º Os serviços de alimentação, tais como restaurantes lanchonetes e bares deverão adotar medidas de prevenção para conter a disseminação do COVID-19.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Fica antecipado o recesso escolar que ocorre sempre no meio de cada ano letivo, na Rede Municipal de Ensino, inclusive particular, devendo o recesso ser iniciado no dia 25 de março de 2020 até o dia 17 de abril de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º O atendimento ao público na sede da Prefeitura de Sumé deverá ocorrer mediante o agendamento prévio na recepção do prédio-sede.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º No caso dos serviços vinculados à Secretaria da Saúde, ato do Secretário desta Pasta, considerando as peculiaridades de cada caso, disciplinará o atendimento na Rede Municipal de Saúde.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º Em relação aos serviços vinculados à Secretaria da Assistência Social, ato do Secretário desta pasta, considerando as peculiaridades de cada caso, disciplinará o atendimento nos serviços a ela vinculados.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º Ficam autorizadas, no âmbito municipal,a realização de despesas, inclusive com dispensa de licitação, para a contratação de profissionais e pessoas jurídicas, aquisição de medicamentos, alimentos e outros bens necessários para o enfrentamento da pandemia, podendo, inclusive, serem distribuídos à população que necessitar.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º As ações e os serviços públicos de saúde voltados à contenção da emergência serão articulados pela Secretaria de Saúde e contarão com a participação dos demais órgãos e entidades da Administração Pública Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7° A tramitação de processos referentes às matérias veiculadas nesta Lei correrá em regime de urgência e terá prioridade em todos os órgãos e entidades da Estrutura Administrativa do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 8° O Comitê de Monitoramento das medidas de prevenção e combate ao COVID-19 será composto por um representante de cada órgão a seguir indicado:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I- Gabinete do Prefeito;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II- Secretaria da Assistência Social;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III – Secretaria da Saúde;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">IV – Secretaria de Administração;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">V - Secretaria da Educação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º Cabe ao Chefe do Poder Executivo indicar os respectivos membros do Comitê de Monitoramento, que serão nomeados por meio de Portaria.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º O Coordenador do Comitê de Monitoramento, devidamente nomeado pelo Chefe do Poder Executivo poderá convidar representantes de outros órgãos e entidades, públicos ou privados, bem como outras secretarias não elencadas nesta Lei para participarem das suas reuniões.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º As reuniões ocorrerão sempre que convocadas pelo Coordenador.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 9° Fica permitido, em caráter especial, o funcionamento das atividades descritas no § 1º, Incisos III (padarias), IV (supermercados e mercadinhos), V (Quitandas), VI (distribuidoras de água e gás) e X (estabelecimentos de vendas de ração e produtos veterinários), do decreto 1313 de 20 de março de 2020, presencialmente e com controle de entrada de pessoas até às 18h00 e, logo após, apenas para serviços de tele entrega (delivery).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 10° A casa lotérica e correspondentes bancários poderão permanecem abertos até o dia 31 de março (terça-feira), das 08h às 15h somente para atendimento dos beneficiários do Bolsa Família e pagamento de salários de servidores, aposentados e pensionistas. Após essa data, terá que cumprir o que está previsto no decreto 1313 de 20 de março de 2020 e suas possíveis alterações.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 11° Ficam suspensos, temporariamente, os pagamentos relativos:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">I - ao Imposto sobre a Propriedade Predial e Territorial Urbana – IPTU;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">II - aos valores dos aluguéis de instalações e imóveis públicos do patrimônio do Município de Sumé, com exceção do prédio público localizado na Avenida 1º de Abril, 207, Térreo – Centro, Sumé (PB).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">III - os Preços Públicos referentes à utilização dos seguintes compartimentos da Central de Abastecimento “Oscar Severo de Macedo":</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">a) tarimba-padrão;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">b) box;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">c) uso permanente de setores;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">d) uso de setores somente nos dias de feira semanal;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 12° Caberá ao Comitê de Monitoramento de que trata o Art. 8°, desta Lei, a emissão de atos complementares necessários para seu fiel cumprimento.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 13° Esta Lei entra em vigor na data de sua publicação, retroagindo seus efeitos para convalidar os atos administrativos executados nos termos dos Decretos n° 1311, 1312, 1313, 1314 e 1315, todos de março de 2020, mantendo os efeitos dos referidos decretos, bem como da presente Lei, enquanto perdurar o estado de emergência em saúde pública, decorrente da pandemia causado pelo COVID-19.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 27 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.366-Dispoe-sobre-as-medidas-temporarias-para-enfrentamento-da-emergencia-decorrente-da-COVID-19.pdf', '2020-03-27', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.365/2020', 'lei-no-1-365-2020', 'projeto_lei', '1365/2020', 2020, 'Altera os artigos 2º e 3º, da Lei nº 1.364, de 11 de março de 2020, que autoriza o Município de Sumé a doar imóvel urbano, ao Estado da Paraíba, para construção de escola pública, nos termos do Art. 17, inciso I, alínea “b”, da Lei nº. 8.666/93.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica alterado o art. 2º da Lei nº 1.364/2020, que passa a vigorar com a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">“Art. 2º A área pública a ser doada é um terreno situado à João Rodrigues Sobrinho, Sumé (PB), medindo 10.030 m² (85 metros de frente e 118 metros de fundo), confrontações: frente: Rua João Rodrigues Sobrinho; lado direito: Rua Aleixo Bezerra; lado esquerdo: Rua José Cavalcanti de Queiroz e Fundos: Imóvel Pertencente a Prefeitura Municipal de Sumé, área menor a ser DESMEMBRADA da área maior de 50.320 m², situado no Alto da Caixa D''água, Sumé (PB), com os seguintes limites: ao Sul, com Manoel de Queiroz Freitas; ao Norte, com terras do D.N.O.C.S; ao Oeste, com terras do D.N.O.C.S; a Leste, com o Conjunto Habitacional Vereador Sebastião Vitorino da Silva; da propriedade do Município de Sumé, Escritura Pública com Registro no livro 47, fl. 73v, em 28/05/1997, registrada sob o nº 02,matrícula 2040, fls 134, Livro 2 – H, em 09/06/1997,junto ao Cartório Viton do Único Ofício Albanita Mendonça Raphael, Comarca de Sumé (PB), que conferiu título de propriedade do Imóvel Urbano ao Município de Sumé, imóvel este desapropriado pela Prefeitura Municipal de Sumé, por meio do Decreto nº. 510/97, de 26 de maio de 1997”;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Fica alterado o art. 3º da Lei nº 1.364/2020, que passa a vigorar com a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">“Art. 3º A doação estará condicionada à finalidade prevista no art. 1º desta Lei, devendo ficar estabelecido O prazo de 02 (dois) anos a contar da publicação desta Lei para execução da obra por parte do Governo do Estado, com a devida cláusula de reversão do patrimônio em favor do Município de SUMÉ, em caso de descumprimento da referida finalidade que justificou autorização da transferência de propriedade”;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Ficam revogadas as disposições em contrário;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º Esta lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 13 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.365-Altera-os-artigos-2-e-3.-da-Lei-n-1.364.-que-autoriza-o-Municipio-de-Sume-a-doar-imovel-urbano-ao-Estado-da-Paraiba-para-construcao-de-escola-publica.pdf', '2020-03-13', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.364/2020', 'lei-no-1-364-2020', 'projeto_lei', '1364/2020', 2020, 'Autoriza o Município de Sumé a doar imóvel urbano, terreno, ao Estado da Paraíba, nos termos do Art. 17, inciso I, alínea "b", da Lei nº. 8.666/93.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica o Município de Sumé autorizado a doar o terreno abaixo especificado ao Estado da Paraíba, com a finalidade específica para construção de Escola Estadual de Nível Fundamental e Médio, projeto próprio (Estado da Paraíba), com 12 (doze) salas de aulas, recursos decorrente de Emenda do Gabinete do Deputado Federal Wellington Roberto;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º A área pública a ser doada é um terreno situado à João Rodrigues Sobrinho, Sumé (PB), medindo 10.030 m² (85 metros de frente e 118 metros de fundo), área menor a ser DESMEMBRADA da área maior de 50.320 m², situado no Alto da Caixa D''água, Sumé (PB), com os seguintes limites: ao Sul, com Manoel de Queiroz Freitas; ao Norte, com terras do D.N.O.C.S; ao Oeste, com terras do D.N.O.C.S; a Leste, com o Conjunto Habitacional Vereador Sebastião Vitorino da Silva; da propriedade do Município de Sumé, Escritura Pública com Registro nº. 5.728, fl. 268, Protocolo nº. 2-A/1-A, de 09 de junho de 1997, Livro n° 46, Fls. 101, junto ao Cartório Viton do Único Ofício Albanita Mendonça Raphael, Comarca de Sumé (PB), que conferiu título de propriedade do Imóvel Urbano ao Município de Sumé, imóvel este desapropriado pela Prefeitura Municipal de Sumé, por meio do Decreto nº. 510/97, de 26 de maio de 1997;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º A doação estará condicionada à finalidade prevista no art. 1º desta Lei, devendo ficar estabelecido na doação o prazo de execução, com a devida cláusula de reversão do patrimônio em favor do Município de SUMÉ, em caso de descumprimento da referida finalidade que justificou autorização da transferência de propriedade;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º A referida doação dependerá de avaliação prévia, sendo dispensada licitação na modalidade de concorrência, por se tratar de doação para outro órgão ou entidade da administração pública, nos termos do Art. 17, inciso I, alínea “b”, da Lei nº. 8.666/93;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º A avaliação do imóvel deverá ser feita por agente público, com capacidade técnica comprovada, a qual procederá à perfeita identificação do bem e estabelecerá o valor do mesmo, com base em pesquisas de mercado;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º o setor de contabilidade deverá ser informado a respeito do preço estimado do imóvel, para que a doação seja devidamente contabilizada quanto às alterações nos registros contábeis e no balanço patrimonial.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7º Ficam revogadas todas as disposições em contrário;</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 8° Esta lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 11 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO CONSTITUCIONAL --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.364-Autoriza-o-Municipio-de-Sume-a-doar-imovel-urbano.-terreno.-ao-Estado-da-Paraiba.pdf', '2020-03-11', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.363/2020', 'lei-no-1-363-2020', 'projeto_lei', '1363/2020', 2020, 'Fixa nova data-limite para adesão ao Programa de Recuperação de Receitas do Município de Sumé – PRO-RECEITA - 2020', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - É fixada no dia 30 (trinta) de abril de 2020 – (Quinta-Feira), a data-limite para adesão ao PROGRAMA DE RECUPERAÇÃO DE RECEITAS DO MUNICÍPIO DE SUMÉ – PRO-RECEITA a que se refere a Lei nº 1.335, de 25 de novembro de 2019, com a alteração produzida pela Lei nº 1.354, de 20 de fevereiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data da sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé, em 11 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.363-Fixa-nova-data-limite-para-adesao-ao-PRO-RECEITA-2020.pdf', '2020-03-11', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.362/2020', 'lei-no-1-362-2020', 'projeto_lei', '1362/2020', 2020, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 97.000,00 (noventa e sete mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.06- SECRETARIA DE CULTURA ESPORTE E TURISMO</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">13.392.2011.2059 – Manutenção das Atividades da Escola de Música</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1510 - Outras Transferências de Convênios ou Contratos de Repasse da União</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.90.52.00.00.00.00 - Equipamentos e Material Permanente 97.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, as anulações das seguintes dotações:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.06- SECRETARIA DE CULTURA ESPORTE E TURISMO</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">13.392.2011.2058 – Realização de Eventos e Festividades populares no município</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1510 - Outras Transferências de Convênios ou Contratos de Repasse da União</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.39.00.00.00.00 - Outros Serviços de Terceiros - Pessoa Jurídica 97.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3° - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé (PB), em 11 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.362-Abertura-de-creditos-adicionais-especiais-para-o-fim-que-especifica.pdf', '2020-03-11', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.361/2020', 'lei-no-1-361-2020', 'projeto_lei', '1361/2020', 2020, 'Cria cargo de provimento efetivo no Plano de Cargos e Sistema de Carreiras Servidores Públicos do Poder Executivo do Município de Sumé.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica criado, no Quadro Permanente de Pessoal do Poder Executivo, consolidado pelas Leis nºs 1.109, de 26 de setembro de 2013, e 1.136, de 13 de junho de 2014, 1 (um) cargo de provimento efetivo Técnico em Saúde Bucal, símbolo SSA-ANI-602.4, dentro do Agrupamento Funcional Atividades de Nível Intermediário, ANI-602, do GRUPO OCUPACIONAL SERVIÇOS DE SAÚDE, código SSA-600.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 10 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.361-Cria-cargo-de-provimento-efetivo-no-Plano-de-Cargos-e-Sistema-de-Carreiras-Servidores-Publicos-do-Poder-Executivo-do-Municipio-de-Sume-Tecnico-em-Saude-Bucal.pdf', '2020-03-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.360/2020', 'lei-no-1-360-2020', 'projeto_lei', '1360/2020', 2020, 'Altera os valores dos padrões de vencimento de Categorias Funcionais que integram o Grupo Ocupacional Magistério Público Municipal – MAG-400.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Os padrões de vencimento único do cargo de provimento efetivo de Professor do Ensino Fundamental I, símbolo MAG-401.1 do Grupo Ocupacional MAGISTÉRIO PÚBLICO MUNICIPAL, código MAG-400, passam a ser os constantes do ANEXO ÚNICO, Tabela 1, a esta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2° - O nível de vencimento único do cargo de Professor, símbolo QSMP-1, do Quadro Suplementar do Magistério Público Municipal, é o estabelecido no ANEXO ÚNICO, Tabela 2, a esta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3° - Em razão do disposto nos artigos 1º e 2º, desta Lei, os proventos dos servidores inativos cujas aposentadorias gozam da garantia da paridade salarial prevista no art. 7º da Emenda Constitucional nº 41, de 19 de dezembro de 2003, e pela Emenda Constitucional nº 47, de 5 de julho de 2005 e bem assim às pensões pagas aos seus dependentes, são reajustados de acordo com os respectivos paradigmas em atividade.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Os proventos dos servidores inativos dos cargos de que tratam os artigos 1º e 2º, desta Lei, não amparados pelo princípio da paridade salarial e cujas aposentadorias são embasadas pelo art. 40, §§ 3º e 17 da Constituição Federal e proventos calculados na forma da Lei Federal nº 10.887, de 18 de junho de 2004, são reajustados em 12,83% (doze inteiros e oitenta e três centésimos) por cento, a partir, inclusive, do dia 1º de janeiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - As despesas com a execução desta Lei correrão à conta das dotações orçamentárias próprias consignadas no Orçamento do Município de Sumé para o corrente exercício financeiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6° - Esta Lei entra em vigor na data de sua publicação, produzindo efeitos jurídicos e financeiros desde o dia 1º de janeiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 10 de março de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><h2>ANEXO ÚNICO - ARTIGO 1º</h2><h3>GRUPO OCUPACIONAL: MAGISTÉRIO PÚBLICO MUNICIPAL - MAG-400</h3><h4>Categoria Profissional: Professores</h4><h4>Tabela 1 - Professor do Ensino Fundamental I - MAG-401</h4><table><thead><tr><th>CARGO</th><th colspan="7">PADRÕES DE VENCIMENTO - SÍMBOLO / R$</th></tr><tr><th></th><th>MAG-401.1.1.</th><th>MAG-401.1.2.</th><th>MAG-401.1.3.</th><th>MAG-401.1.4</th><th>MAG-401.1.5.</th><th>MAG-401.1.6.</th><th>MAG-401.1.7.</th></tr></thead><tbody><tr><td>Professor do Ensino Fundamental I</td><td>2.186,06</td><td>2.295,36</td><td>2.410,13</td><td>2.530,64</td><td>2.657,17</td><td>2.790,03</td><td>2.929,53</td></tr><tr><td></td><td>MAG-401.2.1</td><td>MAG-401.2.2</td><td>MAG-401.2.3</td><td>MAG-401.2.4</td><td>MAG-401.2.5</td><td>MAG-401.2.6</td><td>MAG-401.2.7</td></tr><tr><td></td><td>2.295,36</td><td>2.410,13</td><td>2.530,64</td><td>2.657,17</td><td>2.790,03</td><td>2.929,53</td><td>3.076,01</td></tr><tr><td></td><td>MAG-401.3.1</td><td>MAG-401.3.2</td><td>MAG-401.3.3</td><td>MAG-401.3.4</td><td>MAG-401.3.5</td><td>MAG-401.3.6</td><td>MAG-401.3.7</td></tr><tr><td></td><td>2.404,46</td><td>2.524,68</td><td>2.650,92</td><td>2.783,46</td><td>2.922,64</td><td>3.068,77</td><td>3.222,21</td></tr><tr><td></td><td>MAG-401.4.1</td><td>MAG-401.4.2</td><td>MAG-401.4.3</td><td>MAG-401.4.4</td><td>MAG-401.4.5</td><td>MAG-401.4.6</td><td>MAG-401.4.7</td></tr><tr><td></td><td>2.994,90</td><td>3.144,65</td><td>3.301,88</td><td>3.466,97</td><td>3.640,32</td><td>3.822,34</td><td>4.013,45</td></tr></tbody></table><h4>Tabela 2</h4><h3>QUADRO SUPLEMENTAR DO MAGISTÉRIO PÚBLICO MUNICIPAL - (em extinção)</h3><table><thead><tr><th>CARGO</th><th>VENCIMENTO BÁSICO, EM NÍVEL ÚNICO (R$)</th><th>SÍMBOLO</th></tr></thead><tbody><tr><td>PROFESSOR (*)</td><td>1.782,26</td><td>QSMP-1</td></tr></tbody></table><p>(*) Extinto, quando vagar.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.360-Altera-os-valores-dos-padroes-de-vencimento-de-Categorias-Funcionais-que-integram-o-Grupo-Ocupacional-Magisterio-Publico-Municipal-─-MAG-400.pdf', '2020-03-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.359/2020', 'lei-no-1-359-2020', 'projeto_lei', '1359/2020', 2020, 'Altera os valores dos padrões de vencimento de Categoria Funcional que integra o Grupo Ocupacional Magistério Público Municipal – MAG-400.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - Os padrões de vencimento único do cargo de provimento efetivo de Professor do Ensino Fundamental II, símbolo MAG-402. do Grupo Ocupacional MAGISTÉRIO PÚBLICO MUNICIPAL, código MAG-400, ficam elevados em 5% (cinco por cento) a partir, inclusive, do dia 1º de fevereiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - O nível de vencimento único do cargo de Professor, símbolo QSMP-2, do Quadro Suplementar do Magistério Público Municipal, fica elevado em 5% (cinco por cento) a partir, inclusive, do dia 1º de fevereiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Em razão do disposto nos artigos 1º e 2º, desta Lei, os proventos dos servidores inativos cujas aposentadorias gozam da garantia da paridade salarial prevista no art. 7º da Emenda Constitucional nº 41, de 19 de dezembro de 2003, e pela Emenda Constitucional nº 47, de 5 de julho de 2005 e bem assim às pensões pagas aos seus dependentes, são reajustados de acordo com os respectivos paradigmas em atividade.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Os proventos dos servidores inativos dos cargos de que tratam os artigos 1º e 2º, desta Lei, não amparados pelo princípio da paridade salarial e cujas aposentadorias são embasadas pelo art. 40, §§ 3º e 17 da Constituição Federal e proventos calculados na forma da Lei Federal nº 10.887, de 18 de junho de 2004, são reajustados em 5% (cinco por cento), a partir, inclusive, do dia 1º de fevereiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - As despesas com a execução desta Lei correrão à conta das dotações orçamentárias próprias consignadas no Orçamento do Município de Sumé para o corrente exercício financeiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6° - Esta Lei entra em vigor na data de sua publicação, produzindo efeitos jurídicos e financeiros desde o dia 1º de fevereiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 10 de março de 2020.</span></p><p style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.359-Altera-os-valores-dos-padroes-de-vencimento-de-Categoria-Funcional-que-integra-o-Grupo-Ocupacional-Magisterio-Publico-Municipal-─-MAG-400.pdf', '2020-03-10', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.358/2020', 'lei-no-1-358-2020', 'projeto_lei', '1358/2020', 2020, 'INSTITUI E DISCIPLINA Ο SISTEMA DE TRANSPORTE INDIVIDUAL DE PASSAGEIROS POR MOTOCICLETAS, MOTOTÁXI, NO ÂMBITO DO MUNICÍPIO DE SUMÉ/PB.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Esta Lei institui e disciplina, por normas gerais, o Sistema de transporte individual de passageiros por motocicletas, mototáxi, no Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Compete ao Município de Sumé, através da Prefeitura Municipal, mediante convênio com os órgãos de trânsito, licenciar, gerenciar, fiscalizar, operacionalizar e regulamentar, supletivamente, o sistema de transporte individual de passageiros em veículo automotor tipo motocicleta, respeitadas às Legislações Federal, Estadual e Municipal, em matéria de trânsito e transporte, dentro da competência que lhe foi deferida pela Lei nº 9.503, de 23 de setembro de 1997 – Código de Trânsito Brasileiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Incumbe à Prefeitura Municipal de Sumé, a prestação do serviço de transporte individual de passageiros por veículo automotor tipo motocicleta, diretamente ou mediante delegação a pessoas físicas, sob o regime de permissão.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4° - O serviço de transporte individual de passageiros por motocicletas, mototáxi, será prestado por motociclistas autônomos, mediante cadastro individual junto a Prefeitura Municipal de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5° - Considera-se serviço de mototáxi aquele executado através de motocicleta, por motoristas profissionais autônomos, mediante permissão, delegada pela Prefeitura Municipal de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6° - Para efeito desta Lei, considera-se condutor o motorista devidamente cadastrado no registro de mototaxista da Prefeitura Municipal de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§1° - Cada condutor terá direito a uma única permissão, sendo vedado a sua outorga a ocupantes de cargos públicos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - O condutor que trabalha no serviço de transporte individual de passageiros por veículo automotor tipo motocicleta, mototáxi, será obrigatoriamente treinado para este fim.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º - O condutor deverá estar munido de 2 (dois) capacetes com viseira, luvas e outros equipamentos exigidos pelo Regulamento desta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 4º - O permissionário só poderá conduzir individualmente um passageiro na moto, vedada a condução de crianças de até 07 (sete) anos de idade.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 7° - O veículo tipo motocicleta será licenciado pelo Departamento Nacional de Trânsito – DETRAN, para este fim, como motocicleta de aluguel e terá placa vermelha.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º A motocicleta terá potência mínima de 125cc (cento e vinte e cinco cilindradas).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º As especificações do veículo quanto a padronização, aos equipamentos obrigatórios, bem como a documentação do cadastro serão estabelecidas no Regulamento desta Lei ou em normas expedidas pela Prefeitura Municipal de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º O veículo usado na prestação do serviço de mototáxi terá, no momento da realização do processo da permissão, vida útil de no Máximo, 12 (doze) anos, contados a partir do ano de fabricação constante no documento de utilização de transporte – DUT.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§4° - Veículos com vida útil superior ao prazo fixado no parágrafo anterior poderão ser utilizados na prestação do serviço de mototáxi, desde que sejam submetidos anualmente a inspeção de segurança veicular junto ao órgão de transito competente.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 8° - O serviço regular de mototáxi, executado de forma continua e permanente, será prestado em locais previamente estabelecidos pela Prefeitura Municipal de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 9° - O número de mototáxi será fixado na proporção de 10 (dez) para 800 (oitocentos) habitantes.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – Para efeito da contagem proporcional a que se refere este artigo serão tomados por base os índices de aumento populacional estimados pelo Instituto Brasileiro de Geografia e Estatística (IBGE).</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 10 - É vedada a transferência, a qualquer título, da permissão delegada para a prestação do serviço de mototáxi, no Município de Sumé/PB.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º - O permissionário que, expirado ou não o prazo da delegação, não desejar continuar prestando esse serviço, deverá devolvê-la imediatamente a Prefeitura de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - Caso não proceda à baixa no termo e havendo cessão, a qualquer título, o adquirente não poderá operar o serviço, sendo a transferência da permissão nula de pleno direito, sujeitando-se o cedente as penalidades cabíveis.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 11 - O prestador do serviço de mototáxi deverá exercê-lo nos pontos de estacionamento pré-fixados pela Prefeitura de Sumé, mediante Decreto Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º- O condutor da motocicleta poderá apanhar o usuário fora dos pontos de estacionamento, quando solicitado pelo passageiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º - A prefeitura de Sumé definirá o número de vagas e condutores para execução do serviço nos pontos de estacionamento.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º - Considera-se ponto de estacionamento local fixado pela Prefeitura de Sumé, onde o mototaxista ficará estacionado e exercerá sua atividade.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 12 - O prazo da permissão para a prestação do serviço de mototáxi, fixada na conveniência do interesse público, seguirá as diretrizes estabelecida em regulamento pelo Município.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 13 - O permissionário cumprirá as Legislações Federal, Estadual e Municipal, e, em especial, as normas do Código de Trânsito Brasileiro, as Resoluções do CONTRAN, e demais determinações normativas expedidas pela Prefeitura de Sumé, sujeitando-se, em caso de infração, as penalidades aplicáveis.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 14 - O permissionário é, integral e exclusivamente, responsável por qualquer dano, eventualmente, causado ao usuário, a terceiros ou ao Município permitente, exigindo-se, para o adimplemento desta obrigação, seguro a ser estabelecido no Regulamento desta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 15 - A prefeitura cobrará, anualmente, taxa de alvará no valor definido no Código Tributário Municipal.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 16 - Esta Lei entra em vigor após decorridos 180 (cento e oitenta) dias de sua publicação oficial.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 21 de fevereiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.358-Disciplina.-por-normas-gerais.-o-sistema-de-transporte-individual-de-passageiros-por-motocicletas.-mototaxi.-no-Municipio-de-Sume.pdf', '2020-02-21', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.357/2020', 'lei-no-1-357-2020', 'projeto_lei', '1357/2020', 2020, 'Dispõe sobre a denominação de Logradouro Público do Município de Sumé', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º Fica denominada de José Barbosa Lela "Zé Lêla” o Logradouro Público Passagem Molhada, situada entre o Sítio Chorão e Sítio Riacho de Cima.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 20 de fevereiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.357-Denominada-de-Jose-Barbosa-Lela-Ze-Lela.-o-Logradouro-Publico-Passagem-Molhada.-situada-entre-o-Sitio-Chorao-e-Sitio-Riacho-de-Cima.pdf', '2020-02-20', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
