INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.354/2020', 'lei-no-1-354-2020', 'projeto_lei', '1354/2020', 2020, 'Dá nova redação a dispositivos da Lei 1.335, de 25 de novembro de 2019 – Programa de Recuperação de Receitas do Município de Sumé – PRO-RECEITA - 2020.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - A cabeça do art. 1º da Lei nº 1.335, de 2019, passa a vigorar com a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">“Fica instituído o PROGRAMA DE RECUPERAÇÃO DE RECEITAS DO MUNICÍPIO DE SUMÉ – PRO-RECEITA – 2020, destinado a promover a cobrança/regularização dos créditos da Fazenda Pública Municipal decorrentes de débitos fiscais de pessoas físicas e jurídicas, inscritos ou não na Dívida Ativa do Município, relativos ao Imposto sobre a Propriedade Predial e Territorial Urbana –IPTU; ao Imposto sobre Serviços de Qualquer Natureza – ISSQN e bem assim aos valores em atraso dos alugueis de imóveis do patrimônio do Município sujeitos a contratos de locação.”</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - A cabeça do art. 2º da Lei nº 1.335, de 2019, passa a vigorar com a seguinte redação:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">"Os créditos fiscais da Fazenda Pública Municipal constituídos até o dia 31 de dezembro de 2019 e relativos ao Imposto sobre a Propriedade Predial e Territorial Urbana e ao Imposto sobre Serviços de Qualquer Natureza – ISSQN e bem assim aos valores dos alugueis de imóveis do patrimônio do Município sujeitos a contratos de locação, inscritos ou não na Dívida Ativa do Município, que se encontrem em fase de cobrança administrativa ou judicial, com exigibilidade suspensa ou não, poderão ser renegociados nos termos desta Lei."</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 20 de fevereiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.354-Da-nova-redacao-a-dispositivos-da-Lei-1.335.-de-25-de-novembro-de-2019-–-PRO-RECEITA-2020.pdf', '2020-02-20', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.353/2020', 'lei-no-1-353-2020', 'projeto_lei', '1353/2020', 2020, 'Altera o número de cargos de Auxiliar de Desenvolvimento Cognitivo Infantil – ADI, a que se refere à Lei nº 1.302, de 10 de maio de 2019.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Ο Agrupamento II – Atividades Auxiliares de Magistério – ANEXO II – Segmento Único, da Lei nº 1.302, de 2019, fica acrescido de 10 (dez) cargos de Auxiliar de Desenvolvimento Cognitivo Infantil – ADI.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Em consequência do disposto no Art. 1º, desta Lei, o Agrupamento II – Atividades Auxiliares de Magistério – ANEXO II – Segmento Único, da Lei nº 1.302, de 2019, passa a vigorar com de acordo com o texto constante do ANEXO ÚNICO a esta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entra em vigor na data da sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 20 de fevereiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><h2>ANEXO ÚNICO - ARTIGO 1º</h2><h3>GRUPO OCUPACIONAL: MAGISTÉRIO PÚBLICO MUNICIPAL - MAG-400</h3><h4>AGRUPAMENTO II - ATIVIDADES AUXILIARES DE MAGISTÉRIO – MAG-AAM-402</h4><table><thead><tr><th>Cargo</th><th>CÓDIGO/SÍMBOLO/CLASSE</th><th>Escolaridade e demais requisitos exigidos para ingresso</th><th>Número de cargos</th><th>Padrões de Vencimento (R$)</th><th>LINHAS NATURAIS DE PROGRESSÃO VERTICAL</th></tr></thead><tbody><tr><td rowspan="5">Auxiliar de Desenvolvimento Cognitivo Infantil - ADI</td><td>MAG-AAM-402.1.1</td><td rowspan="5">Curso de Nível Médio Completo</td><td>15</td><td>1.063,98</td><td>Progressão à Classe MAG-AAM-402.1.2</td></tr><tr><td>MAG-AAM-402.1.2</td><td>6</td><td>1.117,18</td><td>Progressão à Classe MAG-AAM-402.1.3</td></tr><tr><td>MAG-AAM-402.1.3</td><td>5</td><td>1.173,04</td><td>Progressão à Classe MAG-AAM-402.1.4</td></tr><tr><td>MAG-AAM-402.1.4</td><td>3</td><td>1.231,69</td><td>Progressão à Classe MAG-AAM-402.1.5</td></tr><tr><td>MAG-AAM-402.1.5</td><td>1</td><td>1.293,77</td><td></td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.353-Altera-o-numero-de-cargos-de-Auxiliar-de-Desenvolvimento-Cognitivo-Infantil.pdf', '2020-02-20', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.352/2020', 'lei-no-1-352-2020', 'projeto_lei', '1352/2020', 2020, 'Altera os valores dos padrões de vencimento dos cargos de provimento efetivo de Agente Comunitário de Saúde e de Agente de Combate às Endemias do Grupo Ocupacional Serviços de Saúde – código SSA-600.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Os padrões de vencimento dos cargos de provimento efetivo de Agente Comunitário de Saúde e de Agente de Combate às Endemias, integrantes do Agrupamento Funcional Atividades de Apoio em Saúde – SSA-APS-603, do Grupo Ocupacional SERVIÇOS DE SAÚDE, código SSA-600 passam a ter, a partir do dia 1º de janeiro de 2020, os valores constantes do ANEXO ÚNICO a esta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 1º A percepção dos valores dos padrões de vencimento fixados na cabeça deste artigo exige o cumprimento de uma jornada de trabalho de 40 (quarenta) horas semanais.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 2º A jornada de trabalho de 40 (quarenta) horas exigida no § 1º deste artigo deverá ser integralmente dedicada a ações e serviços de promoção da saúde, vigilância epidemiológica e combate a endemias em prol das famílias e comunidades assistidas, dentro dos respectivos territórios de atuação, segundo as atribuições típicas estabelecidas na Lei Federal nº 11.350, de 5 de outubro de 2006; na Lei Municipal n° 1.166, de 6 de agosto de 2015, nesta e em outras leis.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">§ 3º É assegurada aos Agentes Comunitários de Saúde e aos Agentes de Combate às Endemias a participação nas atividades de planejamento e avaliação de ações, de detalhamento das tarefas, de registro de dados e de reuniões de equipe.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º As despesas decorrentes da aplicação desta Lei correrão à conta das dotações específicas constantes do Orçamento do Município de Sumé para o exercício financeiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º Esta Lei entra em vigor na data da sua publicação, produzindo efeitos jurídicos e financeiros desde o dia 1º de janeiro de 2020.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 05 de fevereiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><h2>ANEXO ÚNICO (art. 1º)</h2><h3>GRUPO OCUPACIONAL: SERVIÇOS DE SAÚDE - Código SSA-600</h3><h4>Agrupamento Funcional Atividades de Apoio em Saúde - SSA-APS-603</h4><table><thead><tr><th>CARGO</th><th>SIMBOLOGIA</th><th>PADRÕES DE VENCIMENTO (R$)</th></tr></thead><tbody><tr><td rowspan="5">AGENTE COMUNITÁRIO DE SAÚDE</td><td>SSA-APS-603.1.1</td><td>1.400,00</td></tr><tr><td>SSA-APS-603.1.2</td><td>1.470,00</td></tr><tr><td>SSA-APS-603.1.3</td><td>1.543,50</td></tr><tr><td>SSA-APS-603.1.4</td><td>1.620,67</td></tr><tr><td>SSA-APS-603.1.5</td><td>1.701,70</td></tr><tr><td rowspan="5">AGENTE DE COMBATE ÀS ENDEMIAS</td><td>SSA-APS-603.2.1</td><td>1.400,00</td></tr><tr><td>SSA-APS-603.2.2</td><td>1.470,00</td></tr><tr><td>SSA-APS-603.2.3</td><td>1.543,50</td></tr><tr><td>SSA-APS-603.2.4</td><td>1.620,67</td></tr><tr><td>SSA-APS-603.2.5</td><td>1.701,70</td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.352-Altera-os-valores-dos-padroes-de-vencimento-dos-Agentes-Comunitarios-de-Saude-e-dos-Agentes-de-Combate-as-Endemias-do-Municipio.pdf', '2020-02-05', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.351/2020', 'lei-no-1-351-2020', 'projeto_lei', '1351/2020', 2020, 'Abertura de créditos adicionais especiais para o fim que especifica', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1º - Fica o Poder Executivo Municipal autorizado a abrir crédito especial no valor de R$ 20.000,00 (vinte mil reais), destinado a preservar a manutenção do equilíbrio das contas do erário, bem como objetivando o saneamento do planejamento orçamentário e a melhor execução do cumprimento das metas previstas na Lei Orçamentária Anual do Município de Sumé.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Parágrafo único – As despesas decorrentes da aplicação desta Lei correrão por conta das seguintes rubricas orçamentárias:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.10 - SECRETARIA DE SERVIÇOS RURAIS E MEIO AMBIENTE</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">18.544.2006.1017 – Construção de Cisternas</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1001 - Recursos Ordinários</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">4.4.71.70 - Rateio pela participação em Consórcio Público..................20.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - Constituem fontes de recursos para atender a execução do crédito especial mencionado no art. 1º, a fim de se respeitar às disposições legais previstas na Lei 4320/64, as anulações das seguintes dotações:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">02.10 - SECRETARIA DE SERVIÇOS RURAIS E MEIΟ AMBIENTE</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">20.122.1003.2006 – Manutenção das Ativ. Da Sec. Des. Agrop e do Meio Ambiente</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Recurso: 1001 - Recursos Ordinários</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">3.3.90.39.00.00.00.00 - Outros Serviços de Terceiros - Pessoa Jurídica.................20.000,00</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Esta Lei entrará em vigor na data de sua publicação, revogadas as disposições em contrário.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Gabinete do Prefeito de Sumé-PB, em 05 de fevereiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">ÉDEN DUARTE PINTO DE SOUSA</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- PREFEITO --</span></strong></p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.351-Abertura-de-creditos-adicionais-especiais-para-o-fim-que-especifica-e-adota-outras-providencias.pdf', '2020-02-05', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.350/2020', 'lei-no-1-350-2020', 'projeto_lei', '1350/2020', 2020, 'Cria cargos de provimento efetivo no Plano de Cargos e Sistema de Carreiras dos Servidores Públicos do Poder Executivo do Município de Sumé.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - Ficam criados, no GRUPO OCUPACIONAL SERVIÇOS DE SAÚDE – SSA-600, do Quadro Permanente de Pessoal do Poder Executivo, consolidado pelas Leis n° 1.109, de 26 de setembro de 2013; n° 1.136, de 13 de junho de 2014; 1.149, de 2015; 1.217, de 2017; 1.248, de 2017, e 1.223, de 5 de julho de 2017, e, especialmente, o art. 1º da Lei nº 1.302, de 10 de maio de 2019, os cargos de provimento efetivo constantes do ANEXO I, a esta Lei, incluindo a nomenclatura e a remuneração dos cargos respectivos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - As atribuições típicas dos cargos criados no art. 1º, desta Lei, são as constantes do ANEXO II, a esta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Os titulares dos cargos de provimento efetivo de que trata o art. 1º, desta Lei, têm lotação fixada exclusivamente na Secretaria da Saúde.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4° - Os titulares dos cargos de que trata esta Lei não podem ter exercício em outros órgãos da Estrutura Administrativa da Prefeitura do Município de Sumé nem fora das Unidades de Ensino que integram a Estrutura Organizacional da Secretaria da Saúde.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - As despesas com a execução desta Lei correrão à conta das dotações orçamentárias de pessoal alocadas ao Orçamento do Município de Sumé para o corrente exercício financeiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 21 de janeiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><h2>ANEXO II (art. 1º)</h2><h3>GRUPO OCUPACIONAL: SERVIÇOS DE SAÚDE</h3><table><thead><tr><th>CARGO</th><th>SÍMBOLO</th></tr><tr><th colspan="2">TÉCNICO EM ENFERMAGEM</th><th>SSA-ANI-602.1.1</th></tr></thead><tbody><tr><td colspan="2">ATRIBUIÇÕES TÍPICAS</td></tr><tr><td colspan="2"><b>A - De Ordem Geral</b></td></tr><tr><td colspan="2">Orientar e executar o trabalho técnico de enfermagem, participando da elaboração do plano de assistência de enfermagem, em conformidade com as normas e procedimentos de biossegurança.</td></tr><tr><td colspan="2"><b>B - De Ordem Específica</b></td></tr><tr><td colspan="2">1. Executar ações assistenciais de enfermagem, sob supervisão, observando e registrando sinais e sintomas apresentados pelo doente, fazendo curativos, ministrando medicamentos e outros.</td></tr><tr><td colspan="2">2. Executar controles relacionados à patologia de cada paciente.</td></tr><tr><td colspan="2">3. Coletar material para exames laboratoriais.</td></tr><tr><td colspan="2">4. Auxiliar no controle de estoque de materiais, equipamentos e medicamentos.</td></tr><tr><td colspan="2">5. Operar aparelhos de eletrodiagnóstico.</td></tr><tr><td colspan="2">6. Cooperar com a equipe de saúde no desenvolvimento das tarefas assistenciais, de ensino, pesquisa e de educação sanitária.</td></tr><tr><td colspan="2">7. Fazer preparo pré e pós operatório e pré e pós parto.</td></tr><tr><td colspan="2">8. Auxiliar nos atendimentos de urgência e emergência.</td></tr><tr><td colspan="2">9. Circular salas cirúrgicas e obstétricas, preparando a sala e o instrumental cirúrgico, e instrumentalizando nas cirurgias, quando necessário.</td></tr><tr><td colspan="2">10. Realizar procedimentos referentes à admissão, alta, transferência e óbitos.</td></tr><tr><td colspan="2">11. Manter a unidade de trabalho organizada, zelando pela sua conservação comunicando ao Enfermeiro eventuais problemas.</td></tr><tr><td colspan="2">12. Auxiliar em serviços de rotina da Enfermagem.</td></tr><tr><td colspan="2">13. Colaborar no desenvolvimento de programas educativos, atuando no ensino de pessoal auxiliar de atividades de enfermagem e na educação de grupos da comunidade.</td></tr><tr><td colspan="2">14. Verificar e controlar equipamentos e instalações da unidade, comunicando ao responsável.</td></tr><tr><td colspan="2">15. Auxiliar o Enfermeiro na prevenção e controle das doenças transmissíveis em geral, em programas de vigilância epidemiológica e no controle sistemático da infecção hospitalar.</td></tr><tr><td colspan="2">16. Auxiliar o Enfermeiro na prevenção e controle sistemático de danos físicos que possam ser causados a pacientes durante a assistência de saúde.</td></tr><tr><td colspan="2">17. Desempenhar tarefas relacionadas a intervenções cirúrgicas médico-odontológicas, passando-o ao cirurgião e realizando outros trabalhos de apoio.</td></tr><tr><td colspan="2">18. Conferir qualitativa e quantitativamente os instrumentos cirúrgicos, após o término das cirurgias.</td></tr><tr><td colspan="2">19. Orientar a lavagem, secagem e esterilização do material cirúrgico.</td></tr><tr><td colspan="2">20. Zelar, permanentemente, pelo estado funcional dos aparelhos que compõem as salas de cirurgia, propondoa aquisição de novos aparelhos, para reposição daqueles que estão sem condições de uso.</td></tr><tr><td colspan="2">21. Preparar pacientes para exames, orientando-os sobre as condições de realização dos mesmos.</td></tr><tr><td colspan="2">22. Registrar os eletrocardiogramas efetuados, fazendo as anotações pertinentes a fim de liberá-los para os requisitantes e possibilitar a elaboração de boletins estatísticos.</td></tr><tr><td colspan="2">23. Auxiliar nas atividades de radiologia, quando necessário.</td></tr><tr><td colspan="2">24. Executar tarefas pertinentes à área de atuação, utilizando-se de equipamentos e programas de informática.</td></tr><tr><td colspan="2">25. Executar outras tarefas para o desenvolvimento das atividades do setor, inerentes à sua função.</td></tr></tbody></table>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.350-Cria-cargos-de-provimento-efetivo-no-Plano-de-Cargos-e-Sistema-de-Carreiras-dos-Servidores-Publicos-do-Poder-Executivo-do-Municipio-de-Sume.pdf', '2020-01-21', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('LEI Nº 1.349/2020', 'lei-no-1-349-2020', 'projeto_lei', '1349/2020', 2020, 'Cria cargos de provimento efetivo no Plano de Cargos e Sistema de Carreiras dos Servidores Públicos do Poder Executivo do Município de Sumé.', '<p class="ng-star-inserted"><span class="ng-star-inserted">O Prefeito do Município de Sumé</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Faço saber que a Câmara Municipal decreta e eu sanciono a seguinte Lei:</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 1° - Ficam criados, no GRUPO OCUPACIONAL MAGISTÉRIO PÚBLICO MUNICIPAL – MAG-400, do Quadro Permanente de Pessoal do Poder Executivo, consolidado pelas Leis nº 1.109, de 26 de setembro de 2013; n° 1.136, de 13 de junho de 2014; 1.149, de 2015; 1.217, de 2017; 1.248, de 2017, e 1.223, de 5 de julho de 2017, e, especialmente, o art. 1º da Lei nº 1.302, de 10 de maio de 2019, os cargos de provimento efetivo constantes do ANEXO I, a esta Lei, incluindo a nomenclatura e a remuneração dos cargos respectivos.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 2º - As atribuições típicas dos cargos criados no art. 1º, desta Lei, são as constantes do ANEXO II, a esta Lei.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 3º - Os titulares dos cargos de provimento efetivo de que trata o art. 1º, desta Lei, têm lotação fixada exclusivamente na Secretaria da Educação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 4º - Os titulares dos cargos de que trata esta Lei não podem ter exercício em outros órgãos da Estrutura Administrativa da Prefeitura do Município de Sumé nem fora das Unidades de Ensino que integram a Rede Oficial de Ensino da Estrutura Organizacional da Secretaria da Educação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 5º - As despesas com a execução desta Lei correrão à conta das dotações orçamentárias de pessoal alocadas ao Orçamento do Município de Sumé para o corrente exercício financeiro.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">Art. 6º - Esta Lei entra em vigor na data de sua publicação.</span></p><p class="ng-star-inserted"><span class="ng-star-inserted">GABINETE DO PREFEITO DE SUMÉ (PB), em 21 de janeiro de 2020.</span></p><p class="ng-star-inserted" style="text-align: center"><strong><span class="ng-star-inserted">Éden Duarte Pinto de Sousa</span></strong><br class="ng-star-inserted" /><strong><span class="ng-star-inserted">-- Prefeito do Município --</span></strong></p><h2>LEI Nº 1.349/2020</h2><h3>PREFEITURA DO MUNICÍPIO DE SUMÉ</h3><h3>PLANO DE CARGOS E SISTEMA DE CARREIRAS DOS SERVIDORES</h3><h3>PÚBLICOS DO MUNICÍPIO DE SUMÉ</h3><h3>QUADRO PERMANENTE DE PESSOAL</h3><h3>Cargos de Provimento Efetivo</h3><h3>Lotação: Secretaria da Educação</h3><h3>GRUPO OCUPACIONAL: MAGISTÉRIO PÚBLICO MUNICIPAL</h3><h3>CÓDIGO: MAG-400</h3><h3>ANEXO II (art. 1º)</h3><h3>Quadro I - CATEGORIA PROFISSIONAL: PROFESSORES – MAG-402</h3><h3>Subcategoria: Professores do Ensino Fundamental</h3><h3>Tabela 2 - Professor do Ensino Fundamental I - MAG-402</h3><h3>Atribuições Típicas - ANEXO I – Parágrafo Único</h3><h3>GRUPO OCUPACIONAL: MAGISTÉRIO PÚBLICO MUNICIPAL – MAG-400</h3><h3>Categoria Funcional: Professor – MAG-402</h3><h3>Subcategoria: Professor do Ensino Fundamental II –</h3><h3>Habilitação: Música – Símbolo MAG-402.12.1.11</h3><table><thead><tr><th>CARGO</th><th>SÍMBOLO</th></tr></thead><tbody><tr><td>Habilitação: Música – símbolo MAG-402.12.1.1</td><td>MAG-402.12.11.1</td></tr></tbody></table><h2>Atribuições Típicas - ANEXO II</h2><h3>GRUPO OCUPACIONAL: SERVIÇOS DE SAÚDE</h3><table><tbody><tr><td colspan="2"><b>A - De Ordem Geral</b></td></tr><tr><td colspan="2">1. Ministrar aulas no ciclo do 6º ao 9º ano do Ensino Fundamental, no campo da Música;</td></tr><tr><td colspan="2">2. promover a educação dos adolescentes, no âmbito de sua especialidade, aplicando metodologia adequada e atualizada, levando-os à integração e socialização;</td></tr><tr><td colspan="2">3. elaborar o planejamento e executar as atividades relacionadas com a sua especialidade;</td></tr><tr><td colspan="2">4. desempenhar, no âmbito da sua especialidade, as atribuições descritas para os professores da Educação Fundamental, bem como promover programas e métodos relacionados à educação, com o objetivo de definir os recursos metodológicos;</td></tr><tr><td colspan="2">5. elaborar o planejamento de curso e plano de ensino de acordo com as potencialidades;</td></tr><tr><td colspan="2">6. aplicar instrumentos de análise para avaliar o desempenho dos discentes;</td></tr><tr><td colspan="2">7. prestar integralmente a carga horária normal e a de horas-atividade do trabalho pedagógico;</td></tr><tr><td colspan="2">8. participar de toda atividade na unidade escolar;</td></tr><tr><td colspan="2">9. participar de estudo e do que afeta o processo educacional;</td></tr><tr><td colspan="2">10. discutir programas e métodos a serem utilizados ou reformulados, comentando situações e problemas de classe sob sua responsabilidade e emitindo opiniões, a fim de contribuir para a definição adequada dos objetivos, recursos e metodologias;</td></tr><tr><td colspan="2">11. elaborar plano pedagógico de ação imprimindo-lhe caráter flexível, de acordo com as carências e potencialidades de cada aluno, para obter melhores respostas nos ensinamentos ministrados;</td></tr><tr><td colspan="2">12. selecionar e confeccionar o material didático a ser utilizado valendo-se de sua capacidade ou sob orientação pedagógica para facilitar o ensino-aprendizagem;</td></tr><tr><td colspan="2">13. ministrar aulas no Ensino Fundamental, transmitindo por intermédio da adaptação de métodos regulares de ensino, conhecimentos assistematizados de comunicação escrita ou oral, do meio geográfico, social, de habilidades fundamentais à sua integração na sociedade e elaborar o planejamento, imprimindo-lhes caráter flexível, de acordo com as carências e potencialidades de cada aluno;</td></tr><tr><td colspan="2">14. desenvolver o espírito comunitário, os princípios de civismo, do relacionamento social e da criatividade por intermédio de atividades similares e aproveitamento de situações reais, para crian ambientes propícios a uma ação educativa mais completa;</td></tr><tr><td colspan="2">15. promover atividades extraclasse envolvendo a seleção de conteúdos e técnicas e procedimentos de avaliação paralela dos alunos, evitando os eventuais fracassos escolares;</td></tr><tr><td colspan="2">16. participar de forma efetiva da elaboração do projeto pedagógico;</td></tr><tr><td colspan="2">17. elaborar relatórios de observação dos alunos;</td></tr><tr><td colspan="2">18. registrar os conteúdos trabalhados;</td></tr><tr><td colspan="2">19. executar outras tarefas de mesma natureza ou nível de complexidade, associadas à sua área de atuação e demais atribuições definidas no Estatuto do Magistério Municipal e na Lei do Plano de Cargos e Sistema de Carreiras do Magistério Público Municipal;</td></tr><tr><td colspan="2"><b>B - De Ordem Específica</b></td></tr><tr><td colspan="2">20. planejar e ministrar aulas direcionadas ao campo da Música;</td></tr><tr><td colspan="2">21. realizar e acompanhar projetos de pesquisa e leitura em relação ao tema do Tópico 20;</td></tr><tr><td colspan="2">22. orientar e acompanhar a auto-organização da vida de grupo dos alunos;</td></tr><tr><td colspan="2">23. orientar e executar atividades de integração no ambiente escolar entre a escola e a comunidade, e tarefas de manutenção;</td></tr><tr><td colspan="2">24. participar de todas as formações e cursos de aprimoramento profissional ofertados pela Secretaria da Educação;</td></tr><tr><td colspan="2">25. realizar acompanhamento do aluno durante o Tempo Escola e Tempo Comunidade, implementando os seus projetos profissionais;</td></tr><tr><td colspan="2">26. ter conhecimento, possuir afinidade e comprometimento com a missão institucional dos centros educativos/escolas e, de igual modo, com os princípios pedagógicos dirigidos ao ensino da Música;</td></tr><tr><td colspan="2">27. cumprir os horários de aula, o calendário escolar; proceder à avaliação integral da aprendizagem, corrigir provas, trabalhos e registrar os resultados nos Diários de Classe;</td></tr><tr><td colspan="2">28. acompanhar, orientar e avaliar as atividades complementares dos educandos.</td></tr></tbody></table><hr class="ng-star-inserted" />', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/03/Lei-n-1.349-Cria-cargos-de-provimento-efetivo-no-Plano-de-Cargos-e-Sistema-de-Carreiras-dos-Servidores-Publicos-do-Poder-Executivo-do-Municipio-de-Sume.pdf', '2025-03-11', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
