INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 01 - Voto de Pesar', 'requerimento-01-voto-de-pesar', 'requerimento', '292', 2025, 'Requeiro de Vossa Excelência que depois ouvido o Plenário na forma regimental, seja encaminhado Voto de Pesar a família da Senhora Luciana Leite (Nana Leite), não há dor maior do que ter de dizer adeus aos que amamos, pois quem partiu apenas seguiu caminho mais cedo, e um dia voltarão a encontrar-se na eternidade.', '<p>Requeiro de Vossa Excelência que depois ouvido o Plenário na forma regimental, seja encaminhado Voto de Pesar a família da Senhora Luciana Leite (Nana Leite), não há dor maior do que ter de dizer adeus aos que amamos, pois quem partiu apenas seguiu caminho mais cedo, e um dia voltarão a encontrar-se na eternidade.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/04/Requerimento-01-Voto-de-Pesar.pdf', '2025-04-02', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 02 - Voto de Pesar', 'requerimento-02-voto-de-pesar', 'requerimento', '293', 2025, 'Requeiro de Vossa Excelência que depois ouvido o Plenário na forma regimental, seja encaminhado nosso mais profundo voto de pesar à família do Senhor Francisco Rodrigues, carinhosamente conhecido como Buzinho da vassoura. Não há dor maior do que dizer adeus àqueles que amamos. Contudo, acreditamos que quem parte apenas segue o caminho mais cedo, deixando a promessa de um reencontro na eternidade. Que a família encontre conforto e força neste momento difícil.', '<p>Requeiro de Vossa Excelência que depois ouvido o Plenário na forma regimental, seja encaminhado nosso mais profundo voto de pesar à família do Senhor Francisco Rodrigues, carinhosamente conhecido como Buzinho da vassoura. Não há dor maior do que dizer adeus àqueles que amamos. Contudo, acreditamos que quem parte apenas segue o caminho mais cedo, deixando a promessa de um reencontro na eternidade. Que a família encontre conforto e força neste momento difícil.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/04/Requerimento-02-Voto-de-Pesar.pdf', '2025-04-02', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
