INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 09 - Voto de Pesar', 'requerimento-09-voto-de-pesar', 'requerimento', '295', 2025, 'Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a Família da Senhora Maria Ivete Fernandes. Precisamos rezar por aquele que amamos e que partiu, para que descanse em paz e encontre a luz para continuar crescendo espiritualmente. Mas é preciso rezar também por aqueles que ficam, para que encontrem conforto e consigam enviar pensamentos de paz para quem agora já não está entre nós.', '<p>Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a Família da Senhora Maria Ivete Fernandes. Precisamos rezar por aquele que amamos e que partiu, para que descanse em paz e encontre a luz para continuar crescendo espiritualmente. Mas é preciso rezar também por aqueles que ficam, para que encontrem conforto e consigam enviar pensamentos de paz para quem agora já não está entre nós.</p>', 'aprovado', NULL, NULL, '2025-04-02', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
