INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 03 - Voto de Pesar', 'requerimento-03-voto-de-pesar', 'requerimento', '305', 2025, 'Requeiro de Vossa Excelência que após ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a família da Senhora Rivania Torres de Macedo Coelho. Nada capaz de reparar uma perda como esta, mas em nome da amizade e amor de quem fica, e em honra da memória de quem se foi, é preciso continuar vivendo. É preciso transformar o luto em uma luta pela vida e pela felicidade, e transformar a dor em saudade e serenidade. Os meus mais sinceros pêsames!', '<p>Requeiro de Vossa Excelência que após ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a família da Senhora Rivania Torres de Macedo Coelho. Nada capaz de reparar uma perda como esta, mas em nome da amizade e amor de quem fica, e em honra da memória de quem se foi, é preciso continuar vivendo. É preciso transformar o luto em uma luta pela vida e pela felicidade, e transformar a dor em saudade e serenidade. Os meus mais sinceros pêsames!</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/10/Requerimento-03-Voto-de-Pesar.pdf', '2025-03-20', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
