INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 07 - voto de pesar', 'requerimento-07-voto-de-pesar', 'requerimento', '318', 2025, 'Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a Família da Senhora Valdirene Batista Silva. Pensem que quem partiu apenas seguiu viagem primeiro, e que um dia voltarão a encontrar-se. A morte leva fisicamente a pessoa, mas jamais pode roubar ou apagar a memória de quem foi em vida. E principalmente as lembranças e os sentimentos que deixou para trás.', '<p>Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a Família da Senhora Valdirene Batista Silva. Pensem que quem partiu apenas seguiu viagem primeiro, e que um dia voltarão a encontrar-se. A morte leva fisicamente a pessoa, mas jamais pode roubar ou apagar a memória de quem foi em vida. E principalmente as lembranças e os sentimentos que deixou para trás.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/10/Requerimento-07-voto-de-pesar.pdf', '2025-03-24', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
