INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 08_1 - Voto de Pesar', 'requerimento-08_1', 'requerimento', '321', 2025, 'Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a família de Ana Vitória Araújo Macêdo. Quando uma criança parte deste mundo o céu ganha mais um anjo e o firmamento mais uma estrela. Sinto muito a sua perda, e acreditem que a dor da sua família é sentida por todos seus amigos e conhecidos. Recebam meu apoio e o desejo que este momento de grande dor seja breve. O tempo levará o sofrimento e deixará saudade e serenidade. Tenham fé e coragem.', '<p>Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a família de Ana Vitória Araújo Macêdo. Quando uma criança parte deste mundo o céu ganha mais um anjo e o firmamento mais uma estrela. Sinto muito a sua perda, e acreditem que a dor da sua família é sentida por todos seus amigos e conhecidos. Recebam meu apoio e o desejo que este momento de grande dor seja breve. O tempo levará o sofrimento e deixará saudade e serenidade. Tenham fé e coragem.</p>', 'aprovado', NULL, NULL, '2025-03-17', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
