INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 09_1', 'requerimento-09_1-2', 'requerimento', '323', 2025, 'Requeiro de Vossa Excelência que após ouvido o Plenário na Forma Regimental, seja encaminhado ao Excelentíssimo Senhor Prefeito Municipal Manoel Lourenço Queiroz Duarte, o que a seguir requer: reitero a solicitação das informações requeridas nos Ofícios nº 002/2025, direcionado à Secretaria de Assistência Social, e nº 010/2025, enviado ao Diretor Geral do Hospital Maternidade Alice de Almeida. Ressalto que tais informações devem ser fornecidas em conformidade com a Lei de Acesso à Informação (Lei nº 12.527/2011), o Artigo 25 do Regimento Interno, bem como os Artigos 13 e 18 da Lei Orgânica do Município de Sumé. Ressalto que, até o presente momento, não recebi nenhuma resposta referente às solicitações. Segue em anexo os Ofícios.', '<p>Requeiro de Vossa Excelência que após ouvido o Plenário na Forma Regimental, seja encaminhado ao Excelentíssimo Senhor Prefeito Municipal Manoel Lourenço Queiroz Duarte, o que a seguir requer: reitero a solicitação das informações requeridas nos Ofícios nº 002/2025, direcionado à Secretaria de Assistência Social, e nº 010/2025, enviado ao Diretor Geral do Hospital Maternidade Alice de Almeida. Ressalto que tais informações devem ser fornecidas em conformidade com a Lei de Acesso à Informação (Lei nº 12.527/2011), o Artigo 25 do Regimento Interno, bem como os Artigos 13 e 18 da Lei Orgânica do Município de Sumé. Ressalto que, até o presente momento, não recebi nenhuma resposta referente às solicitações. Segue em anexo os Ofícios.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/10/Requerimento-09_1.pdf', '2025-04-02', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 10 - Voto de Pesar', 'requerimento-10-voto-de-pesar', 'requerimento', '324', 2025, 'Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a família do Senhor José Braz da Silva. Perder alguém que amamos é uma das dores mais intensas da vida. Que a presença do amor e do carinho daqueles que estão ao seu redor possa trazer algum conforto neste momento de luto. O tempo levará o sofrimento e deixará saudade e serenidade. Tenham fé e coragem.', '<p>Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Pesar a família do Senhor José Braz da Silva. Perder alguém que amamos é uma das dores mais intensas da vida. Que a presença do amor e do carinho daqueles que estão ao seu redor possa trazer algum conforto neste momento de luto. O tempo levará o sofrimento e deixará saudade e serenidade. Tenham fé e coragem.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/10/Requerimento-10-Voto-de-Pesar.pdf', '2025-03-25', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO legislative_activities (title, slug, type, number, year, summary, content, status, author, file_url, session_date, is_active, created_at, updated_at) VALUES ('Requerimento 11 - Voto de Aplauso', 'requerimento-11-voto-de-aplauso', 'requerimento', '325', 2025, 'Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Aplauso ao Excelentíssimo Senhor Prefeito Manoel Lourenço Queiroz Duarte e a toda equipe responsável pela realização da ExporSumé 2025. O evento, dedicado à caprinovinocultura, gastronomia e à rica cultura regional, evidencia a relevância econômica e cultural para o município e a região, destacando seu impacto positivo na valorização local e no fortalecimento da economia regional.', '<p>Requeiro de Vossa Excelência que depois de ouvido o Plenário na Forma Regimental, seja concedido Voto de Aplauso ao Excelentíssimo Senhor Prefeito Manoel Lourenço Queiroz Duarte e a toda equipe responsável pela realização da ExporSumé 2025. O evento, dedicado à caprinovinocultura, gastronomia e à rica cultura regional, evidencia a relevância econômica e cultural para o município e a região, destacando seu impacto positivo na valorização local e no fortalecimento da economia regional.</p>', 'aprovado', NULL, 'https://camaradesume.pb.gov.br/wp-content/uploads/2025/10/Requerimento-11-Voto-de-Aplauso.pdf', '2025-04-02', true, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
