INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO - DV00004/2026', '20260112085144', 'ESTADO DA PARAÍBA CÂMARA MUNICIPAL DE SUMÉ EXTRATO DE DECISÃO DE RECURSO ADMINISTRATIVO PROCESSO ADM. Nº: 251125DV00004 REFERÊNCIA: Processo Licitatório nº DV 00004/2026 RECORRENTE: Instar Tecnologia LTDA OBJETO: Contratação de pessoa jurídica de direito privado para locação de sítio institucional e', '<p style="text-align: center;"><strong>ESTADO DA PARA&Iacute;BA</strong></p> <p style="text-align: center;"><strong>C&Acirc;MARA MUNICIPAL DE SUM&Eacute;</strong></p> <p style="text-align: center;"><strong>EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO</strong></p> <p>&nbsp;</p> <p style="text-align: justify;"><strong>PROCESSO ADM. N&ordm;:</strong> 251125DV00004</p> <p style="text-align: justify;"><strong>REFER&Ecirc;NCIA:</strong> Processo Licitat&oacute;rio n&ordm; DV 00004/2026</p> <p style="text-align: justify;"><strong>RECORRENTE: </strong>Instar Tecnologia LTDA&nbsp;</p> <p style="text-align: justify;"><strong>OBJETO:</strong> Contrata&ccedil;&atilde;o de pessoa jur&iacute;dica de direito privado para loca&ccedil;&atilde;o de s&iacute;tio institucional e di&aacute;rio eletr&ocirc;nico para a C&acirc;mara Municipal de Sum&eacute; - PB.</p> <p style="text-align: justify;"><strong>DECIS&Atilde;O:</strong>&nbsp;O Agente de Contrata&ccedil;&otilde;es decide pelo CONHECIMENTO do recurso, ante a sua tempestividade, e, no m&eacute;rito, pelo N&Atilde;O PROVIMENTO, mantendo a decis&atilde;o de INABILITA&Ccedil;&Atilde;O da empresa recorrente por descumprimento aos requisitos de regularidade previstos no instrumento convocat&oacute;rio.</p> <p style="text-align: justify;"><strong>S&Iacute;NTESE DA FUNDAMENTA&Ccedil;&Atilde;O:</strong>&nbsp;Aus&ecirc;ncia de Documento Obrigat&oacute;rio: A recorrente deixou de apresentar a Certid&atilde;o de Contas Julgadas Irregulares do TCE de seu domic&iacute;lio, documento expressamente exigido e fundamentado no Aviso de Contrata&ccedil;&atilde;o Direta, conforme as diretrizes da Lei n&ordm; 14.133/2021 e orienta&ccedil;&otilde;es do TCU.</p> <p style="text-align: justify;"><strong>Inconsist&ecirc;ncia da Justificativa (Recesso Institucional):</strong> O argumento de que o &oacute;rg&atilde;o emissor encontrava-se em recesso &eacute; insuficiente para afastar o dever de dilig&ecirc;ncia do licitante. Profissionais que atuam no mercado de licita&ccedil;&otilde;es devem antecipar-se &agrave;s exig&ecirc;ncias documentais, sendo a instru&ccedil;&atilde;o correta da proposta um dever inafast&aacute;vel do proponente.</p> <p style="text-align: justify;"><strong>Inviabilidade de Dilig&ecirc;ncia Saneadora:</strong> A concess&atilde;o de prazo para juntada do documento seria infrut&iacute;fera e economicamente invi&aacute;vel, considerando a necessidade de contrata&ccedil;&atilde;o imediata sinalizada no Plano de Contrata&ccedil;&otilde;es Anual para 2026.</p> <p style="text-align: justify;"><strong>Veda&ccedil;&atilde;o &agrave; Inclus&atilde;o de Novo Documento:</strong> Conforme o Ac&oacute;rd&atilde;o 1.211/2021-TCU-Plen&aacute;rio, a dilig&ecirc;ncia n&atilde;o pode ser utilizada para a juntada de documentos que deveriam compor originariamente a habilita&ccedil;&atilde;o. No mesmo sentido, o Ac&oacute;rd&atilde;o 8436/2025 do TCE-CE refor&ccedil;a que n&atilde;o h&aacute; o que sanear quando o vazio documental &eacute; absoluto.</p> <p style="text-align: justify;"><strong>Julgamento Objetivo e Isonomia:</strong> A Administra&ccedil;&atilde;o n&atilde;o pode condecorar a des&iacute;dia da recorrente em preju&iacute;zo aos demais licitantes que apresentaram pre&ccedil;os exequ&iacute;veis e cumpriram integralmente as regras do certame. O Princ&iacute;pio da Vincula&ccedil;&atilde;o ao Instrumento Convocat&oacute;rio impede a flexibiliza&ccedil;&atilde;o de regras claras em favor de interesses privados.</p> <p style="text-align: justify;"><strong>CONCLUS&Atilde;O:</strong>&nbsp;Fica ratificada a inabilita&ccedil;&atilde;o, devendo o processo seguir para as etapas subsequentes de adjudica&ccedil;&atilde;o e homologa&ccedil;&atilde;o conforme o rito legal.</p> <p style="text-align: right;">Sum&eacute; - PB, 12 de janeiro de 2026.</p> <p style="text-align: justify;">&nbsp;</p> <p style="text-align: center;"><strong>VANKLIN MIKAEL CARNEIRO DE SOUZA SILVA </strong></p> <p style="text-align: center;"><em>Agente de Contrata&ccedil;&otilde;es&nbsp;</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20260112085144/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20260112085144/?link=CMSU</a> ', 'published', '2026-01-12 21:02:31', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO - DV00003/2026', '20260112084709', 'ESTADO DA PARAÍBA CÂMARA MUNICIPAL DE SUMÉ EXTRATO DE DECISÃO DE RECURSO ADMINISTRATIVO PROCESSO ADM. Nº: 251125DV00003 REFERÊNCIA: Processo Licitatório nº DV 00003/2026 RECORRENTE: 50.772.512 Henrique Lucas de Sousa OBJETO: Contratação de pessoa jurídica de direito privado para prestação de serviço', '<p style="text-align: center;"><strong>ESTADO DA PARA&Iacute;BA</strong></p> <p style="text-align: center;"><strong>C&Acirc;MARA MUNICIPAL DE SUM&Eacute;</strong></p> <p style="text-align: center;"><strong>EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO</strong></p> <p><br><strong>PROCESSO ADM. N&ordm;:</strong> 251125DV00003</p> <p><strong>REFER&Ecirc;NCIA:</strong> Processo Licitat&oacute;rio n&ordm; DV 00003/2026</p> <p><strong>RECORRENTE:</strong> 50.772.512 Henrique Lucas de Sousa</p> <p style="text-align: justify;"><strong>OBJETO:</strong> Contrata&ccedil;&atilde;o de pessoa jur&iacute;dica de direito privado para presta&ccedil;&atilde;o de servi&ccedil;o especializado de manuten&ccedil;&atilde;o corretiva e preventiva de computadores e perif&eacute;ricos, al&eacute;m dos servi&ccedil;os de monitoramento de som e &aacute;udio nas sess&otilde;es ordin&aacute;rias e solenes na C&acirc;mara Municipal de Sum&eacute; - PB.</p> <p style="text-align: justify;"><strong>DECIS&Atilde;O:</strong>&nbsp;O Agente de Contrata&ccedil;&otilde;es, no uso de suas atribui&ccedil;&otilde;es legais, decide pelo <strong>CONHECIMENTO </strong>do recurso, ante a constata&ccedil;&atilde;o de sua tempestividade , e, no m&eacute;rito, pelo <strong>N&Atilde;O PROVIMENTO</strong>, mantendo-se a decis&atilde;o de <strong>INABILITA&Ccedil;&Atilde;O </strong>da empresa recorrente por aus&ecirc;ncia total de documentos de habilita&ccedil;&atilde;o.</p> <p style="text-align: justify;"><strong>S&Iacute;NTESE DA FUNDAMENTA&Ccedil;&Atilde;O:</strong>&nbsp;Vazio Documental Absoluto: A recorrente falhou em instruir sua proposta com qualquer documento essencial exigido no Aviso de Contrata&ccedil;&atilde;o / Termo de Refer&ecirc;ncia.</p> <p style="text-align: justify;"><strong>Impossibilidade de Juntada Posterior:</strong> O instituto do saneamento de falhas e o princ&iacute;pio do formalismo moderado (Art. 12, III, da Lei n&ordm; 14.133/2021) n&atilde;o autorizam a inclus&atilde;o posterior de documentos que deveriam constar originariamente na proposta.</p> <p style="text-align: justify;"><strong>Veda&ccedil;&atilde;o &agrave; Dilig&ecirc;ncia:</strong> Conforme o Ac&oacute;rd&atilde;o 1.211/2021-TCU-Plen&aacute;rio, a realiza&ccedil;&atilde;o de dilig&ecirc;ncia para suprir a aus&ecirc;ncia de documento indispens&aacute;vel &eacute; vedada, pois fere o Princ&iacute;pio da Isonomia e da Sele&ccedil;&atilde;o da Proposta mais Vantajosa.</p> <p style="text-align: justify;"><strong>Inexist&ecirc;ncia de Lastro:</strong> Para que houvesse saneamento, seria necess&aacute;ria a exist&ecirc;ncia de documento pr&eacute;vio enviado que demonstrasse a situa&ccedil;&atilde;o f&aacute;tica &agrave; &eacute;poca da abertura, o que n&atilde;o ocorreu devido ao vazio documental absoluto.</p> <p style="text-align: justify;"><strong>Preserva&ccedil;&atilde;o da Isonomia:</strong> Aceitar a juntada de toda a documenta&ccedil;&atilde;o habilitat&oacute;ria ap&oacute;s a fase de julgamento privilegiaria a des&iacute;dia da recorrente em detrimento dos licitantes que cumpriram rigorosamente o edital.</p> <p style="text-align: justify;"><strong>Vincula&ccedil;&atilde;o ao Instrumento Convocat&oacute;rio: </strong>O descumprimento dos requisitos b&aacute;sicos do instrumento convocat&oacute;rio constitui v&iacute;cio material insan&aacute;vel, operando-se a preclus&atilde;o sobre o direito de inovar no conjunto documental nesta fase processual.</p> <p style="text-align: justify;"><strong>CONCLUS&Atilde;O:</strong>&nbsp;Diante da neglig&ecirc;ncia documental e do car&aacute;ter protelat&oacute;rio do recurso que n&atilde;o enfrenta o descumprimento material, ratifica-se a inabilita&ccedil;&atilde;o para assegurar a legalidade e a moralidade administrativa.</p> <p style="text-align: justify;">&nbsp;</p> <p style="text-align: right;">Sum&eacute; - PB, 12 de janeiro de 2026.</p> <p style="text-align: justify;">&nbsp;</p> <p style="text-align: center;"><strong>Vanklin Mikael Carneiro de Souza Silva</strong></p> <p style="text-align: center;"><em>Agente de Contrata&ccedil;&otilde;es</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20260112084709/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20260112084709/?link=CMSU</a> ', 'published', '2026-01-12 20:51:18', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO - DV00001/2026', '20260112084155', 'ESTADO DA PARAÍBA CÂMARA MUNICIPAL DE SUMÉ EXTRATO DE DECISÃO DE RECURSO ADMINISTRATIVO PROCESSO ADM. Nº: 251125DV00001 REFERÊNCIA: Processo Licitatório nº DV 00001/2026 RECORRENTE: Zaron Engenharia e Serviços LTDA OBJETO: Contratação de serviços técnicos especializados de consultoria e assessoria a', '<p style="text-align: center;"><strong>ESTADO DA PARA&Iacute;BA</strong></p> <p style="text-align: center;"><strong>C&Acirc;MARA MUNICIPAL DE SUM&Eacute;</strong></p> <p>&nbsp;</p> <p style="text-align: center;"><strong>EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO</strong></p> <p><br><strong>PROCESSO ADM. N&ordm;: </strong>251125DV00001</p> <p style="text-align: justify;"><strong>REFER&Ecirc;NCIA: </strong>Processo Licitat&oacute;rio n&ordm; DV 00001/2026</p> <p style="text-align: justify;"><strong>RECORRENTE:</strong> Zaron Engenharia e Servi&ccedil;os LTDA</p> <p style="text-align: justify;"><strong>OBJETO:</strong> Contrata&ccedil;&atilde;o de servi&ccedil;os t&eacute;cnicos especializados de consultoria e assessoria administrativa junto &agrave; C&acirc;mara Municipal de Sum&eacute; &ndash; PB.</p> <p style="text-align: justify;"><strong>DECIS&Atilde;O:</strong>&nbsp;O Agente de Contrata&ccedil;&otilde;es, no exerc&iacute;cio de suas atribui&ccedil;&otilde;es, decide pelo <strong>CONHECIMENTO </strong>e, no m&eacute;rito, pelo <strong>N&Atilde;O PROVIMENTO</strong> do recurso administrativo interposto pela empresa Zaron Engenharia e Servi&ccedil;os LTDA, mantendo integralmente a decis&atilde;o de sua <strong>INABILITA&Ccedil;&Atilde;O</strong>.</p> <p style="text-align: justify;"><strong>S&Iacute;NTESE DA FUNDAMENTA&Ccedil;&Atilde;O:</strong>&nbsp;Aus&ecirc;ncia de Capacidade Operacional: O descumprimento do item 5.1 do Termo de Refer&ecirc;ncia (TR), que exige a comprova&ccedil;&atilde;o de corpo t&eacute;cnico dispon&iacute;vel e experiente, configura omiss&atilde;o de requisito essencial para servi&ccedil;os de natureza intelectual.</p> <p style="text-align: justify;"><strong>Inexist&ecirc;ncia de Erro Formal:</strong> A n&atilde;o apresenta&ccedil;&atilde;o da rela&ccedil;&atilde;o da equipe t&eacute;cnica n&atilde;o constitui mero erro formal, mas sim uma omiss&atilde;o material substantiva que compromete a aferi&ccedil;&atilde;o da exequibilidade da proposta.</p> <p style="text-align: justify;"><strong>Veda&ccedil;&atilde;o &agrave; Juntada Posterior:</strong> A dilig&ecirc;ncia saneadora (Art. 64 da Lei n&ordm; 14.133/2021) destina-se a esclarecer informa&ccedil;&otilde;es preexistentes e n&atilde;o pode ser utilizada para a inclus&atilde;o de documentos obrigat&oacute;rios que deveriam compor originariamente a habilita&ccedil;&atilde;o.</p> <p style="text-align: justify;"><strong>Princ&iacute;pio da Isonomia:</strong> Outros 17 (dezessete) licitantes cumpriram integralmente as regras edital&iacute;cias; conceder prazo extraordin&aacute;rio para a recorrente configuraria vantagem indevida e viola&ccedil;&atilde;o &agrave; equidade do certame.</p> <p style="text-align: justify;"><strong>Inaplicabilidade de Jurisprud&ecirc;ncia Externa:</strong> O entendimento do Ac&oacute;rd&atilde;o 8436/2025 do TCE-CE &eacute; inaplic&aacute;vel ao caso, visto que n&atilde;o houve sequer a indica&ccedil;&atilde;o dos profissionais no ato da habilita&ccedil;&atilde;o, inexistindo dado preexistente a ser ratificado.</p> <p style="text-align: justify;"><strong>CONCLUS&Atilde;O:&nbsp;</strong>Fica mantida a inabilita&ccedil;&atilde;o por descumprimento dos crit&eacute;rios de aceitabilidade do Termo de Refer&ecirc;ncia, devendo o certame prosseguir com os demais atos pertinentes.</p> <p style="text-align: right;"><br>&nbsp;Sum&eacute; - PB, 12 de janeiro de 2026.</p> <p style="text-align: justify;"><br>&nbsp;</p> <p style="text-align: center;"><strong>VANKLIN MIKAEL CARNEIRO DE SOUZA SILVA </strong></p> <p style="text-align: center;"><em>Agente de Contrata&ccedil;&otilde;es</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20260112084155/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20260112084155/?link=CMSU</a> ', 'published', '2026-01-12 20:44:51', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO - DV00001/2026', '20260112083309', 'ESTADO DA PARAÍBA CÂMARA MUNICIPAL DE SUMÉ EXTRATO DE DECISÃO DE RECURSO ADMINISTRATIVO PROCESSO ADM. Nº: 251125DV00001 MODALIDADE: Dispensa de Licitação Nº 00001/2026 RECORRENTE: Malheiros Inteligência Pública LTDA OBJETO: Contratação de consultoria para área de licitações DECISÃO: A autoridade sup', '<p style="text-align: center;"><strong>ESTADO DA PARA&Iacute;BA</strong></p> <p style="text-align: center;"><strong>C&Acirc;MARA MUNICIPAL DE SUM&Eacute;</strong></p> <p>&nbsp;</p> <p style="text-align: center;"><strong>EXTRATO DE DECIS&Atilde;O DE RECURSO ADMINISTRATIVO</strong></p> <p><br><strong>PROCESSO ADM. N&ordm;: </strong>251125DV00001</p> <p><strong>MODALIDADE: </strong>Dispensa de Licita&ccedil;&atilde;o N&ordm; 00001/2026</p> <p><strong>RECORRENTE: </strong>Malheiros Intelig&ecirc;ncia P&uacute;blica LTDA</p> <p><strong>OBJETO: </strong>Contrata&ccedil;&atilde;o de consultoria para &aacute;rea de licita&ccedil;&otilde;es</p> <p>&nbsp;</p> <p style="text-align: justify;"><strong>DECIS&Atilde;O:</strong>&nbsp;A autoridade superior, no uso de suas atribui&ccedil;&otilde;es legais, ap&oacute;s an&aacute;lise t&eacute;cnica e jur&iacute;dica, decide pelo CONHECIMENTO e, no m&eacute;rito, pelo N&Atilde;O PROVIMENTO do recurso interposto pela empresa [Nome da Empresa], mantendo a decis&atilde;o de sua INABILITA&Ccedil;&Atilde;O.</p> <p style="text-align: justify;"><strong>S&Iacute;NTESE DA FUNDAMENTA&Ccedil;&Atilde;O:</strong>&nbsp;Diferencia&ccedil;&atilde;o Documental: A Certid&atilde;o Negativa de D&eacute;bitos (CND) e a Prova de Inscri&ccedil;&atilde;o Municipal (Alvar&aacute; ou Certid&atilde;o de Inscri&ccedil;&atilde;o) possuem naturezas jur&iacute;dicas e finalidades distintas. A exist&ecirc;ncia do n&uacute;mero de inscri&ccedil;&atilde;o em documento fiscal n&atilde;o supre a aus&ecirc;ncia do documento comprobat&oacute;rio de registro exigido no Aviso de Contrata&ccedil;&atilde;o.</p> <p style="text-align: justify;"><br><strong>Julgamento Objetivo e Isonomia:</strong> Admitir documento diverso feriria o Princ&iacute;pio do Julgamento Objetivo e a Isonomia entre os licitantes que cumpriram integralmente as exig&ecirc;ncias edital&iacute;cias.<br>Inaplicabilidade de Dilig&ecirc;ncia: A dilig&ecirc;ncia saneadora n&atilde;o se presta para a inclus&atilde;o posterior de documento que deveria constar originariamente na proposta, conforme veda a Lei n&ordm; 14.133/2021 e jurisprud&ecirc;ncia do TCU. A des&iacute;dia do licitante n&atilde;o pode ser suprida por dilig&ecirc;ncia, sob pena de privilegiar o interesse privado em detrimento do interesse p&uacute;blico e da transpar&ecirc;ncia.</p> <p style="text-align: justify;"><br><strong>Preclus&atilde;o: </strong>O licitante anuiu com as regras do certame ao n&atilde;o impugnar o edital tempestivamente, operando-se a preclus&atilde;o do direito de questionar a exig&ecirc;ncia documental ap&oacute;s a fase de julgamento.</p> <p style="text-align: justify;"><br><strong>CONCLUS&Atilde;O:</strong>&nbsp;Fica mantida a inabilita&ccedil;&atilde;o da recorrente e a continuidade do certame com a empresa subsequente, por estar em conformidade com o instrumento convocat&oacute;rio.</p> <p style="text-align: right;">Sum&eacute; - PB, 12 de Janeiro de 2026.</p> <p style="text-align: center;">&nbsp;<br><strong>Vanklin Mikael Carneiro de Souza Silva</strong></p> <p style="text-align: center;"><em>Agente de Contrata&ccedil;&otilde;es</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20260112083309/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20260112083309/?link=CMSU</a> ', 'published', '2026-01-12 20:39:02', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
