INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('EXTRATO DE CONTRATO - DV00022/2025', '20251219100934', 'ESTADO DA PARAÍBA CÂMARA MUNICIPAL DE SUMÉ EXTRATO DE CONTRATOS ESPÉCIE: Contratos nº 00030/2025 e 00031/2025, decorrente da Dispensa de Licitação nº 00022/2025. CONTRATANTE: Câmara Municipal de Sumé, inscrita no CNPJ sob o nº 05.563.774/0001-20. CONTRATADAS: Andrade Produções e Eventos LTDA, inscri', '<p style="text-align: center;" data-path-to-node="4"><strong data-path-to-node="4" data-index-in-node="0">ESTADO DA PARA&Iacute;BA</strong>&nbsp;</p> <p style="text-align: center;" data-path-to-node="4"><strong data-path-to-node="4" data-index-in-node="18">C&Acirc;MARA MUNICIPAL DE SUM&Eacute;</strong></p> <p style="text-align: center;" data-path-to-node="4"><strong data-path-to-node="4" data-index-in-node="18">EXTRATO DE CONTRATOS</strong></p> <p style="text-align: center;" data-path-to-node="4">&nbsp;</p> <p style="text-align: justify;" data-path-to-node="5"><strong data-path-to-node="5" data-index-in-node="0">ESP&Eacute;CIE:</strong> Contratos n&ordm; 00030/2025 e 00031/2025, decorrente da Dispensa de Licita&ccedil;&atilde;o n&ordm; 00022/2025.</p> <p style="text-align: justify;" data-path-to-node="6"><strong data-path-to-node="6" data-index-in-node="0">CONTRATANTE:</strong> C&acirc;mara Municipal de Sum&eacute;, inscrita no CNPJ sob o n&ordm; 05.563.774/0001-20.</p> <p style="text-align: justify;" data-path-to-node="7"><strong data-path-to-node="7" data-index-in-node="0">CONTRATADAS:</strong> Andrade Produ&ccedil;&otilde;es e Eventos LTDA, inscrita no CNPJ sob o n&ordm; 55.900.475/0001-26.</p> <p style="text-align: justify;" data-path-to-node="8"><strong data-path-to-node="8" data-index-in-node="0">OBJETO:</strong> Contrata&ccedil;&atilde;o de pessoa jur&iacute;dica de direito privado para presta&ccedil;&atilde;o de servi&ccedil;os de loca&ccedil;&atilde;o, transporte, montagem e desmontagem, d