INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('AUTOGRTAFO N&ordm; 792/2025 AO PROJETO DE LEI N&ordm; 939/2025', '20251121053515', 'Reconhece o caráter educacional, formativo e de relevante interesse social da prática do Muay Thai no município de Sumé – PB. A CÂMARA MUNICIPAL DE SUMÉ, ESTADO DA PARAÍBA, por seus representantes legais, aprova e o PREFEITO MUNICIPAL sanciona a seguinte Lei: Art. 1º Fica reconhecido, no âmbito do M', '<p style="text-align: right;"><em>Reconhece o car&aacute;ter educacional, formativo e de relevante interesse&nbsp;social da pr&aacute;tica do Muay Thai no munic&iacute;pio de Sum&eacute; &ndash; PB.</em></p> <p style="text-align: justify;">A C&Acirc;MARA MUNICIPAL DE SUM&Eacute;, ESTADO DA PARA&Iacute;BA, por seus&nbsp;representantes legais, aprova e o PREFEITO MUNICIPAL sanciona a seguinte Lei:</p> <p style="text-align: justify;"><br><strong>Art. 1&ordm;</strong> Fica reconhecido, no &acirc;mbito do Munic&iacute;pio de Sum&eacute; &ndash; PB, o car&aacute;ter&nbsp;educacional, formativo e de relevante interesse social da pr&aacute;tica do Muay Thai.</p> <p style="text-align: justify;"><br><strong>Art. 2&ordm;</strong> O Muay Thai, enquanto modalidade esportiva, cultural e social, contribui para:</p> <p style="text-align: justify;"><br><strong>I &ndash;</strong> o desenvolvimento f&iacute;sico, mental e emocional dos praticantes;<br><strong>II &ndash; </strong>a forma&ccedil;&atilde;o de valores como respeito, disciplina, resili&ecirc;ncia, cidadania e solidariedade;<br><strong>III &ndash; </strong>a inclus&atilde;o social de crian&ccedil;as, adolescentes, jovens, adultos e idosos;<br><strong>IV &ndash;</strong> a preven&ccedil;&atilde;o de situa&ccedil;&otilde;es de risco social, promovendo h&aacute;bitos de vida saud&aacute;veis e&nbsp;pr&aacute;ticas de conviv&ecirc;ncia pac&iacute;fica.</p> <p style="text-align: justify;"><br><strong>Art. 3&ordm;</strong> O Poder Executivo poder&aacute; firmar parcerias com associa&ccedil;&otilde;es, federa&ccedil;&otilde;es,&nbsp;academias, entidades esportivas e institui&ccedil;&otilde;es de ensino, visando promover projetos, eventos e&nbsp;pol&iacute;ticas p&uacute;blicas que fomentem a pr&aacute;tica do Muay Thai no munic&iacute;pio.</p> <p style="text-align: justify;"><br><strong>Art. 4&ordm;</strong> Esta Lei n&atilde;o implica obrigatoriedade de despesas, ficando sua execu&ccedil;&atilde;o&nbsp;condicionada &agrave; disponibilidade or&ccedil;ament&aacute;ria e &agrave; observ&acirc;ncia das normas da Lei de&nbsp;Responsabilidade Fiscal.</p> <p style="text-align: justify;"><br><strong>Art. 5&ordm;</strong> Esta Lei entra em vigor ap&oacute;s decorridos 30 (trinta) dias de sua publica&ccedil;&atilde;o.</p> <p style="text-align: right;"><br>Sum&eacute;, 18 de novembro de 2025.</p> <p style="text-align: right;">&nbsp;</p> <p style="text-align: center;"><strong>Jeffeson Figueiredo Menezes</strong><br><em>Presidente da C&acirc;mara</em></p> <p style="text-align: center;"><strong>Juan Victor Gomes de S&aacute; Pires Pereira</strong></p> <p style="text-align: center;"><em>1&ordm; Secret&aacute;rio</em></p> <p style="text-align: center;"><strong>Bruno Setefanio de Sousa Duarte</strong></p> <p style="text-align: center;"><em>2&ordm; Secret&aacute;rio</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20251121053515/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20251121053515/?link=CMSU</a> ', 'published', '2025-11-21 17:37:13', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
