INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('AUTOGRAFO N&ordm; 793/2025 AO PROJETO DE LEI N&ordm; 941/2025', '20251121053233', '"Institui, no Município de Sumé, a Semana da Pátria." A CÂMARA MUNICIPAL DE SUMÉ, ESTADO DA PARAÍBA, por seus representantes legais, aprova e o PREFEITO MUNICIPAL sanciona a seguinte Lei: Art. 1º Fica instituída, no Município de Sumé, a Semana da Pátria, a ser comemorada anualmente, de 1º a 7 de set', '<p style="text-align: right;"><em>"Institui, no Munic&iacute;pio de Sum&eacute;, a Semana da P&aacute;tria."</em></p> <p style="text-align: justify;">A C&Acirc;MARA MUNICIPAL DE SUM&Eacute;, ESTADO DA PARA&Iacute;BA, por seus&nbsp;representantes legais, aprova e o PREFEITO MUNICIPAL sanciona a seguinte Lei:</p> <p style="text-align: justify;"><strong>Art. 1&ordm;</strong> Fica institu&iacute;da, no Munic&iacute;pio de Sum&eacute;, a Semana da P&aacute;tria, a ser comemorada&nbsp;anualmente, de 1&ordm; a 7 de setembro, com atividades educativas, culturais e c&iacute;vicas alusivas &agrave;&nbsp;Independ&ecirc;ncia do Brasil.</p> <p style="text-align: justify;"><br><strong>Art. 2&ordm; </strong>A Semana da P&aacute;tria tem como objetivos:</p> <p style="text-align: justify;"><br><strong>I &ndash; </strong>estimular o civismo e o esp&iacute;rito de cidadania entre crian&ccedil;as, adolescentes e jovens;<br><strong>II &ndash;</strong> valorizar os s&iacute;mbolos nacionais e a hist&oacute;ria da Independ&ecirc;ncia do Brasil;<br><strong>III &ndash;</strong> promover a integra&ccedil;&atilde;o entre escolas, comunidade e Poder P&uacute;blico em torno de atividades&nbsp;c&iacute;vicas;<br><strong>IV &ndash;</strong> incentivar a participa&ccedil;&atilde;o volunt&aacute;ria das escolas p&uacute;blicas e privadas, associa&ccedil;&otilde;es,&nbsp;institui&ccedil;&otilde;es e comunidade em geral nas comemora&ccedil;&otilde;es do 7 de setembro.</p> <p style="text-align: justify;"><br><strong>Art. 3&ordm;</strong> O Poder Executivo poder&aacute;, no &acirc;mbito de sua compet&ecirc;ncia:</p> <p style="text-align: justify;"><br><strong>I &ndash; </strong>organizar desfiles c&iacute;vicos, apresenta&ccedil;&otilde;es culturais, atividades esportivas e educativas;<br><strong>II &ndash; </strong>convidar escolas p&uacute;blicas e privadas, entidades sociais e culturais para participar da&nbsp;programa&ccedil;&atilde;o;<br><strong>III &ndash; </strong>firmar parcerias com institui&ccedil;&otilde;es de ensino e organiza&ccedil;&otilde;es da sociedade civil para a&nbsp;realiza&ccedil;&atilde;o das atividades.</p> <p style="text-align: justify;"><strong>Art. 4&ordm; </strong>As despesas decorrentes da execu&ccedil;&atilde;o desta Lei correr&atilde;o por conta das dota&ccedil;&otilde;es&nbsp;or&ccedil;ament&aacute;rias pr&oacute;prias, suplementadas se necess&aacute;rio, ficando sua execu&ccedil;&atilde;o condicionada &agrave;&nbsp;disponibilidade or&ccedil;ament&aacute;ria e &agrave; observ&acirc;ncia da Lei de Responsabilidade Fiscal.</p> <p style="text-align: justify;"><br><strong>Art. 5&ordm; </strong>Esta Lei entra em vigor 30 (trinta) dias ap&oacute;s sua publica&ccedil;&atilde;o.</p> <p style="text-align: right;"><br>Sum&eacute;, 18 de novembro de 2025.</p> <p style="text-align: right;">&nbsp;</p> <p style="text-align: center;"><strong>Jeffeson Figueiredo Menezes</strong><br><em>Presidente da C&acirc;mara</em></p> <p style="text-align: center;"><strong>Juan Victor Gomes de S&aacute; Pires Pereira</strong></p> <p style="text-align: center;"><em>1&ordm; Secret&aacute;rio</em></p> <p style="text-align: center;"><strong>Bruno Setefanio de Sousa Duarte</strong></p> <p style="text-align: center;"><em>2&ordm; Secret&aacute;rio</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20251121053233/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20251121053233/?link=CMSU</a> ', 'published', '2025-11-21 17:35:03', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
