INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('REQUERIMENTO N&ordm; 32/2025', '20251121052505', 'EMENTA: Voto de Pesar. Prezado Senhor, Requeiro, nos termos regimentais e após apreciação pelo Plenário, que seja concedido Voto de Pesar à família da Senhora Rosália Ricardo Gonçalves, em razão de seu falecimento. A perda de um ente querido é sempre um momento de profunda dor. Que seus familiares e', '<p style="text-align: justify;"><strong>EMENTA: </strong>Voto de Pesar.</p> <p style="text-align: justify;">Prezado Senhor,</p> <p style="text-align: justify;">Requeiro, nos termos regimentais e ap&oacute;s aprecia&ccedil;&atilde;o pelo Plen&aacute;rio, que seja concedido&nbsp;Voto de Pesar &agrave; fam&iacute;lia da Senhora Ros&aacute;lia Ricardo Gon&ccedil;alves, em raz&atilde;o de seu falecimento. A&nbsp;perda de um ente querido &eacute; sempre um momento de profunda dor. Que seus familiares encontrem&nbsp;conforto nas boas lembran&ccedil;as e na solidariedade daqueles que os cercam. Que a mem&oacute;ria e o&nbsp;legado da Senhora Ros&aacute;lia permane&ccedil;am vivos entre todos que tiveram o privil&eacute;gio de conhec&ecirc;-la&nbsp;e am&aacute;-la.</p> <p style="text-align: justify;"><br>Expresso, com respeito e sinceridade, os meus mais profundos sentimentos pela&nbsp;irrepar&aacute;vel perda.</p> <p style="text-align: right;">Sum&eacute; em, 18 de novembro de 2025.</p> <p style="text-align: center;"><strong>Jeffeson Figueiredo Menezes</strong><br><em>Vereador</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20251121052505/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20251121052505/?link=CMSU</a> ', 'published', '2025-11-21 17:26:36', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
