INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('INDICA&Ccedil;&Atilde;O N&ordm; 13/2025', '20251121045909', 'INDICAÇÃO Nº 13/2025 Senhor Presidente, Nos termos regimentais, e após a devida apreciação plenária, indico ao Excelentíssimo Senhor Prefeito Municipal de Sumé – PB, que encaminhe a esta Casa Legislativa o Projeto de Lei que trata da autorização para variação dos valores do IPTU conforme a infraestr', '<p style="text-align: center;"><strong>INDICA&Ccedil;&Atilde;O N&ordm; 13/2025</strong></p> <p style="text-align: center;">&nbsp;</p> <p>Senhor Presidente,</p> <p style="text-align: justify;"><br>Nos termos regimentais, e ap&oacute;s a devida aprecia&ccedil;&atilde;o plen&aacute;ria, indico ao Excelent&iacute;ssimo&nbsp;Senhor Prefeito Municipal de Sum&eacute; &ndash; PB, que encaminhe a esta Casa Legislativa o Projeto de&nbsp;Lei que trata da autoriza&ccedil;&atilde;o para varia&ccedil;&atilde;o dos valores do IPTU conforme a infraestrutura das&nbsp;vias p&uacute;blicas municipais, conforme minuta anexa.</p> <p style="text-align: center;"><strong>JUSTIFICATIVA:</strong></p> <p style="text-align: justify;">A presente Indica&ccedil;&atilde;o tem por objetivo estimular a justi&ccedil;a fiscal e a equidade&nbsp;tribut&aacute;ria no &acirc;mbito do Munic&iacute;pio de Sum&eacute;, propondo que a cobran&ccedil;a do IPTU leve em&nbsp;considera&ccedil;&atilde;o as condi&ccedil;&otilde;es reais das vias p&uacute;blicas onde se encontram os im&oacute;veis tributados.<br>&Eacute; de conhecimento p&uacute;blico que diversos contribuintes residem em ruas n&atilde;o&nbsp;pavimentadas, sem ilumina&ccedil;&atilde;o adequada ou com defici&ecirc;ncia em servi&ccedil;os b&aacute;sicos, enquanto&nbsp;outros moradores, em regi&otilde;es plenamente urbanizadas, recebem a mesma cobran&ccedil;a tribut&aacute;ria.</p> <p style="text-align: justify;"><br>Essa situa&ccedil;&atilde;o gera desequil&iacute;brio e sensa&ccedil;&atilde;o de injusti&ccedil;a entre os cidad&atilde;os.Com a medida proposta, pretende-se autorizar o Executivo Municipal a instituir&nbsp;descontos ou varia&ccedil;&otilde;es na base de c&aacute;lculo do IPTU para im&oacute;veis localizados em logradouros&nbsp;com infraestrutura urbana incompleta, observando sempre os limites da Lei de&nbsp;Responsabilidade Fiscal (LC n&ordm; 101/2000) e as diretrizes or&ccedil;ament&aacute;rias municipais.</p> <p style="text-align: justify;"><br>Ressalta-se que diversos munic&iacute;pios brasileiros, como Campina Grande&nbsp;(PB), Cariacica (ES) e Goi&acirc;nia (GO), j&aacute; adotaram iniciativas semelhantes, reconhecendo que&nbsp;a cobran&ccedil;a de tributos deve refletir a realidade de cada contribuinte e da localidade em que&nbsp;reside.</p> <p style="text-align: justify;"><br>Por se tratar de mat&eacute;ria de iniciativa do Poder Executivo, apresenta-se anexa &agrave;&nbsp;presente Indica&ccedil;&atilde;o a minuta de Projeto de Lei, como sugest&atilde;o t&eacute;cnica a ser encaminhada por&nbsp;esta Prefeitura Municipal.</p> <p style="text-align: justify;"><br>Diante do exposto, solicito o apoio dos nobres vereadores e o acolhimento da presente&nbsp;Indica&ccedil;&atilde;o pelo Excelent&iacute;ssimo Senhor Prefeito Municipal, para que o Munic&iacute;pio avance&nbsp;em justi&ccedil;a social e equil&iacute;brio tribut&aacute;rio.</p> <p style="text-align: right;">&nbsp;</p> <p style="text-align: right;">Sum&eacute;, 13 de novembro de 2025.</p> <p style="text-align: right;">&nbsp;</p> <p style="text-align: center;"><strong>Juan Victor Gomes de S&aacute; Pires Pereira</strong></p> <p style="text-align: center;"><em>Vereador</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20251121045909/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20251121045909/?link=CMSU</a> ', 'published', '2025-11-21 17:01:47', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('INDICA&Ccedil;&Atilde;O N&ordm; 13/2025', '20251121045909', 'INDICAÇÃO Nº 13/2025 Senhor Presidente, Nos termos regimentais, e após a devida apreciação plenária, indico ao Excelentíssimo Senhor Prefeito Municipal de Sumé – PB, que encaminhe a esta Casa Legislativa o Projeto de Lei que trata da autorização para variação dos valores do IPTU conforme a infraestr', '<p style="text-align: center;"><strong>INDICA&Ccedil;&Atilde;O N&ordm; 13/2025</strong></p> <p style="text-align: center;">&nbsp;</p> <p>Senhor Presidente,</p> <p style="text-align: justify;"><br>Nos termos regimentais, e ap&oacute;s a devida aprecia&ccedil;&atilde;o plen&aacute;ria, indico ao Excelent&iacute;ssimo&nbsp;Senhor Prefeito Municipal de Sum&eacute; &ndash; PB, que encaminhe a esta Casa Legislativa o Projeto de&nbsp;Lei que trata da autoriza&ccedil;&atilde;o para varia&ccedil;&atilde;o dos valores do IPTU conforme a infraestrutura das&nbsp;vias p&uacute;blicas municipais, conforme minuta anexa.</p> <p style="text-align: center;"><strong>JUSTIFICATIVA:</strong></p> <p style="text-align: justify;">A presente Indica&ccedil;&atilde;o tem por objetivo estimular a justi&ccedil;a fiscal e a equidade&nbsp;tribut&aacute;ria no &acirc;mbito do Munic&iacute;pio de Sum&eacute;, propondo que a cobran&ccedil;a do IPTU leve em&nbsp;considera&ccedil;&atilde;o as condi&ccedil;&otilde;es reais das vias p&uacute;blicas onde se encontram os im&oacute;veis tributados.<br>&Eacute; de conhecimento p&uacute;blico que diversos contribuintes residem em ruas n&atilde;o&nbsp;pavimentadas, sem ilumina&ccedil;&atilde;o adequada ou com defici&ecirc;ncia em servi&ccedil;os b&aacute;sicos, enquanto&nbsp;outros moradores, em regi&otilde;es plenamente urbanizadas, recebem a mesma cobran&ccedil;a tribut&aacute;ria.</p> <p style="text-align: justify;"><br>Essa situa&ccedil;&atilde;o gera desequil&iacute;brio e sensa&ccedil;&atilde;o de injusti&ccedil;a entre os cidad&atilde;os.Com a medida proposta, pretende-se autorizar o Executivo Municipal a instituir&nbsp;descontos ou varia&ccedil;&otilde;es na base de c&aacute;lculo do IPTU para im&oacute;veis localizados em logradouros&nbsp;com infraestrutura urbana incompleta, observando sempre os limites da Lei de&nbsp;Responsabilidade Fiscal (LC n&ordm; 101/2000) e as diretrizes or&ccedil;ament&aacute;rias municipais.</p> <p style="text-align: justify;"><br>Ressalta-se que diversos munic&iacute;pios brasileiros, como Campina Grande&nbsp;(PB), Cariacica (ES) e Goi&acirc;nia (GO), j&aacute; adotaram iniciativas semelhantes, reconhecendo que&nbsp;a cobran&ccedil;a de tributos deve refletir a realidade de cada contribuinte e da localidade em que&nbsp;reside.</p> <p style="text-align: justify;"><br>Por se tratar de mat&eacute;ria de iniciativa do Poder Executivo, apresenta-se anexa &agrave;&nbsp;presente Indica&ccedil;&atilde;o a minuta de Projeto de Lei, como sugest&atilde;o t&eacute;cnica a ser encaminhada por&nbsp;esta Prefeitura Municipal.</p> <p style="text-align: justify;"><br>Diante do exposto, solicito o apoio dos nobres vereadores e o acolhimento da presente&nbsp;Indica&ccedil;&atilde;o pelo Excelent&iacute;ssimo Senhor Prefeito Municipal, para que o Munic&iacute;pio avance&nbsp;em justi&ccedil;a social e equil&iacute;brio tribut&aacute;rio.</p> <p style="text-align: right;">&nbsp;</p> <p style="text-align: right;">Sum&eacute;, 13 de novembro de 2025.</p> <p style="text-align: right;">&nbsp;</p> <p style="text-align: center;"><strong>Juan Victor Gomes de S&aacute; Pires Pereira</strong></p> <p style="text-align: center;"><em>Vereador</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20251121045909/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20251121045909/?link=CMSU</a> ', 'published', '2025-11-21 17:01:47', 0, '2026-02-13 10:53:48', '2026-02-13 10:53:48');
INSERT INTO news (title, slug, excerpt, content, status, published_at, views_count, created_at, updated_at) VALUES ('DECRETO LEGISLATIVO N&ordm; 07/2025', '20251121045726', 'DECRETO LEGISLATIVO Nº 07/2025. "DISPÕE SOBRE PONTO FACULTATIVO NAS DEPENDÊNCIAS DA CÂMARA MUNICIPAL DE SUMÉ NO DIA 21 DE NOVEMBRO DE 2025." O Excelentíssimo Senhor Presidente da Câmara Municipal de Sumé, no uso de suas atribuições que são lhe conferidas por lei. DECRETA : Art. 1º - Fica estabelecid', '<p style="text-align: center;"><strong>DECRETO LEGISLATIVO N&ordm; 07/2025.</strong></p> <p style="text-align: right;"><em>"DISP&Otilde;E SOBRE PONTO FACULTATIVO NAS DEPEND&Ecirc;NCIAS DA C&Acirc;MARA&nbsp;MUNICIPAL DE SUM&Eacute; NO DIA 21 DE&nbsp;NOVEMBRO DE 2025."</em></p> <p style="text-align: justify;">O Excelent&iacute;ssimo Senhor Presidente da C&acirc;mara Municipal de Sum&eacute;, no uso de suas&nbsp;atribui&ccedil;&otilde;es que s&atilde;o lhe conferidas por lei.<br><strong>DECRETA</strong>:</p> <p style="text-align: justify;"><strong>Art. 1&ordm; -</strong> Fica estabelecido o ponto facultativo nas depend&ecirc;ncias da C&acirc;mara Municipal&nbsp;de Sum&eacute; no dia 21 de novembro de 2025, em decorr&ecirc;ncia do feriado nacional celebrado em&nbsp;20 de novembro, alusivo ao Dia da Consci&ecirc;ncia Negra. As atividades administrativas e&nbsp;legislativas ser&atilde;o retomadas normalmente no dia 24 de novembro de 2025.</p> <p style="text-align: justify;"><strong>Art. 2&ordm; -</strong> Este decreto entra em vigor na data de sua publica&ccedil;&atilde;o.</p> <p style="text-align: right;">Sum&eacute;-PB, 14 de novembro de 2025.</p> <p style="text-align: center;"><strong>Jeffeson Figueiredo Menezes</strong><br><em>Presidente da C&acirc;mara</em></p><br><br>LINK DA MATÉRIA <a href="https://getpublic.inf.br/system/publicacao/materia/20251121045726/?link=CMSU" target="_blank" rel="noopener">https://getpublic.inf.br/system/publicacao/materia/20251121045726/?link=CMSU</a> ', 'published', '20